extension GqlFeeder {
  
  public func search(searchItem: SearchItem,
                     closure: @escaping(Result<SearchItem,Error>)->()) {
    if searchItem.searching {
      closure(.failure(self.error("Still searching in Progress")))
      return
    }
    searchItem.searching = true
    guard let gqlSession = self.gqlSession else {
      closure(.failure(fatal("Not connected"))); return
    }
    let wasAuthenticated: Bool = authToken != nil
    let request = searchItem.request
    let started = Date()
    gqlSession.query(graphql: request,
                     type: GqlSearchResponseWrapper.self) {   [weak self] res in
      guard let self = self else { return }
      print("Request Duration: \(Date().timeIntervalSince(started))s for: \(request)")
      searchItem.searching = false
      switch res {
        case .success(let searchResponseWrapper):
          if let authError
              = self.handleResponseAuthInfo(wasAuthenticated,
                                            searchResponseWrapper.search.authInfo){
            closure(.failure(authError))
            return
          }
          searchItem.lastResponse = searchResponseWrapper
          closure(.success(searchItem))
        case .failure(let err):
          closure(.failure(err))
      }
    }
  }
  
  
  /// Checks Auth Status
  /// - Parameters:
  ///   - wasAuthenticated: Request auth
  ///   - authStatus: Response AuthStatus
  /// - Returns: Error if auth expired or changed
  func handleResponseAuthInfo(_ wasAuthenticated:Bool, _ authInfo: GqlAuthInfo) -> Error? {
    if wasAuthenticated {
      if authInfo.status == .expired {
        return FeederError.expiredAccount(authInfo.message)
      }
      else if authInfo.status != .valid {
        return FeederError.changedAccount(authInfo.message)
      }
    }
    return nil
  }
  
  
  static func test() -> GqlSearchResponse?{
    let result =  "{\"data\":{\"search\":{\"authInfo\":{\"status\":\"valid\",\"message\":null},\"total\":1000,\"text\":\"Hochwasser\",\"searchHitList\":[{\"sDate\":\"2021-07-31\",\"snippet\":\"... verkleistert \\u2013 wir nennen das Kolmation. <span class=\\\"snippet\\\">Hochwasser<\\/span> k\\u00f6nnen diese Situation verst\\u00e4rken, oft... Fluss aus. \\u00dcberleben sie extreme <span class=\\\"snippet\\\">Hochwasser<\\/span> wie das im Juli? Hans... verschlammen\\u201c Wie schlimm ist extremes <span class=\\\"snippet\\\">Hochwasser<\\/span> eigentlich f\\u00fcr Bachflohkrebse und andere...\",\"teaser\":\"Wie schlimm ist extremes <span class=\\\"snippet\\\">Hochwasser<\\/span> eigentlich f\\u00fcr Bachflohkrebse und andere Kleinstlebewesen? Ein Gespr\\u00e4ch mit dem Gew\\u00e4sser\\u00f6kologen Hans J\\u00fcrgen Hahn \\u00fcber verstopfte Bachsedimente, zu niedrige Grundwasserspiegel und eingebaute Fl\\u00fcsse\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>\\u201eDie L\\u00fccken verschlammen\\u201c<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AL0_2017 eptNorm\\\"><h2 class=\\\"Titel\\\">\\u201eDie L\\u00fccken verschlammen\\u201c<\\/h2><h4 class=\\\"Unterzeile\\\">Wie schlimm ist extremes <span class=\\\"snippet\\\">Hochwasser<\\/span> eigentlich f\\u00fcr Bachflohkrebse und andere Kleinstlebewesen? Ein Gespr\\u00e4ch mit dem Gew\\u00e4sser\\u00f6kologen Hans J\\u00fcrgen Hahn \\u00fcber verstopfte Bachsedimente,  \\nzu niedrige Grundwasserspiegel und  \\neingebaute Fl\\u00fcsse<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptNorm\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=484.309 BH=383.25&#10;IB=1816 IH=1437&#10;S=3.6 b=660 h= C=Main&#10;id=1097290 typ=JPEG&#10;\\\" src=\\\"Media.1097290.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097290.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Von der Flut  \\nauf die Stra\\u00dfe gesp\\u00fclt:  \\nein Krebs  \\nim Ahrtal-Ort Schuld nach dem <span class=\\\"snippet\\\">Hochwasser<\\/span> \\n<span class=\\\"zAu\\\">Foto: Michael Probst\\/ap<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AL0_2017 eptNorm\\\"><p class=\\\"Korrespondent\\\">Interview <span class=\\\"zAu\\\">Ulrike Fokken<\\/span><\\/p><p class=\\\"oeinz\\\"><strong>taz am wochenende: Herr Hahn, kleinste Lebewesen wie Bachflohkrebse und K\\u00f6cherfliegen machen den Gro\\u00dfteil der mit blo\\u00dfem Auge sichtbaren Tiere in einem Fluss aus. \\u00dcberleben sie extreme <span class=\\\"snippet\\\">Hochwasser<\\/span> wie das im Juli?<\\/strong><\\/p><p class=\\\"meinz\\\"><strong>Hans J\\u00fcrgen Hahn:<\\/strong> Wenn sie nicht verschwemmt oder von Sand und Steinen zerrieben werden. Zum Gl\\u00fcck sind Flie\\u00dfgew\\u00e4sser-\\u00d6kosysteme dynamisch: Tiere erobern sich einen Lebensraum relativ schnell wieder, wenn es nicht dauerhaft St\\u00f6rungen gibt.<\\/p><p class=\\\"meinz\\\"><strong>Was ist an einem <span class=\\\"snippet\\\">Hochwasser<\\/span> am schlimmsten f\\u00fcr die kleinen Lebewesen?<\\/strong><\\/p><p class=\\\"meinz\\\">Das Aufwirbeln und Verlagern der Sedimente. Au\\u00dferdem l\\u00e4uft zu viel Mist ins Wasser. Je gr\\u00f6\\u00dfer das Einzugsgebiet eines Flusses, desto mehr Dreck von Industrie, \\u00c4ckern, Siedlungen und Stra\\u00dfen wird hineingesp\\u00fclt. Kl\\u00e4ranlagen werden bei Starkregen auf Durchfluss gestellt. Oft ziehen Gr\\u00e4ben, Kanalisation und Stra\\u00dfen das Wasser aus der Landschaft heraus, hin zum n\\u00e4chsten Gew\\u00e4sser.<\\/p><p class=\\\"meinz\\\"><strong>All dieses Wasser saust in den Fluss.<\\/strong><\\/p><p class=\\\"meinz\\\">Unter bestimmten Umst\\u00e4nden kommt nach starken Regenf\\u00e4llen das Wasser rasend schnell durch die Landschaft. Die Tiere im Fluss haben keine Chance, dem auszuweichen. Die tauchen dann ab in den Untergrund. Wenn das Bachsediment verstopft ist, werden sie abgetrieben.<\\/p><p class=\\\"meinz\\\"><strong>Wieso verstopft?<\\/strong><\\/p><p class=\\\"meinz\\\">Bei vielen Flie\\u00dfgew\\u00e4ssern sind die L\\u00fcckenr\\u00e4ume zwischen Steinen und Kies durch Schlamm und Schluff vor allem von den Ackerfl\\u00e4chen verkleistert \\u2013 wir nennen das Kolmation. <span class=\\\"snippet\\\">Hochwasser<\\/span> k\\u00f6nnen diese Situation verst\\u00e4rken, oft sp\\u00fclen sie die L\\u00fccken im Sediment aber auch wieder frei. Die Zwischenr\\u00e4ume sind mal gr\\u00f6\\u00dfer, mal kleiner, je nach dem Gestein im Einzugsgebiet der Fl\\u00fcsse. Sie werden vom Oberfl\\u00e4chenwasser und vom Grundwasser durchstr\\u00f6mt, sodass Sauerstoff einflie\\u00dft. Daher k\\u00f6nnen sich Tiere und Mikroorganismen wie Bakterien erhalten. Da unten sind 95 Prozent oder mehr der gesamten Biomasse eines Flusses zu finden. Nur durch die Kleinstlebewesen und intakte Sedimente entsteht ein funktionierendes Gew\\u00e4sser.<\\/p><p class=\\\"meinz\\\"><strong>Ringelw\\u00fcrmer und Wasserwanzen schaffen saubere Fl\\u00fcsse?<\\/strong><\\/p><p class=\\\"meinz\\\">Alle zusammen. Die Sedimente mit ihren Bakterienfilmen bilden einen Biofilter, der das Flusswasser reinigt. Sie und andere Mikroorganismen werden von Insektenlarven und kleinen Tieren gefressen. Die zerkleinern und zersetzen zudem das organische Material wie Bl\\u00e4tter, \\u00c4ste oder Aas, und durch ihre Grabt\\u00e4tigkeit halten sie die Nischen im Sediment ja auch offen. Das ganze f\\u00fchrt zur Selbstreinigung der Fl\\u00fcsse.<\\/p><p class=\\\"meinz\\\"><strong>Und was passiert, wenn die L\\u00fccken verschlammen?<\\/strong><\\/p><p class=\\\"meinz\\\">Wenn die Nischen verstopfen, haben die kleinen Tiere keinen Lebensraum. Damit entf\\u00e4llt die Funktion der Bachsedimente als reinigender Bioreaktor. Lachse, Forellen, Bachmuscheln haben keinen Brutraum mehr, die Lebensgemeinschaft im Fluss verarmt. Die Biodiversit\\u00e4t geht zur\\u00fcck und damit auch die \\u00d6kosystemdienstleistung.<\\/p><p class=\\\"meinz\\\"><strong>Was bedeutet das f\\u00fcr das Grundwasser?<\\/strong><\\/p><p class=\\\"meinz\\\">In kolmatierten Bereichen wird die Wechselwirkung zwischen Oberfl\\u00e4chenwasser und Grundwasser reduziert \\u2013 es str\\u00f6mt nicht so viel Wasser ins Grundwasser, aber es str\\u00f6mt auch weniger Grundwasser ins Oberfl\\u00e4chenwasser.<\\/p><p class=\\\"meinz\\\"><strong>Und was hei\\u00dft das?<\\/strong><\\/p><p class=\\\"meinz\\\">Bislang gibt es kaum Untersuchungen dazu, auf jeden Fall dreht sich ein nat\\u00fcrlicher Prozess um. Hier spielt der Klimawandel eine gro\\u00dfe Rolle. Zunehmend versiegen B\\u00e4che und Fl\\u00fcsse, der Landschaftswasserhaushalt kippt vielerorts gerade. Im Oberfl\\u00e4chengew\\u00e4sser kommt es zu einer Eindickung der Schadstoffe. Beim Versickern wird der Dreck dann zunehmend nach unten ins Grundwasser verklappt.<\\/p><p class=\\\"meinz\\\"><strong>Die Grundwasserst\\u00e4nde sinken seit Jahrzehnten und ziehen das Oberfl\\u00e4chenwasser mit in die Tiefe. Es gibt also nicht nur weniger Trinkwasser, es wird auch schmutziger?<\\/strong><\\/p><p class=\\\"meinz\\\">Nicht \\u00fcberall in Deutschland, aber in S\\u00fcdwestdeutschland ist die Grundwasserneubildung seit 2000 um 25 Prozent zur\\u00fcckgegangen. Die Konsequenzen sind politisch noch nicht \\u00fcberall angekommen. Den Leuten ist nicht klar, dass im Klimawandel die Mengenprobleme direkt zu Qualit\\u00e4tsproblemen f\\u00fchren.<\\/p><p class=\\\"meinz\\\"><strong>Werden wir in absehbarer Zeit zu wenig Trinkwasser haben?<\\/strong><\\/p><p class=\\\"meinz\\\">Auf jeden Fall in vielen Gegenden deutlich weniger als heute.<\\/p><p class=\\\"meinz\\\"><strong>Ein Prozent der Fl\\u00fcsse in Deutschland flie\\u00dft noch nat\\u00fcrlich. Welche Folgen hat das?<\\/strong><\\/p><p class=\\\"meinz\\\">Die Mehrzahl der Fl\\u00fcsse ist begradigt und eingefasst. Damit geht die Vielfalt der Strukturen verloren, und das spiegelt sich im Verlust der Artenvielfalt. Die anspruchsvollen, die seltenen Arten verschwinden. Und es fehlt die Durchg\\u00e4ngigkeit, die Fl\\u00fcsse sind zerhackt in Teilst\\u00fccke mit Wasserkraftanlagen und Wehren, die nat\\u00fcrliche Abflussdynamik fehlt. Kolmation und Mikroschadstoffe wie Arzneimittelr\\u00fcckst\\u00e4nde machen den Gew\\u00e4sserbewohnern schwer zu schaffen.<\\/p><p class=\\\"meinz\\\"><strong>Die Fl\\u00fcsse sind auch sauberer geworden. In meiner Kindheit durfte ich nicht mal mit den F\\u00fc\\u00dfen in die Elbe bei Hamburg gehen, heute kann man dort baden.<\\/strong><\\/p><p class=\\\"meinz\\\">Im Vergleich zu den 1970er Jahren sind die Gew\\u00e4sser um ein Vielfaches besser geworden. Die Kl\\u00e4ranlagen haben eine deutlich bessere Leistungsf\\u00e4higkeit und fast alle Haushalte und Betriebe sind daran angeschlossen. Die chemischen und biologischen Rahmenparameter haben sich auf jeden Fall verbessert.<\\/p><p class=\\\"meinz\\\"><strong>Eine gute Nachricht.<\\/strong><\\/p><p class=\\\"meinz\\\">Ja, das ist eine der wenigen gro\\u00dfen Erfolgsgeschichten im Umweltschutz. Dennoch: Nicht mal zehn Prozent der Fl\\u00fcsse haben den guten \\u00f6kologischen Zustand erreicht, den die Wasserrahmenrichtlinie vorschreibt.<\\/p><p class=\\\"meinz\\\"><strong>Laut der EU-Wasserrahmenrichtlinie h\\u00e4tte Deutschland bis 2021 daf\\u00fcr sorgen m\\u00fcssen, dass Fl\\u00fcsse und Seen in einem guten \\u00f6kologischen Zustand sind. Bislang schaffen das neun Prozent der Fl\\u00fcsse. Deutschland hat bis 2027 Zeit, die Richtlinie umzusetzen. Was muss passieren?<\\/strong><\\/p><p class=\\\"meinz\\\">Im Mittelpunkt muss ein funktionierender Landschaftswasserhaushalt stehen. Man muss ein klares Ordnungsrecht schaffen, das dem Landwirt auch mal vorschreibt, dass er in einem Flusseinzugsgebiet keinen Mais anbauen darf, damit bei Regen keine Schlammlawinen vom Acker in den Fluss sp\\u00fclen und die L\\u00fccken im Bachbett verstopfen. Es muss einen klar gew\\u00e4sserbezogenen Rechtsrahmen geben, aber je mehr wirtschaftliche Interessen sich im Einzugsgebiet eines Flusses \\u00fcberschneiden, desto schwieriger wird es, die Gesetze zum Grundwasserschutz oder zum Naturschutz in Fl\\u00fcssen umzusetzen. Das sehen Sie in Brandenburg beim Bau der Tesla-Fabrik.<\\/p><p class=\\\"meinz\\\"><strong>Vertreter von Wirtschaft und Politik wollen die Wasserrahmenrichtlinie novellieren.<\\/strong><\\/p><p class=\\\"meinz\\\">Das w\\u00e4re vermutlich eine Katastrophe. Bei einer Novellierung muss man eine massive Einflussnahme von Interessenverb\\u00e4nden wie der Wasserkraft bef\\u00fcrchten.<\\/p><p class=\\\"meinz\\\"><strong>Die \\u00dcberschwemmungen haben das Land geschockt. K\\u00f6nnte die Katastrophe dazu f\\u00fchren, dass jetzt mehr passiert?<\\/strong><\\/p><p class=\\\"meinz\\\">Der Unfall der Chemiefabrik Sandoz 1986 am Rhein hat zu einem massiven Umdenken gef\\u00fchrt. Man hat Einleitungen abgestellt und Kl\\u00e4ranlagen gebaut. Es zeigt, was wir alles tun k\\u00f6nnen, wenn wir es wollen.<\\/p><div class=\\\"Shorty BeitragsArt_taz_print_biokasten ArtikelTyp_Bio_2017 eptShorty\\\"><img alt=\\\"BB=56.693 BH=73.5&#10;IB=211 IH=298&#10;S=0.5 b= h=298 C=Bio&#10;id=1097289 typ=JPEG&#10;\\\" src=\\\"Media.1097289.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097289.norm.jpg\')\\\" class=\\\"MediaImg ImgBio ImgSize192\\\"\\/><p class=\\\"MediaButze BuBio ImgSize192\\\"><span class=\\\"zAu\\\">Foto: privat<\\/span><\\/p><div class=\\\"BioText\\\"><p class=\\\"ShortyTitel\\\">Hans J\\u00fcrgen Hahn,<\\/p><p class=\\\"oeinz\\\"> geboren 1963, ist Gew\\u00e4sser\\u00f6kologe. Er lehrt und forscht als Privatdozent an der Universit\\u00e4t Koblenz-Landau, zudem ist er Gesch\\u00e4ftsf\\u00fchrer des Instituts f\\u00fcr Grundwasser\\u00f6kologie.<\\/p><\\/div><div class=\\\"clear\\\"> <\\/div><\\/div><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a241\\/\\\"\\/><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a241\\/\\\"><h4 class=\\\"AutorName\\\">Ulrike Fokken<\\/h4><\\/a><\\/div><\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-31.017151ecab637f8bedc37bf9fa303c84563e86b7\",\"article\":{\"articleHtml\":{\"name\":\"art03605686.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627663395\",\"sha256\":\"8566dee7faba4405d3777b66186f76ff03ac52cd3cb59ca4cdc66aa321ca88c2\",\"sSize\":\"11628\"},\"audioFile\":null,\"title\":\"\\u201eDie L\\u00fccken verschlammen\\u201c\",\"teaser\":\"Wie schlimm ist extremes Hochwasser eigentlich f\\u00fcr Bachflohkrebse und andere Kleinstlebewesen? Ein Gespr\\u00e4ch mit dem Gew\\u00e4sser\\u00f6kologen Hans J\\u00fcrgen Hahn \\u00fcber verstopfte Bachsedimente, zu niedrige Grundwasserspiegel und eingebaute Fl\\u00fcsse\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5787863\",\"pageNames\":[\"s00811249.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097290.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663387\",\"sha256\":\"d0b0307911496e268882c2540bace7396f77e06840b8650e40e64b5139550bbb\",\"sSize\":\"75772\"},{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097289.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663395\",\"sha256\":\"a7d38b1eb14a03fb46b5ce4a5d475b3e8e099d696ee3b8fa8964649e43128fa8\",\"sSize\":\"15783\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097290.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663388\",\"sha256\":\"e170d68df6adc24ab58904ab80a53436dab12e4e0c1795d44a5c56e1b4e8fa82\",\"sSize\":\"436018\"}],\"authorList\":null}},{\"sDate\":\"2021-07-30\",\"snippet\":\"... ihre Geb\\u00e4ude gegen Starkregen oder <span class=\\\"snippet\\\">Hochwasser<\\/span> wappnen. Eine Elementarsch\\u00e4den-Versicherung \\u201esollte...\",\"teaser\":\"Die Gr\\u00fcnen-Spitze versucht sich an einem Balanceakt: Sie will das Leid und die Opfer der Flutkatastrophe nicht instrumentalisieren, aber mit konkreten Ideen f\\u00fcr Hochwasserschutz und Klimavorsorge punkten\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Schritt f\\u00fcr Schritt in die Offensive<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A3_2017 eptPolitik\\\"><h2 class=\\\"Titel\\\">Schritt f\\u00fcr Schritt in die Offensive<\\/h2><h4 class=\\\"Unterzeile\\\">Die Gr\\u00fcnen-Spitze versucht sich an einem Balanceakt: Sie will das Leid und die Opfer der Flutkatastrophe nicht instrumentalisieren, aber mit konkreten Ideen f\\u00fcr Hochwasserschutz und Klimavorsorge punkten<\\/h4><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A3_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Ulrich Schulte<\\/span><\\/p><p class=\\\"oeinz\\\">Die Gr\\u00fcnen-Spitze will mit Ideen zur Bek\\u00e4mpfung von Flutkatastrophen wieder in die Offensive kommen. Am Donnerstag stellten Parteichef Robert Habeck, Fraktionschef Anton Hofreiter und Klimaschutzexperte Oliver Krischer ein achtseitiges Papier vor, in dem sie eine bessere Klimavorsorge und intensiveren Hochwasserschutz forderten. \\u201eVorsorge muss zum Leitprinzip einer neuen Politik werden\\u201c, hei\\u00dft es darin.<\\/p><p class=\\\"meinz\\\">In dem Papier finden sich mehrere Vorschl\\u00e4ge. Ein vom Bund aufgelegter Klimavorsorge-Fonds von 25 Milliarden Euro solle Kommunen bei der Anpassung an den Klimawandel unterst\\u00fctzen \\u2013 und etwa die Umwandlung in \\u201eSchwammst\\u00e4dte\\u201c \\u2013 also St\\u00e4dte, die durch geschickte Planung viel Wasser aufnehmen k\\u00f6nnen \\u2013 oder den Umbau der Kanalisation finanzieren.<\\/p><p class=\\\"meinz\\\">F\\u00fcr Hausbesitzer solle es k\\u00fcnftig Steueranreize oder F\\u00f6rdermittel geben, wenn sie ihre Geb\\u00e4ude gegen Starkregen oder <span class=\\\"snippet\\\">Hochwasser<\\/span> wappnen. Eine Elementarsch\\u00e4den-Versicherung \\u201esollte Standard werden\\u201c, fordert die Gr\\u00fcnen-Spitze. Au\\u00dferdem m\\u00fcsse es ein Klimasch\\u00e4den-Kataster geben, das regionale Folgen erfasst. Sie schl\\u00e4gt au\\u00dferdem vor, neue Standorte in den Hochwasserschutz einzubeziehen. \\u201eBesser eine Kiesgrube oder ein Braunkohletagebau laufen kontrolliert voll, als dass Siedlungen \\u00fcberflutet werden.\\u201c<\\/p><p class=\\\"meinz\\\">Die Hochwasserkatastrophe in Nordrhein-Westfalen und Rheinland-Pfalz sei \\u201eein Fenster, durch das wir auf unsere Zukunft schauen\\u201c, hei\\u00dft es in dem Papier. Nicht jede Naturkatastrophe sei eine unmittelbare Folge der Erderhitzung. Starkregen, hei\\u00dfe Sommer, Waldbr\\u00e4nde und Sturmfluten habe es schon immer gegeben \\u2013 \\u201eaber die Heftigkeit, Summe und die schnelle Abfolge der Extremwetterereignisse sind ein untr\\u00fcglicher Indikator daf\\u00fcr, dass die Klimakrise da ist und Menschenleben kostet\\u201c.<\\/p><p class=\\\"meinz\\\">Die Gr\\u00fcnen \\u00e4ndern damit die Tonlage. Als vor zwei Wochen ganze D\\u00f6rfer in den Fluten untergingen, agierten sie maximal vorsichtig. Kanzlerkandidatin Annalena Baerbock reiste ins Krisengebiet, aber nicht in die zerst\\u00f6rten Hotspots und ohne Kamerabegleitung. Sie lobte die Rettungskr\\u00e4fte und forderte unb\\u00fcrokratische Hilfe f\\u00fcr die Opfer. Aber dass die Klimakrise solche Extremwetterereignisse wahrscheinlicher macht, tauchte in der Kommunikation anfangs nicht auf. Olaf Scholz und Armin Laschet stellten den Zusammenhang damals schneller her als die \\u00d6kopartei.<\\/p><p class=\\\"meinz\\\">Die Gr\\u00fcnen-Spitze versucht eine Gratwanderung. Sie will den Eindruck vermeiden, das Leid und die Toten zu instrumentalisieren, aber gleichzeitig mit dem Thema in die Offensive kommen. Das Strategie lautet: Schritt f\\u00fcr Schritt zur\\u00fcck in die inhaltliche Auseinandersetzung, weg von den Skand\\u00e4lchen um Baerbocks Buch oder Lebenslauf. \\u201eEs kommt auf den Tonfall an\\u201c, hei\\u00dft es bei den Gr\\u00fcnen. \\u201eWir d\\u00fcrfen nicht wie Besserwisser r\\u00fcberkommen, die den Eindruck vermitteln, sie h\\u00e4tten es schon immer gewusst.\\u201c<\\/p><p class=\\\"meinz\\\">Diese Woche nun startete die n\\u00e4chste Phase der \\u201eInhalte vor\\u201c-Strategie. Der Zur\\u00fcckhaltung der ersten Tage folgen nun Vorst\\u00f6\\u00dfe, die auf eine bessere Klimavorsorge zielen. Den Anfang machten am Montag Baerbock und die Innenpolitikerin Irene Mihalic. Sie stellten in der Berliner Bundespressekonferenz Ideen f\\u00fcr einen zentralisierten Katastrophenschutz vor. Dann folgte das Papier von Habeck, Hofreiter und Krischer. Auch ein dritter Akt ist geplant. Kommende Woche werden Baerbock und Habeck ein Klimaschutz-Sofortprogramm vorstellen, dass im Fall einer Regierungsbeteiligung schnell umgesetzt werden soll.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a45\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.7.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a45\\/\\\"><h4 class=\\\"AutorName\\\">Ulrich Schulte<\\/h4><h5 class=\\\"AutorJob\\\">Leiter Parlamentsb\\u00fcro<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Ulrich Schulte &lt;schulte@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorTwitter\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/twitter.com\\/@UlrichSchulte\\\"><img class=\\\"icon\\\" src=\\\"resources\\/microblog_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorFacebook\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/facebook.com\\/Ulrich Schulte\\\"><img class=\\\"icon\\\" src=\\\"resources\\/facebook_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Ulrich Schulte, Jahrgang 1974, schreibt \\u00fcber Bundespolitik und Parteien. Er besch\\u00e4ftigt sich vor allem mit der SPD und den Gr\\u00fcnen. Schulte arbeitet seit 2003 f\\u00fcr die taz. Bevor er 2011 ins Parlamentsb\\u00fcro wechselte, war er drei Jahre lang Chef des Inlands-Ressorts. <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-30.b061da1dfd13fcad5f8b4bbf8e0ef747c4e5a7b5\",\"article\":{\"articleHtml\":{\"name\":\"art03605384.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627578772\",\"sha256\":\"0301842eb590ce7c010924c2be13277b357b3f112a9c6dc5c3e2b2365710602e\",\"sSize\":\"7225\"},\"audioFile\":null,\"title\":\"Schritt f\\u00fcr Schritt in die Offensive\",\"teaser\":\"Die Gr\\u00fcnen-Spitze versucht sich an einem Balanceakt: Sie will das Leid und die Opfer der Flutkatastrophe nicht instrumentalisieren, aber mit konkreten Ideen f\\u00fcr Hochwasserschutz und Klimavorsorge punkten\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5786074\",\"pageNames\":[\"s00811154.pdf\"],\"imageList\":null,\"authorList\":[{\"name\":\"Ulrich Schulte\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.7.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993014\",\"sha256\":\"4bb299a2fcfffd3a318195a4fcb7a9a44f3685ff5ea6e8e077c2100d6d713fbb\",\"sSize\":\"42770\"}}]}},{\"sDate\":\"2021-07-26\",\"snippet\":\"... Internet ein Foto einer vom <span class=\\\"snippet\\\">Hochwasser<\\/span> zerst\\u00f6rten Wohnung \\u2013 und ver\\u00f6ffentlichten einen...\",\"teaser\":\"Viele Menschen wollen die Menschen in den Flutgebieten unterst\\u00fctzen. Aber wie? Was kommt wirklich bei den Betroffenen an, die vielfach vor dem Nichts stehen?\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Helfen, aber richtig<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><h2 class=\\\"Titel\\\">Helfen, aber richtig<\\/h2><h4 class=\\\"Unterzeile\\\">Viele Menschen wollen die Menschen in den Flutgebieten unterst\\u00fctzen. Aber wie?  \\nWas kommt wirklich bei den Betroffenen an, die vielfach vor dem Nichts stehen?<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptPolitik\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=532.914 BH=336&#10;IB=1825 IH=1217&#10;S=4 b=660 h= C=Main&#10;id=1096271 typ=JPEG&#10;\\\" src=\\\"Media.1096271.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1096271.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Aufr\\u00e4umarbeiten im an der Ahr gelegenen Sinzig in Rheinland-Pfalz <span class=\\\"zAu\\\">Foto: Wolfgang Rattay\\/reuters<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Hanna Gersmann<\\/span><\\/p><p class=\\\"oeinz\\\">Die Betr\\u00fcger sind schon da. Die Polizei K\\u00f6ln warnt die Menschen in den Flutgebieten. Und jene, die f\\u00fcr sie spenden wollen. Der Grund: Hochwassergesch\\u00e4digte bestellten im Internet einen Bautrockner, um ihre R\\u00e4ume zu entfeuchten, \\u00fcberwiesen die Rechnung. Doch die Ware kam nie an. Ans Telefon ging auch niemand. Eine Elektrofirma, die angab, zu Reparaturarbeiten zu kommen, daf\\u00fcr aber vorab eine Zahlung verlangte, tauchte auch nie auf. In einem anderen Fall kopierten Gauner aus dem Internet ein Foto einer vom <span class=\\\"snippet\\\">Hochwasser<\\/span> zerst\\u00f6rten Wohnung \\u2013 und ver\\u00f6ffentlichten einen Spendenaufruf. Der sei mittlerweile wieder gel\\u00f6scht, so die Polizei. Doch r\\u00e4t sie:\\u201e\\u00dcbergeben oder \\u00fcberweisen Sie Geld nie an unbekannte Personen.\\u201c<\\/p><p class=\\\"meinz\\\">Die Frage: Wie l\\u00e4sst sich helfen? Was kommt wirklich bei den Menschen an, die nach der Hochwasserkatastrophe vielfach vor dem Nichts stehen? Die H\\u00e4user verw\\u00fcstet. Br\\u00fccken kaputt. Stromleitungen zerst\\u00f6rt. Mancherorts \\u00e4hnelt es einem Kriegsgebiet seit die Unwetter erst nach Nordrhein-Westfalen und Rheinland-Pfalz kamen, sp\\u00e4ter auch ins Berchtesgadener Land und ins \\u00f6stliche Sachsen. Schon jetzt packen viele mit an, Nachbarn, Urlauber.<\\/p><p class=\\\"meinz\\\">Aber sich einfach mit Besen, Eimer, Gummistiefeln aufzumachen, ist derzeit keine gute Idee. Das Bundesamt f\\u00fcr Bev\\u00f6lkerungsschutz und Katastrophenhilfe schreibt auf seiner Internetseite: \\u201eWarnhinweis: Bitte beachten Sie: Fahren Sie nicht auf eigene Faust in die betroffenen Gebiete, um zu helfen!\\u201c Helfende k\\u00f6nnen mit ihren Autos Stra\\u00dfen blockieren, das Aufr\\u00e4umen behindern.<\\/p><p class=\\\"meinz\\\">Wer vor Ort helfen will, vielleicht auch als Unternehmen mit Werkzeug und Ger\\u00e4ten oder als Privatperson mit einer Unterkunft oder Sonstigem, kann sich bei betroffenen Gemeinden und St\\u00e4dten melden. Die haben oft B\\u00fcrgertelefone und Hotlines geschaltet, um Hilfsangebote und Eins\\u00e4tze vor Ort zu koordinieren. Das Bundesamt hat die Anlaufstellen auf seiner Homepage gelistet \\u2013 und gibt noch einen dringenden Hinweis: \\u201eWICHTIG! Wenn Sie beabsichtigen Sachspenden zu spenden, kl\\u00e4ren Sie dies unbedingt vorab mit den betreffenden Organisationen und Initiativen ab \\u2013 viele Einrichtungen haben einen Annahmestopp f\\u00fcr Sachspenden ausgesprochen.\\u201c<\\/p><p class=\\\"meinz\\\">Kleidung, Schuhe, M\\u00f6bel t\\u00fcrmen sich vielerorts, weil die Unterst\\u00fctzung enorm, viel bereits angekommen ist. Das muss nun auch erst gesichtet und sortiert werden. Tische oder St\\u00fchle werden ohnehin erst in einigen Wochen gebraucht, nach dem Aufr\\u00e4umen. So ist derzeit vor allem eins gefragt: Geld.<\\/p><p class=\\\"meinz\\\">Das habe auch mit dem Selbstwertgef\\u00fchl zu tun, sagt Thomas Eiting. Er ist Pressesprecher der Stadt Wuppertal und meint: \\u201eWenn man fast alles verloren hat, dann m\\u00f6chte man, so gut wie es gemeint ist, nicht auch noch eine gebrauchte Hose haben. Sondern dann m\\u00f6chte man zumindest diese W\\u00fcrde behalten, sich selbst eine neue Hose kaufen zu k\\u00f6nnen.\\u201c<\\/p><p class=\\\"meinz\\\">Zwar hat die Bundesregierung bereits zugesagt, schnell und unb\\u00fcrokratisch mit 200 Millionen Euro zu helfen. Zusammen mit den Hilfen aus den Bundesl\\u00e4ndern werden so erst mal insgesamt 400 Millionen Euro aufgebracht, um Notlagen zu \\u00fcberbr\\u00fccken, das Schlimmste an Geb\\u00e4uden und Infrastruktur zu beseitigen. Aber das reicht keinesfalls, die Sch\\u00e4den sind milliardenschwer. Und gegen die Flut versichert sind l\\u00e4ngst nicht alle vor Ort.<\\/p><p class=\\\"meinz\\\">Es gibt viele Spendenaufrufe, zum Beispiel von Sportvereinen wie dem 1. FC K\\u00f6ln oder dem Deutschen Gewerkschaftsbund. Von den gro\\u00dfen Hilfsorganisationen sowieso. Das Deutsche Rote Kreuz hilft derzeit zum Beispiel mit 3.500 Leuten bei der Versorgung mit Trinkwasser, Strom, Hygieneartikeln, stellt mobile Arztpraxen auf, bietet psychologische Beratung.<\\/p><p class=\\\"meinz\\\">Nordrhein-Westfalen und Rheinland-Pfalz selbst haben auch Spendenkonten eingerichtet. Das Geld soll \\u00fcber die Kreisverwaltungen an die Betroffenen gehen. \\u201eVor Ort kann am besten gesehen werden, wo die Not am gr\\u00f6\\u00dften ist und wie Abhilfe geschaffen werden kann\\u201c, erkl\\u00e4rt der rheinland-pf\\u00e4lzische Innenminister Roger Lewentz.<\\/p><p class=\\\"meinz\\\">Am sichersten sei es beim Spenden, auf die zu setzen \\u2013 so schreibt die Polizei K\\u00f6ln \\u2013, die \\u201eSie pers\\u00f6nlich kennen oder \\u00fcber deren Seriosit\\u00e4t Sie sich ausreichend informiert haben\\u201c. Wer sich unsicher ist, kann auf das DZI-Spendensiegel achten. Das bekommt nur, so erkl\\u00e4rt die Stiftung Warentest, wer sich nach strengen Kriterien des Deutschen Zentralinstituts f\\u00fcr soziale Fragen pr\\u00fcfen lasse und nicht mehr als 30 Prozent des Geldes f\\u00fcr Verwaltung und Werbung ausgibt. Das DZI hat extra f\\u00fcr die \\u201eUnwetterkatastrophe im Westen Deutschlands\\u201c Adressen und Kontonummern von Organisationen zusammengestellt.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a40\\/\\\"\\/><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a40\\/\\\"><h4 class=\\\"AutorName\\\">Hanna Gersmann<\\/h4><h5 class=\\\"AutorJob\\\">taz-Autorin<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Hanna Gersmann &lt;gersmann@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> War von 2002 bis 2013 in der taz, leitete dort zuletzt das Inlandsressort. Jetzt geh\\u00f6rt sie zum B\\u00fcro die-korrespondenten.de im Haus der Bundespressekonferenz in Berlin. Sie schreibt vor allem \\u00fcber Umwelt-, Verbraucher- und Wirtschaftspolitik. <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-26.3bdcd4ede45bc22a2cf23080ac2403ade83c39f0\",\"article\":{\"articleHtml\":{\"name\":\"art03604310.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627231908\",\"sha256\":\"390a8a983f6cc870fe1d3dcf8a4b69c18e3dcf4e72eaf02c7ee9ab095e714bf7\",\"sSize\":\"8378\"},\"audioFile\":null,\"title\":\"Helfen, aber richtig\",\"teaser\":\"Viele Menschen wollen die Menschen in den Flutgebieten unterst\\u00fctzen. Aber wie? Was kommt wirklich bei den Betroffenen an, die vielfach vor dem Nichts stehen?\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5785646\",\"pageNames\":[\"s00810884.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1096271.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627231907\",\"sha256\":\"e8ccd2a5f70ac3886895b7ac6a0c177fbef4cc8810f7f73ff42162b572a4ca62\",\"sSize\":\"74540\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1096271.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627231908\",\"sha256\":\"4344eb4ca22bfdbd0df9fc1e8b23d129bdb2143ff9b0598c19cc49f0d46a828e\",\"sSize\":\"652511\"}],\"authorList\":null}},{\"sDate\":\"2021-07-28\",\"snippet\":\"... Region \\u201eextrem ausgiebigen Dauerregen\\u201c und <span class=\\\"snippet\\\">Hochwasser<\\/span> angek\\u00fcndigt. Die Konsequenzen dieser Warnungen... von ihrer Tochter \\u00fcber das <span class=\\\"snippet\\\">Hochwasser<\\/span> in Walpforzheim Ahr-aufw\\u00e4rts informiert... in den Tagen nach dem <span class=\\\"snippet\\\">Hochwasser<\\/span> unmittelbar mit, wie die Menschen...\",\"teaser\":\"Trotz aller vorzeitigen Warnungen vor den Unwettern und ihren Folgen: In Sinzig sind in einem Wohnheim zw\\u00f6lf Menschen mit Behinderung in der Flut gestorben\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Wie konnte das passieren?<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A1_2017 eptPolitik\\\"><h2 class=\\\"Titel\\\">Wie konnte das passieren?<\\/h2><h4 class=\\\"Unterzeile\\\">Trotz aller vorzeitigen Warnungen vor den Unwettern und ihren Folgen:  \\nIn Sinzig sind in einem Wohnheim zw\\u00f6lf Menschen mit Behinderung in der Flut gestorben<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptPolitik\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=464.881 BH=309.301&#10;IB=1575 IH=1048&#10;S=3.5 b=660 h= C=Main&#10;id=1096637 typ=JPEG&#10;\\\" src=\\\"Media.1096637.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1096637.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Flowers in the dirt: Trauerbekundungen vor dem Lebenshilfehaus in Sinzig, 17. Juli <span class=\\\"zAu\\\">Foto: Lena Mucha\\/ NYXT\\/redux\\/laif<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A1_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Aus Sinzig <span class=\\\"zAu\\\">Lena von Seggern<\\/span><\\/p><p class=\\\"Initial\\\">Dort, wo die Augen w\\u00e4ren, hat die Statue nur zwei klaffende L\\u00f6cher. Der Mund ist weit aufgerissen. Veronika Wiertz (60), die die Statue aus dem Garten ihres \\u00fcberfluteten Hauses gerettet hat, setzt den Kopf vorsichtig auf eine Kommode. Die Kommode ist durch das Wasser unbrauchbar geworden. Die Statue scheint erst durch die Verw\\u00fcstung zu ihrem eigentlichen Ausdruck gefunden zu haben. \\u201eDie klagt jetzt f\\u00fcr mich\\u201c, sagt Veronika Wiertz. Sie und ihr Mann Uli Martin wohnen in der Sinziger Pestalozzistra\\u00dfe. Hier steht ein Wohnheim f\\u00fcr Menschen mit Behinderung. Es hei\\u00dft \\u201eLebenshilfehaus\\u201c. Veronika Wiertz und Uli Martin mussten mitverfolgen, wie 12 der 36 Bewohnerinnen und Bewohner in der Flut der Ahr ertranken. Zw\\u00f6lf Menschen, die k\\u00f6rperlich und geistig beeintr\\u00e4chtigt waren.<\\/p><p class=\\\"meinz\\\">Am Mittwoch, dem 14. Juli 2021 geschah es. \\u201eGegen 22.30 Uhr war ich noch mit dem Hund unterwegs. Als ich mich so um 23.30 Uhr ins Bett legte, war noch nichts zu sehen von der Flut.\\u201c Aber etwas stimmte nicht. Wiertz und Martin erz\\u00e4hlen, dass sie gegen Mitternacht aufwachten. Wiertz trat vor die T\\u00fcr. Das Wasser stand bereits bis zur Treppe. Sie h\\u00f6rten Hilfeschreie aus dem Lebenshilfehaus. Wiertz traute sich nicht mehr, \\u00fcber die Stra\\u00dfe zu gehen. Zu tief war das Wasser im Garten des Wohnheims, aus dem die Schreie kamen, und zu stark die Str\\u00f6mung des rei\\u00dfenden Flusses. Martin versuchte die Feuerwehr zu erreichen: \\u201eDa schreit jemand dr\\u00fcben im Wohnheim um Hilfe.\\u201c<\\/p><p class=\\\"meinz\\\">Zwei junge Feuerwehrm\\u00e4nner tauchten unvermittelt in der Stra\\u00dfe auf. Sie versuchten \\u00fcber die Stra\\u00dfe zu dem Wohnheim zu kommen, aber Veronika warnte sie: \\u201eDa vorne geht es sehr tief runter. Das ist zu gef\\u00e4hrlich.\\u201c<\\/p><p class=\\\"meinz\\\">Zu dieser Zeit befanden sich 36 Menschen mit geistiger und k\\u00f6rperlicher Beeintr\\u00e4chtigung im Wohnheim. Die Feuerwehr hatte nach Informationen des SWR die Nachtwache \\u2013 bestehend aus einem einzigen Mitarbeiter \\u2013 gewarnt und zur Evakuierung der Bewohner*innen aufgerufen. Doch das Wasser sei innerhalb von Minuten so hoch gestiegen, dass eine Rettung nicht mehr m\\u00f6glich war. In der Nachbarschaft erz\\u00e4hlt man, als das Wasser das Erdgeschoss \\u00fcberflutete, sei der Mitarbeiter gerade damit besch\\u00e4ftigt gewesen, Menschen aus einem anderen Geb\\u00e4ude herauszuhelfen. Der Bewohner, der sich am Lebenshaus ans Fenster klammerte, wurde nach etwa 30 Minuten von einem Rettungsboot der Feuerwehr aufgenommen und in Sicherheit gebracht. Zw\\u00f6lf der Bewohnerinnen und Bewohner des Wohnheims starben in der Flut.<\\/p><p class=\\\"meinz\\\">Die Staatsanwaltschaft Koblenz hat das Todesermittlungsverfahren, wie das in diesen F\\u00e4llen \\u00fcblich ist, aufgenommen. Es wird au\\u00dferdem gepr\\u00fcft, ob sich eine der beteiligten Personen strafbar gemacht hat. Kreisverwaltung und Lebenshilfe m\\u00f6chten zu dem genauen Ablauf noch keine Angaben machen. Deshalb l\\u00e4sst sich der genaue Hergang noch nicht mit Sicherheit rekonstruieren. Unklar ist zum Beispiel, wann genau die Feuerwehr das Signal zur Evakuierung des Wohnheims gegeben hatte.<\\/p><p class=\\\"meinz\\\">Grunds\\u00e4tzlich gab es fr\\u00fchzeitig Warnungen vor den schweren Unwettern. Die Europ\\u00e4ische Flutwarnbeh\\u00f6rde warnte schon vier Tage vor der Flut vor m\\u00f6glichen \\u00dcberschwemmungen. Der Deutsche Wetterdienst hatte f\\u00fcr die Region \\u201eextrem ausgiebigen Dauerregen\\u201c und <span class=\\\"snippet\\\">Hochwasser<\\/span> angek\\u00fcndigt. Die Konsequenzen dieser Warnungen sind allerdings Sache der Zust\\u00e4ndigen vor Ort. Der Katastrophenschutz ist aus rechtlicher Sicht Sache der Kommunen und Kreise.<\\/p><p class=\\\"meinz\\\">Am Dienstag, den 13. Juli wurden nach Angaben des Kreises Ahrweiler alle Feuerwehrleiter im Kreis \\u00fcber die Wetterprognose informiert. So auch der in Sinzig. Die Feuerwehr dort besteht ausschlie\\u00dflich aus Ehrenamtlichen, so wie 95 Prozent der Feuerwehren in ganz Deutschland. \\u201eF\\u00fcr jede Art von Notfall gibt es klar vorgegebene Notfallma\\u00dfnahmen\\u201c, so Silvia Oestreicher, Pressesprecherin beim Deutschen Feuerwehrverband. \\u201eAber die \\u00f6rtlichen Feuerwehren m\\u00fcssen selbst einsch\\u00e4tzen, wie hoch die Gefahrenstufe vor Ort ist.\\u201c Sie m\\u00fcssen die vorgegebenen Musterpl\\u00e4ne entsprechend anpassen.<\\/p><p class=\\\"meinz\\\">Und das ist nicht so leicht, denn \\u201e2016 war die bislang h\\u00f6chste Flut an der Ahr bei etwa 2,8 Metern\\u201c erz\\u00e4hlt der ehrenamtliche Feuerwehrmann, Alex Reich (50) aus H\\u00f6nningen, einem anderen Ort an der Ahr. \\u201eWir haben viele Lehren aus der damaligen Flut gezogen und Ma\\u00dfnahmenpl\\u00e4ne aufgestellt. Ich habe mich also darauf eingestellt, etwa zehn Keller im Dorf leerpumpen zu m\\u00fcssen. Aber was dann kam, damit hat keiner von uns gerechnet.\\u201c Die Flut war diesmal mehr als doppelt so hoch in H\\u00f6nningen. In Sinzig war sie sogar acht Meter hoch.<\\/p><p class=\\\"meinz\\\">Reichs Frau Beate ist selbst fast in den Fluten umgekommen. Sie wollte im Kindergarten Spatzennest, der am Ufer der Ahr liegt, Sands\\u00e4cke f\\u00fcllen und zum Schutz der H\\u00e4user ins Dorf bringen. Hier wurden sie und ihre beiden Kinder von der Flut \\u00fcberrascht. \\u201eIch kam mir vor wie diese Frau aus dem Film \\u201aTitanic\\u2018, die ihre beiden Kinder noch in den Schlaf singt, bevor sie alle drei mit dem Schiff im Wasser versinken\\u201c, sagt sie. Erst am n\\u00e4chsten Morgen konnten sie und ihre beiden Kinder aus dem wasserumsp\\u00fclten Kindergarten geborgen werden.<\\/p><p class=\\\"meinz\\\">\\u201eWir bei der Feuerwehr k\\u00f6nnen nicht einsch\\u00e4tzen, welche konkreten Ma\\u00dfnahmen es braucht, um auf eine so abstrakte Prognose wie \\u201a50 Milliliter Niederschlag pro Quadratmeter\\u2018 zu reagieren\\u201c, sagt Reich. \\u201eDazu brauchen wir Experten, die erkl\\u00e4ren, was konkret zu tun ist.\\u201c<\\/p><p class=\\\"meinz\\\">Die Stadt Sinzig hatte die Bev\\u00f6lkerung erstmals am 13. Juli gewarnt. Auf ihrer Facebook-Seite k\\u00fcndigte sie \\u201eheftigen Regen\\u201c an und riet \\u201einsbesondere den Anliegern in Stra\\u00dfenz\\u00fcgen im unmittelbaren Einzugsbereich der Ahr, entsprechende Vorkehrungen zu treffen\\u201c. Am Abend des 14. Juli mahnte sie dann, den Uferbereich \\u201eunbedingt\\u201c zu meiden. Noch sp\\u00e4ter am Abend wurde in Sinzig der Katastrophenschutzalarm ausgerufen. In dieser Situation k\\u00f6nnen auch Einsatzkr\\u00e4fte aus umliegenden Kreisen und Kommunen herangezogen werden. Die Feuerwehr in Sinzig entschied sich dazu, alle Geb\\u00e4ude, die bis zu 50 Meter von der Ahr entfernt liegen, zu evakuieren.<\\/p><p class=\\\"meinz\\\">Das Geb\\u00e4ude der Lebenshilfe liegt aber mehr als 250 Meter vom Ufer entfernt. \\u201eEine Hauswirtschaftskraft der Lebenshilfe wurde nachts um 2 Uhr von ihrer Tochter \\u00fcber das <span class=\\\"snippet\\\">Hochwasser<\\/span> in Walpforzheim Ahr-aufw\\u00e4rts informiert\\u201c, erz\\u00e4hlt Kerstin Laubmann. Sie ist Pfarrerin der Evangelischen Gemeinde Remagen-Sinzig. \\u201eSofort versuchten sie und ihr Mann, Kontakt aufzunehmen zu den Bewohnenden. Als das nicht klappte, versuchte ihr Mann, in die Pestalozzistra\\u00dfe zu fahren, um dort zu helfen. Aber er kam schon gar nicht mehr hin: Zwei Stra\\u00dfen oberhalb der Pestalozzistra\\u00dfe sah er im Dunkeln schon das Wasser schimmern.\\u201c<\\/p><p class=\\\"meinz\\\">Als Pfarrerin bekommt Laubmann in den Tagen nach dem <span class=\\\"snippet\\\">Hochwasser<\\/span> unmittelbar mit, wie die Menschen im Ort mit der Katastrophe umgehen. Die Anteilnahme in Sinzig sei gro\\u00df. \\u201eSo einen intensiven Gottesdienst wie diesen nach der Flut habe ich, glaube ich, noch nie gefeiert\\u201c, erz\\u00e4hlt sie. \\u201eSo viel Trauer, Verluste, Betroffenheit. Viele hat es so zerrissen. Ich habe noch nie erlebt, dass Menschen noch eine Stunde nach dem Gottesdienst zusammenstanden, um miteinander sprechen zu k\\u00f6nnen. Und das in Anbetracht der schwierigen Umst\\u00e4nde, die die Pandemie bereitet.\\u201c<\\/p><p class=\\\"meinz\\\">Gleichzeitig sind viele Gemeindemitglieder aber noch ganz anders gefordert. Viele H\\u00e4user sind nach wie vor unbewohnbar. Die Aufr\\u00e4umarbeiten \\u00fcberlagern vieles. \\u201eDie Menschen sind noch nicht bereit f\\u00fcr Seelsorge\\u201c, sagt Laubmann. \\u201eJetzt arbeiten sie erst in ihren H\\u00e4usern. Viele haben ihre Kinder zu Freunden und Verwandten gegeben, damit sie hier in Ruhe die H\\u00e4user wieder herrichten k\\u00f6nnen.\\u201c Erst wenn der Alltag zur\\u00fcckkomme, werde Zeit sein f\\u00fcr intensive Gespr\\u00e4che und die Seelsorge.<\\/p><p class=\\\"meinz\\\">Bei anderen hat die Aufarbeitung des Erlebten schon begonnen. Matthias Mandos ist Gesch\\u00e4ftsf\\u00fchrer der Lebenshilfe Rheinland-Pfalz. Er richtet den Blick auf die Zur\\u00fcckgebliebenen: \\u201eDie Mitarbeitenden der Lebenshilfe sind leicht bis schwer traumatisiert. Sie werden aktuell psychologisch betreut. Die Menschen hier vor Ort haben ganze Arbeit geleistet. Davor kann man nur den Hut ziehen\\u201c, sagt er.<\\/p><p class=\\\"meinz\\\">Kurz nach der Katastrophe hat er sich mit dem Sozialminister, dem Opferbeauftragten und dem Landesbeauftragten f\\u00fcr die Belange von Menschen mit Behinderung getroffen. Gemeinsam haben sie beraten, was jetzt f\\u00fcr die Menschen aus dem Wohnheim und deren Angeh\\u00f6rige zu tun ist. Auf der Tagesordnung stand zun\\u00e4chst die Frage, wo sie zeitweilig untergebracht und betreut werden k\\u00f6nnen. Bewohnbar ist ihr bisheriges Zuhause schlie\\u00dflich nicht mehr. Ein Teil der Bewohner*innen wohnt vor\\u00fcbergehend in einer Einrichtung im Westerwald.<\\/p><p class=\\\"meinz\\\">F\\u00fcr konkrete Lehren aus dem Ungl\\u00fcck ist es f\\u00fcr die Verantwortlichen vor Ort noch zu fr\\u00fch. Fragen stellen sich zu Gen\\u00fcge: Was muss sich in Zukunft \\u00e4ndern? Wie k\\u00f6nnen Warnungen schneller ankommen? Kann eine einzige Nachtwache \\u00fcberhaupt die ganze Einrichtung besch\\u00fctzen? Auf solche Fragen gibt es in Sinzig noch keine Antworten. In der Region ist man noch zu besch\\u00e4ftigt: mit Aufr\\u00e4umen, der Suche nach Vermissten und dem Bergen von Leichen.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a84394\\/\\\"\\/><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a84394\\/\\\"><h4 class=\\\"AutorName\\\">Lena von Seggern<\\/h4><\\/a><\\/div><\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-28.f5b7f222536a224715fdf50bdf82261440943d9f\",\"article\":{\"articleHtml\":{\"name\":\"art03604800.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627403807\",\"sha256\":\"b23934fc8b4ce96ffbaede8cfc4e674decbe6e37b5495c2264775b60834a81af\",\"sSize\":\"12617\"},\"audioFile\":null,\"title\":\"Wie konnte das passieren?\",\"teaser\":\"Trotz aller vorzeitigen Warnungen vor den Unwettern und ihren Folgen: In Sinzig sind in einem Wohnheim zw\\u00f6lf Menschen mit Behinderung in der Flut gestorben\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5785903\",\"pageNames\":[\"s00811004.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1096637.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627403806\",\"sha256\":\"332c5c493c19ccdb1b54e9d4a1461e3a5d0c9715ebfee5d89ee382b89066ed5c\",\"sSize\":\"80429\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1096637.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627403807\",\"sha256\":\"0e7e98a48d4642a701013eb9c8bf157cf3672bef1ff26fc195a7d7c985630825\",\"sSize\":\"436470\"}],\"authorList\":null}},{\"sDate\":\"2021-07-27\",\"snippet\":\"... Stellen nicht ausreichend vor dem <span class=\\\"snippet\\\">Hochwasser<\\/span> vor zwei Wochen gewarnt worden... Gr\\u00fcnen, die sich nach dem <span class=\\\"snippet\\\">Hochwasser<\\/span> zun\\u00e4chst stark zur\\u00fcckgehalten und auf...\",\"teaser\":\"Innenausschuss ber\\u00e4t \\u00fcber Konsequenzen aus der Flutkatastrophe. Baerbock stellt Sofortprogramm vor und kritisiert unklaren Klimakurs der Union\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Streit um Klima und Katastrophenschutz<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><h2 class=\\\"Titel\\\">Streit um Klima und Katastrophenschutz<\\/h2><h4 class=\\\"Unterzeile\\\">Innenausschuss ber\\u00e4t \\u00fcber Konsequenzen aus der Flutkatastrophe.  \\nBaerbock stellt Sofortprogramm vor und kritisiert unklaren Klimakurs der Union<\\/h4><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Malte Kreutzfeldt<\\/span>  \\nund <span class=\\\"zAu\\\">Tobias Schulze<\\/span><\\/p><p class=\\\"oeinz\\\">Eigentlich soll es um konkrete Ver\\u00e4nderungen beim Katastrophenschutz gehen, als Annalena Baerbock am Montag zum ersten Mal seit ihrem Urlaub in Berlin vor die Presse tritt. Zusammen mit der Innenpolitikerin Irene Mihalic (Gr\\u00fcne) stellt sie dazu ein Papier vor, in dem etwa eine \\u201eSt\\u00e4rkung der Fr\\u00fchwarnsysteme und Krisenforschung\\u201c gefordert wird. Doch schnell kommt Baerbock auf das Thema zu sprechen, mit dem sich die Gr\\u00fcnen nach der Katastrophe wirklich profilieren wollen: die Klimapolitik. \\u201eSie wissen, wir arbeiten an diesem Thema nicht erst seit gestern\\u201c, sagt Baerbock als Spitze gegen die politische Konkurrenz.<\\/p><p class=\\\"meinz\\\">Es ist offensichtlich: Die Kanzlerkandidatin der Gr\\u00fcnen, die sich nach dem <span class=\\\"snippet\\\">Hochwasser<\\/span> zun\\u00e4chst stark zur\\u00fcckgehalten und auf \\u00f6ffentliche Auftritte im Flutgebiet ebenso verzichtet hatte wie auf Vorw\\u00fcrfe gegen andere, geht wieder st\\u00e4rker in die Offensive. Vor allem den Unions-Kanzlerkandidaten Armin Laschet greift Baerbock direkt an. Mit ihrer Forderung, dass die Politik \\u201enach einer so unglaublichen Katastrophe nicht einfach weitermachen kann wie bisher\\u201c, stellt sie sich gegen Laschets Aussage, \\u201ewegen eines solchen Tages\\u201c \\u00e4ndere man \\u201enicht einfach die Politik\\u201c. Mit der Ansage, sie wolle \\u201eKlimaschutzpolitik nicht nur versprechen, sondern konkret machen\\u201c, stellt sie sich gegen das unklare Wahlprogramm der Union. Und die widerspr\\u00fcchlichen Aussagen von CDU und CSU kommentiert Baerbock mit der Feststellung, dass das Land sich \\u201edieses Klimawirrwarr der Union nicht l\\u00e4nger leisten\\u201c k\\u00f6nne. Laschet hatte zuvor im ZDF-Sommerinterview erkl\\u00e4rt, beim Klimaschutz sei mehr Tempo erforderlich, aber keinerlei Ma\\u00dfnahmen genannt, wie die Union dieses Ziel erreichen will; die Forderung von CSU-Chef Markus S\\u00f6der, den Kohleausstieg auf 2030 vorzuziehen, lehnt er ab. \\u201eDie Zeit von Herumlavieren, Abwiegeln, Besch\\u00f6nigen, mit der muss jetzt Schluss sein\\u201c, forderte Baerbock.<\\/p><p class=\\\"meinz\\\">Doch nicht nur beim Klimaschutz, auch beim Katastrophenschutz wollen die Gr\\u00fcnen manches anders machen als die Union in der Vergangenheit. So forderten Baerbock und Mihalic etwa mehr Kompetenzen und Stellen f\\u00fcr das Bundesamt f\\u00fcr Bev\\u00f6lkerungsschutz und Katastrophenhilfe (BBK). Bisher ist das Amt vor allem im Verteidigungsfall zust\\u00e4ndig, w\\u00e4hrend die Zust\\u00e4ndigkeit f\\u00fcr den zivilen Katastrophenschutz bei den Bundesl\\u00e4ndern liegt. Zwar wollen die Gr\\u00fcnen die Kompetenzen nicht komplett auf den Bund verlagern, dem BKK jedoch zur Koordinierung eine st\\u00e4rkere \\u201eZentralstellenkompetenz\\u201c zuweisen. Zudem fordern sie eine schnelle Einf\\u00fchrung von Cell Broadcasting, also jenem System, mit dem Warnungen automatisiert an jedes Handy geschickt werden, das sich in einer bestimmten Funkzelle befindet.<\\/p><p class=\\\"meinz\\\">Zumindest mit Horst Seehofer sind die Gr\\u00fcnen in diesem letzten Punkt auf einer Linie. Der CSU-Politiker und Innenminister war am Montag in den Innenausschuss des Bundestags eingeladen. Die Gr\\u00fcnen hatten die Sondersitzung anl\\u00e4sslich der Flutkatastrophe zusammen mit der SPD beantragt.<\\/p><p class=\\\"meinz\\\">Er selbst habe sich bereits f\\u00fcr die Einf\\u00fchrung des Cell Broadcastings entschieden, sagte Seehofer vor Beginn der Sitzung. \\u201eIch glaube, man kann das noch in diesem Jahr hinbringen. Wenn man will, kann man Berge versetzen.\\u201c Die Flutkatastrophe hat das Tempo hier wohl erh\\u00f6ht: Eine Machbarkeitsstudie unter Hoheit des BBK l\\u00e4uft zwar schon seit einigen Monaten, sie war bislang aber ergebnisoffen angelegt. Im Unions-Wahlprogramm wird die Technologie zwar erw\\u00e4hnt, allerdings hei\\u00dft es dort, sie sei als \\u201eerg\\u00e4nzender Multiplikator im Warnmittelmix zu pr\\u00fcfen\\u201c.<\\/p><p class=\\\"meinz\\\">Gebremst, so Seehofer am Montag, h\\u00e4tten bislang aber andere, nicht er selbst und sein Ministerium. \\u201eVon der Idee sind nicht immer alle begeistert gewesen in den letzten Monaten\\u201c, sagte er. \\u201eIch wei\\u00df um die Bedenken in manchen Ministerien, ich wei\\u00df um die Kosten.\\u201c Welche Ressorts Einw\\u00e4nde haben oder hatten, verriet der Innenminister nicht.<\\/p><p class=\\\"meinz\\\">Vor zu vielen Bundeskompetenzen im Katastrophenschutz warnt Seehofer jedoch. St\\u00e4rkere Absprachen zwischen den verschiedenen Ebenen mit dem BKK als \\u201eKompetenzzentrum\\u201c im Mittelpunkt fordert er zwar auch und hat im Fr\\u00fchjahr eine entsprechende Reform auf den Weg gebracht. Die Zust\\u00e4ndigkeiten will er dabei aber nicht verlagern. \\u201eSeit Jahrzehnten sind die L\\u00e4nder zust\\u00e4ndig, und das halte ich f\\u00fcr richtig. Das ist genauso wichtig wie die f\\u00f6derale Struktur der Sicherheitsorgane\\u201c, sagte der Bundesinnenminister.<\\/p><p class=\\\"meinz\\\">Kritik an den momentanen Zust\\u00e4ndigkeiten war aufgekommen, weil die Bev\\u00f6lkerung zum Teil trotz entsprechender Vorhersagen des Deutschen Wetterdienstes und anderer Stellen nicht ausreichend vor dem <span class=\\\"snippet\\\">Hochwasser<\\/span> vor zwei Wochen gewarnt worden war. Zust\\u00e4ndig daf\\u00fcr sind die L\\u00e4nder beziehungsweise die Kommunen, weshalb die Warnungen von Ort zu Ort sehr unterschiedlich ausgefallen waren. Bei der Flutkatastrophe sind allein in Deutschland mindestens 170\\u00a0Menschen ums Leben gekommen.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a134\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.61.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a134\\/\\\"><h4 class=\\\"AutorName\\\">Malte Kreutzfeldt<\\/h4><h5 class=\\\"AutorJob\\\">Redakteur f\\u00fcr Wirtschaft und Umwelt<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Malte Kreutzfeldt &lt;mkr@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorTwitter\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/twitter.com\\/MKreutzfeldt\\\"><img class=\\\"icon\\\" src=\\\"resources\\/microblog_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Jahrgang 1971, ist Korrespondent f\\u00fcr Wirtschaft und Umwelt im Parlamentsb\\u00fcro der taz. Er hat in G\\u00f6ttingen und Berkeley Biologie, Politik und Englisch studiert, sich dabei umweltpolitisch und globalisierungskritisch engagiert und sp\\u00e4ter bei der Hessischen\\/Nieders\\u00e4chsischen Allgemeinen in Kassel volontiert.   \\u00a0 \\n\\nF\\u00fcr seine Aufdeckung der  Rechenfehler von Lungenarzt Dr. Dieter K\\u00f6hler  wurde er 2019 vom Medium Magazin als Journalist des Jahres in der Kategorie Wissenschaft ausgezeichnet. Zudem erhielt er 2019 den Umwelt-Medienpreis der DUH in der Kategorie Print. \\n \\nDerzeit besch\\u00e4ftigt er sich neben seinen sonstigen Themen intensiv mit der Entwicklung der Corona-Epidemie, auch auf seinem Twitter-Account @MKreutzfeldt .  <\\/div><\\/div><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a2557\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.153.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a2557\\/\\\"><h4 class=\\\"AutorName\\\">Tobias Schulze<\\/h4><h5 class=\\\"AutorJob\\\">Ressortleiter Inland<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Tobias Schulze &lt;tobias.schulze@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorTwitter\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/twitter.com\\/tschlze\\\"><img class=\\\"icon\\\" src=\\\"resources\\/microblog_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Geboren 1988, arbeitet seit 2013 f\\u00fcr die taz. Leitet das Ressort Inland und schreibt unter anderem \\u00fcber deutsche Au\\u00dfen- und Verteidigungspolitik. Hat davor Politikwissenschaft studiert und an der Deutschen Journalistenschule gelernt.  <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-27.555ee58674c7460be40bdee758f47067d917d223\",\"article\":{\"articleHtml\":{\"name\":\"art03604568.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627318954\",\"sha256\":\"cf8dd5a1a9ce122835fec49c063c4713f433a8a66f5a3fc29dbf78c3b98cbe74\",\"sSize\":\"10022\"},\"audioFile\":null,\"title\":\"Streit um Klima und Katastrophenschutz\",\"teaser\":\"Innenausschuss ber\\u00e4t \\u00fcber Konsequenzen aus der Flutkatastrophe. Baerbock stellt Sofortprogramm vor und kritisiert unklaren Klimakurs der Union\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5789127\",\"pageNames\":[\"s00810949.pdf\"],\"imageList\":null,\"authorList\":[{\"name\":\"Malte Kreutzfeldt\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.61.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993242\",\"sha256\":\"13f42e206f4f426371ed57b468701c627f94adc4fd939f6fd382179f3df62276\",\"sSize\":\"56941\"}},{\"name\":\"Tobias Schulze\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.153.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993514\",\"sha256\":\"4126b3a53aca8619f638bb205589650fe2d5b52df34c8d8675300bd33646e30a\",\"sSize\":\"75048\"}}]}},{\"sDate\":\"2021-07-30\",\"snippet\":\"... der Abend auch den \\u00d6kozid \\u2013 <span class=\\\"snippet\\\">Hochwasser<\\/span>, Hitzewellen, Fl\\u00e4chenbr\\u00e4nde? Wir denken das... mehr so wichtig, das Eifel-<span class=\\\"snippet\\\">Hochwasser<\\/span> kurzfristig noch textlich einzubauen. Wir...\",\"teaser\":\"Schuberts Musik aktuell deuten und mit der \\u201eFl\\u00fcchtlingskrise\\u201c verbinden: Das versucht das Ensemble \\u201eNico and the Navigators\\u201c in der Performance \\u201eSilent Songs\\u201c. Zu sehen ist sie zur Er\\u00f6ffnung der Sommerlichen Musiktage Hitzacker\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>\\u201eEin authentischer, stummer Schrei\\u201c<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_hamburg ArtikelTyp_AL2_2017 eptNorm\\\"><h2 class=\\\"Titel\\\">\\u201eEin authentischer,  \\nstummer Schrei\\u201c<\\/h2><h4 class=\\\"Unterzeile\\\">Schuberts Musik aktuell deuten und mit der \\u201eFl\\u00fcchtlingskrise\\u201c verbinden: Das  \\nversucht das Ensemble \\u201eNico and the Navigators\\u201c in der Performance \\u201eSilent Songs\\u201c.  \\nZu sehen ist sie zur Er\\u00f6ffnung der Sommerlichen Musiktage Hitzacker<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptNorm\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=545.669 BH=387.23&#10;IB=1969 IH=1310&#10;S=4.1 b=660 h= C=Main&#10;id=1097128 typ=JPEG&#10;\\\" src=\\\"Media.1097128.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097128.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Puristisch  \\nund intim:  \\nYui Kawaguchi und Ted Schmitz in \\u201eSilent Songs\\u201c   <span class=\\\"zAu\\\">Foto:  \\nDieter Hartwig <\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_hamburg ArtikelTyp_AL2_2017 eptNorm\\\"><p class=\\\"Korrespondent\\\">Interview <span class=\\\"zAu\\\">Petra Schellen<\\/span><\\/p><p class=\\\"oeinz\\\"><strong>taz: Frau H\\u00fcmpel, warum eignet sich Schubert f\\u00fcr Ihr \\u201eStaged Concert\\u201c \\u2013 eine Performance zur \\u201eFl\\u00fcchtlingskrise\\u201c?<\\/strong><\\/p><p class=\\\"meinz\\\"><strong>Nicola H\\u00fcmpel:<\\/strong> Ganz so tagesaktuell ist es nicht gemeint \\u2013 und wird er nicht gedacht! Schubert ist zeitlos! Ich bin in meinem Leben immer wieder auf ihn gesto\\u00dfen, er spielt in fast jeder Produktion eine Rolle. Jetzt, f\\u00fcr die Wiederaufnahme des St\\u00fccks \\u201eSilent Songs\\u201c, die erstmals mit dem von Oliver Wille geleiteten Kuss-Quartett in Hitzacker gespielt wird, ist mir neu bewusst geworden, wie aktuell die von Schubert vertonten Texte von Wilhelm M\\u00fcller, Heine und Goethe sind. Deshalb haben wir \\u2013 das Team der Navigators \\u2013 beschlossen, die Texte ins Zeitgef\\u00fchl von heute zu holen und assoziativ mit aktuellen Themen zu verbinden.<\\/p><p class=\\\"meinz\\\"><strong>Welche Texte w\\u00e4ren das zum Beispiel?<\\/strong><\\/p><p class=\\\"meinz\\\">Im \\u201eWanderer\\u201c etwa hei\\u00dft es: \\u201eWo bist du, mein geliebtes Land \\u2013 gesucht, geahnt, ungekannt, das Land, das Land so hoffnungsgr\\u00fcn, das Land, wo meine Rosen bl\\u00fchn?\\u201c Das ist hoch aktuell. Wir wollten aber auf keinen Fall eine illustrative Fl\\u00fcchtlingsgeschichte zeigen. Deshalb haben wir die Inszenierung ganz puristisch gehalten. Die AkteurInnen tragen allt\\u00e4gliche, fast monochrome Kost\\u00fcme und versetzen sich in eine innere Welt, die sehr schlicht und assoziativ ist.<\\/p><p class=\\\"meinz\\\"><strong>Wie begegnen die S\\u00e4ngerInnen und T\\u00e4nzerInnen Schuberts Liedern?<\\/strong><\\/p><p class=\\\"meinz\\\">Sie versuchen, die Texte fast biografisch zu singen. Das ist sehr facettenreich, denn die AkteurInnen kommen aus sieben Nationen, haben fast alle signifikante Erfahrungen mit Abschieden und flechten kurze biografische Texte ein. Manchmal gibt es kleine Ausfl\\u00fcge: Was passiert, wenn man sich heute ganz auf Schubert einl\\u00e4sst? Dann kann es passieren, dass man in Lachen ausbricht, ein Weinen, einen Schrei oder Tanz oder eine kurze eigene Interpretation passieren.<\\/p><p class=\\\"meinz\\\"><strong>Verfremden Sie die Lieder?<\\/strong><\\/p><p class=\\\"meinz\\\">Nein. Wir arbeiten immer zun\\u00e4chst mit dem Original, singen die Lieder meist eins zu eins, aber en passant entstehen zeitgen\\u00f6ssische Bilder und Beziehungen zwischen den AkteurInnen. Oder die Begleitung einer E-Gitarre l\\u00e4sst das Ganze auf einmal sehr zeitgen\\u00f6ssisch klingen. Wir nehmen auch nicht alles extrem ernst. \\u201eIm Fr\\u00fchling\\u201c zum Beispiel wird fast persifliert. \\u201eWanderer\\u201c oder \\u201eMeeresstille\\u201c dagegen vertragen solche Zwischent\\u00f6ne nicht. Aus den Zyklen \\u201eDie sch\\u00f6ne M\\u00fcllerin\\u201c, \\u201eWinterreise\\u201c und \\u201eSchwanengesang\\u201c haben wir Lieder gew\\u00e4hlt, in denen Motive wie \\u201eWandern\\u201c, \\u201eAbschied\\u201c, \\u201eHeimatsuche\\u201c, \\u201eFremdsein\\u201c, \\u201eEinsamkeit\\u201c zu finden sind. Diese Themen geben sowohl den roten Faden Schuberts als auch den f\\u00fcr unser Staged Concert vor. Sie konfrontieren uns auch mit der anhaltenden Fl\\u00fcchtlingssituation.<\\/p><p class=\\\"meinz\\\"><strong>Es geht Ihnen weniger um Schuberts mutma\\u00dfliche Ideen als um das, was er ausl\\u00f6st?<\\/strong><\\/p><p class=\\\"meinz\\\">Ja, genau! Wobei ich glaube, dass unsere Gedanken und Gef\\u00fchle angesichts seiner Musik den damaligen Inspirationen durchaus \\u00e4hneln k\\u00f6nnen \\u2013 aus einer anderen, heutigen Sicht. Das ist ja gerade das Spannende an der Arbeit mit Schuberts Musik: dass sich Lebenserfahrung und Weltwahrnehmung nicht wirklich ver\\u00e4ndert haben.<\\/p><p class=\\\"meinz\\\"><strong>Thematisiert der Abend auch den \\u00d6kozid \\u2013 <span class=\\\"snippet\\\">Hochwasser<\\/span>, Hitzewellen, Fl\\u00e4chenbr\\u00e4nde?<\\/strong><\\/p><p class=\\\"meinz\\\">Wir denken das auf jeden Fall mit, denn auch hier geht es um Heimatverlust und Flucht: Wo gehen wir hin, wenn wir nicht mehr da sein k\\u00f6nnen, wo wir wollen? Da ist es gar nicht mehr so wichtig, das Eifel-<span class=\\\"snippet\\\">Hochwasser<\\/span> kurzfristig noch textlich einzubauen. Wir haben den Abend eher assoziativ gebaut, und es gen\\u00fcgt, das Bewusstsein mit in die Szene zu nehmen. Wir arbeiten ja generell viel mit Subtext und weniger mit konkreten Bildern.<\\/p><p class=\\\"meinz\\\"><strong>Ein Beispiel?<\\/strong><\\/p><p class=\\\"meinz\\\">W\\u00e4hrend des Liedes \\u201eMeeresstille\\u201c sehen wir eine kleine Gruppe, die still und eng nebeneinander an einem Ort steht \\u2013 wie auf einem Flo\\u00df oder Boot. Dazu h\\u00f6rt man sehr leise, fast klirrend stille Kl\\u00e4nge, die f\\u00fcr die Meeresstille stehen. Dazu sieht man \\u2013 durch die Kameratechnik, die das B\\u00fchnengeschehen live \\u00fcbertr\\u00e4gt, stark vergr\\u00f6\\u00dfert \\u2013 die Gesichter. Wir blicken sehr intim in ihren Ausdruck, sehen jede Tr\\u00e4ne, jedes Augenbrauenzucken. Wir sehen, wie die Blicke in die Ferne gehen und vergebens nach Land suchen. Ein Mensch f\\u00e4ngt wirklich an zu weinen, weil er nur das Nichts sieht. Die Gesichter stehen quasi allein, ohne Worte, aber die Emotionen sind da. Und sie sind authentisch.<\\/p><p class=\\\"meinz\\\"><strong>Wie zeigt sich das??<\\/strong><\\/p><p class=\\\"meinz\\\">Die T\\u00e4nzerin Yui Kawaguchi<strong>,<\\/strong> die einen stummen Todesschrei ausst\\u00f6\\u00dft, kommt aus Tokio, hat den Tsunami und das Reaktorungl\\u00fcck von Fukushima erlebt. Der israelische T\\u00e4nzer Michael Shapira zitiert ein hebr\\u00e4isches Gedicht. Und die neu hinzugekommene syrische T\\u00e4nzerin Lujain Mustafa, die 2015 mit dem Schlauchboot \\u00fcber sieben L\\u00e4nder hierher floh, erz\\u00e4hlt, dass sie seither ihre Eltern nicht mehr sah und dankbar ist f\\u00fcr das Internet, durch das sie mit ihnen kommunizieren kann.<\\/p><p class=\\\"meinz\\\"><strong>Und welche Rolle spielen die Kameras?<\\/strong><\\/p><p class=\\\"meinz\\\">Sie zu nutzen, war ein ganz bewusster Schritt. Jahrzehntelang hatte ich mich gegen jegliche Videotechnik gewehrt, weil ich das effekthascherisch fand. Dann habe ich in meiner Inszenierung von Philippe Boesmans \\u201eReigen\\u201c an der Stuttgarter Oper erstmals mit dieser Technik arbeiten d\\u00fcrfen. Das hat mich begeistert, weil die Operns\\u00e4ngerInnen, die sonst immer so klein und zappelig mit \\u00fcberbordenden Gesten singen m\\u00fcssen, jetzt nat\\u00fcrlich agieren konnten, wahnsinnig fein und intim. Das war vor sechs Jahren, und seither habe ich diese Technik immer weiter perfektioniert. Inzwischen arbeiten wir vom ersten Probentag an mit diesem Medium. Wir zeichnen nichts auf, sondern arbeiten direkt in die Kamera hinein.<\\/p><p class=\\\"meinz\\\"><strong>Was bedeutet das f\\u00fcr die AkteurInnen?<\\/strong><\\/p><p class=\\\"meinz\\\">Zum Beispiel, dass sie sich in bestimmten Momenten nicht gro\\u00df bewegen d\\u00fcrfen. Sie m\\u00fcssen sich sehr ruhig verhalten, weil den ZuschauerInnen schlecht wird, wenn das Bild permanent wackelt und der Rhythmus der Musik zerst\\u00f6rt wird. Andererseits k\\u00f6nnen die AkteurInnen eine unglaubliche Intimit\\u00e4t und einen Sog entwickeln, denn das Publikum guckt direkt in die Augen, in die Seele der S\\u00e4ngerInnen und T\\u00e4nzerInnen. Daf\\u00fcr m\\u00fcssen sich die K\\u00fcnstlerInnen daran gew\\u00f6hnen, nicht in den Zuschauerraum zu gucken, sondern in die Kamera. Irgendwann setzt der Moment ein, in dem das richtig funktioniert und die S\\u00e4ngerInnen die Energie des Publikums sp\\u00fcren. Die Stille im Raum. Und man wird fast s\\u00fcchtig danach, dem Publikum \\u00fcber dieses Medium nahe zu kommen.<\\/p><p class=\\\"meinz\\\"><strong>Sieht das Publikum ausschlie\\u00dflich den Live-Film?<\\/strong><\\/p><p class=\\\"meinz\\\">Nein, es sieht auch die B\\u00fchne. Man kann sich entscheiden, den Live-Spieler anzugucken, aber man hat eben auch die \\u201eLupe\\u201c \\u2013 ein modernes Opernglas. Zugleich \\u2013 und das gef\\u00e4llt mir besonders \\u2013 demokratisiert die Kamera die Position im Zuschauerraum. Es gibt keine billigen Pl\\u00e4tze mehr, auf denen man weniger sieht, sondern man erlebt im ganzen Saal dasselbe.<\\/p><p class=\\\"Hinweis\\\">Er\\u00f6ffnung der bis 8.\\u20058. laufenden <strong>Sommerlichen Musiktage Hitzacker<\\/strong> mit \\u201eNico and the Navigators\\u201c: Sa, 31.\\u20057, 15.03 Uhr, Verdo-Konzertsaal;  \\nProgramm und Karten: https:\\/\\/www.musiktage-hitzacker.de<\\/p><div class=\\\"Shorty BeitragsArt_taz_print_biokasten ArtikelTyp_Bio_2017 eptShorty\\\"><img alt=\\\"BB=56.693 BH=73.701&#10;IB=192 IH=250&#10;S=0.5 b= h=250 C=Bio&#10;id=1097127 typ=JPEG&#10;\\\" src=\\\"Media.1097127.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097127.norm.jpg\')\\\" class=\\\"MediaImg ImgBio ImgSize192\\\"\\/><p class=\\\"MediaButze BuBio ImgSize192\\\"><span class=\\\"zAu\\\">Foto: Oliver Proske <\\/span><\\/p><div class=\\\"BioText\\\"><p class=\\\"ShortyTitel\\\">Nicola H\\u00fcmpel<\\/p><p class=\\\"oeinz\\\">54, hat an der Hochschule f\\u00fcr bildende K\\u00fcnste in Hamburg studiert und ist k\\u00fcnstlerische Leiterin des 1998 gemeinsam mit Oliver Proske gegr\\u00fcndeten Ensembles \\u201eNico and the Navigators\\u201c, das mit internationalen Orchestern und B\\u00fchnen kooperiert. <\\/p><\\/div><div class=\\\"clear\\\"> <\\/div><\\/div><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a206\\/\\\"\\/><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a206\\/\\\"><h4 class=\\\"AutorName\\\">Petra Schellen<\\/h4><h5 class=\\\"AutorJob\\\">Hamburg-Redakteurin<\\/h5><\\/a><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Seit 2000 Redakteurin der taz am Standort Hamburg. Schwerpunkte: Kultur und -politik, Drittes Reich, Judentum, Religion allgemein. <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-30.b061da1dfd13fcad5f8b4bbf8e0ef747c4e5a7b5\",\"article\":{\"articleHtml\":{\"name\":\"art03605454.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627578817\",\"sha256\":\"2217ef5415486a1130045f9a72ea1c0a2f4ab0c7c4504f0ce3e61e59151c5161\",\"sSize\":\"11927\"},\"audioFile\":null,\"title\":\"\\u201eEin authentischer, stummer Schrei\\u201c\",\"teaser\":\"Schuberts Musik aktuell deuten und mit der \\u201eFl\\u00fcchtlingskrise\\u201c verbinden: Das versucht das Ensemble \\u201eNico and the Navigators\\u201c in der Performance \\u201eSilent Songs\\u201c. Zu sehen ist sie zur Er\\u00f6ffnung der Sommerlichen Musiktage Hitzacker\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5790055\",\"pageNames\":[\"s00811183.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097128.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627578816\",\"sha256\":\"e2c9e2db3b2d208d992979b24b746982aa572f6e5e1ec0fcaf9c380fc942a756\",\"sSize\":\"16748\"},{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097127.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627578817\",\"sha256\":\"152801a9dd6b4816c02603026495abcd68c88efb85710a5bc506b7c9714c35af\",\"sSize\":\"8526\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097128.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627578817\",\"sha256\":\"83eaa13b907f78a54bf140e3831388ab668a019f97065abf30bea7db30cb915b\",\"sSize\":\"115303\"}],\"authorList\":null}},{\"sDate\":\"2021-07-27\",\"snippet\":\"... vergangenen Tagen in den vom <span class=\\\"snippet\\\">Hochwasser<\\/span> betroffenen Regionen unterwegs war, der...\",\"teaser\":\"Daniel B\\u00f6ldt \\u00fcber die zivile Hilfe nach der Flut\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Hilfe annehmen<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AKOM2_2017 eptKommentar\\\"><h4 class=\\\"Dach\\\"><span class=\\\"zAu\\\">Daniel B\\u00f6ldt <\\/span>\\u00fcber die zivile Hilfe nach der Flut<\\/h4><h2 class=\\\"Titel\\\">Hilfe annehmen<\\/h2><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AKOM2_2017 eptKommentar\\\"><p class=\\\"Initial\\\">Wer in den vergangenen Tagen in den vom <span class=\\\"snippet\\\">Hochwasser<\\/span> betroffenen Regionen unterwegs war, der sah neben jeder Menge Leid und Zerst\\u00f6rung vor allem eines: Solidarit\\u00e4t. Familie, Freund*innen, Nachbar*innen, teils vollkommen ortsfremde Menschen helfen dabei, den M\\u00fcll und Schutt aus den zerst\\u00f6rten H\\u00e4usern zu holen. Sie bieten Schlafpl\\u00e4tze f\\u00fcr jene an, die die Flut obdachlos gemacht hat, spenden Pumpen und Generatoren, verteilen Essen und Wasser. Viele opfern daf\\u00fcr ihren Urlaub. Die Polizei bittet inzwischen Helfer:innen, nicht mehr mit dem privaten Pkw anzureisen. Es k\\u00e4men so viele, dass sie die Stra\\u00dfen f\\u00fcr Feuerwehr und Technisches Hilfswerk verstopften.<\\/p><p class=\\\"meinz\\\">Die aktuelle Hilfsbereitschaft ist bewundernswert, mitunter r\\u00fchrend \\u2013 \\u00fcberraschend ist sie jedoch nicht. Viele Menschen helfen \\u2013 entgegen dem weitverbreiteten Mythos \\u2013 gerne anderen. Zwar stimmt es, dass das Ma\\u00df der Hilfsbereitschaft proportional zum Schrecken einer Katastrophe steigt. Doch auch f\\u00fcr Nichtkrisenzeiten gilt, dass Solidarit\\u00e4t m\\u00f6glich ist \\u2013 wenn man denn danach fragt.<\\/p><p class=\\\"meinz\\\">Das Besondere einer Naturkatastrophe ist eben nicht, dass Menschen auf einmal ihr Herz entdecken, sondern dass Menschen sich trauen, Hilfe auch anzunehmen. Dass sie sich nicht scheuen oder sch\\u00e4men zu sagen: Ich schaffe es nicht allein. In der Katastrophe ist diese Einsicht leicht. Das sollte sie auch in anderen Zeiten sein.<\\/p><p class=\\\"meinz\\\">All das nimmt den Staat keineswegs aus der Verantwortung. Man d\\u00fcrfe die Ressource Solidarit\\u00e4t nicht \\u00fcberstrapazieren, sagte der Soziologe Ulf Tranow k\\u00fcrzlich im Deutschlandfunk. Auch das stimmt. Das derzeitige Ma\\u00df an Hilfsbereitschaft ist allein schon deshalb begrenzt, weil es die Menschen tats\\u00e4chlich an ihre Grenzen bringt. Ein kapitalistisch organisierter Staat kann sich strukturell gar nicht auf die st\\u00e4ndige gegenseitige Hilfsbereitschaft verlassen, weil die meisten Menschen daf\\u00fcr schlicht keine Zeit haben. Dass es vielen im Krisenfall dennoch gelingt, Solidarit\\u00e4t zu zeigen, ist ein Gl\\u00fccksfall, eine Garantie f\\u00fcr die Zukunft ist es nicht.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a37452\\/\\\"\\/><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a37452\\/\\\"><h4 class=\\\"AutorName\\\">Daniel B\\u00f6ldt<\\/h4><\\/a><\\/div><\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-27.555ee58674c7460be40bdee758f47067d917d223\",\"article\":{\"articleHtml\":{\"name\":\"art03604542.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627318977\",\"sha256\":\"243d50fa2b21296038eae63bf2d91be064288991916b1beb2f28d0be8e226a4b\",\"sSize\":\"4555\"},\"audioFile\":null,\"title\":\"Hilfe annehmen\",\"teaser\":\"Daniel B\\u00f6ldt \\u00fcber die zivile Hilfe nach der Flut\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5785765\",\"pageNames\":[\"s00810941.pdf\"],\"imageList\":null,\"authorList\":null}},{\"sDate\":\"2021-07-31\",\"snippet\":\"..., wie an Ahr oder Oder, <span class=\\\"snippet\\\">Hochwasser<\\/span>. Aber auch das Gegenteil von...\",\"teaser\":\"Die Lausitz verk\\u00f6rpert alles, was wir eigentlich nicht m\\u00f6gen. Arbeit von gestern, Kohle, geschundene Landschaft. Und jetzt soll sie noch mit Milliarden gepampert werden? Zeit f\\u00fcr eine Liebeserkl\\u00e4rung\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Die W\\u00fcste lebt<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_berlin ArtikelTyp_AWE1_2017 eptGrossWochenend\\\"><h2 class=\\\"Titel\\\">Die W\\u00fcste lebt<\\/h2><h4 class=\\\"Unterzeile\\\">Die Lausitz verk\\u00f6rpert alles, was wir eigentlich nicht m\\u00f6gen. Arbeit von gestern, Kohle, geschundene Landschaft. Und jetzt soll sie noch mit Milliarden gepampert werden? Zeit f\\u00fcr eine Liebeserkl\\u00e4rung<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptGrossWochenend\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=745.228 BH=580.098&#10;IB=2738 IH=2145&#10;S=5.6 b=660 h= C=Main&#10;id=1097247 typ=JPEG&#10;\\\" src=\\\"Media.1097247.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097247.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Drei junge Fliesenleger  \\nan einem Aussichtspunkt am Tagebau J\\u00e4nschwalde bei Briesnig, Niedersorbisch Rjasnik, im brandenburgischen Landkreis Spree-Nei\\u00dfe, aufgenommen 1997    \\n<span class=\\\"zAu\\\">Foto:  \\nLorenz Kienzle<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_berlin ArtikelTyp_AWE1_2017 eptGrossWochenend\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Uwe Rada<\\/span> <\\/p><p class=\\\"Initial\\\">Nein, mit der Gifte speienden Kohle hat es nichts zu tun, dass die Lausitz bis heute diese Abwehrreflexe ausl\\u00f6st. Das hat sie schon vor 250\\u00a0Jahren getan. 1789 setzte Christian Gottlieb Schmidt, ein Pastor aus der N\\u00e4he von Dresden, in seinen \\u201eBriefen \\u00fcber die Niederlausitz\\u201c die Lausitz gleich mit \\u201etiefen Sandmeeren\\u201c, \\u201eunfruchtbaren Steppen\\u201c und unkultiviertem Sorbenland. \\u00dcber Lieberose etwa schrieb der erste Lausitzhasser: \\u201eJetzt f\\u00fchre ich Sie, mein Freund, in den unkultivirtesten, sandigsten und unangenehmsten Krei\\u00df der Niederlausitz, und ich rathe Ihnen, sich ja nicht weit von der Stadt zu entfernen und Excursionen aufs Land zu machen, wenn Sie es nicht bei jedem Schritt f\\u00fchlen wollen, da\\u00df Sie in der Gegend sind, die Ihnen so oft als die Wendische Tartarei vorgestellet wurdet.\\u201c<\\/p><p class=\\\"meinz\\\">Wendische Tartarei, das blieb h\\u00e4ngen. Feindliches Slawenland also. Da passte es ganz gut ins Bild, dass sich das erste Brandenburger Wolfsrudel 2009 in Welzow ansiedelte, auf dem Gel\\u00e4nde eines bis heute existierenden Tagebaus. Nicht nur unzug\\u00e4nglich und fremd war die Lausitz, sondern auch gef\\u00e4hrlich. Und jetzt sollen in diese W\\u00fcste Milliarden gepumpt werden, um den Menschen den Ausstieg aus der Braunkohle schmackhaft zu machen? Sind das nicht Perlen vor die W\\u00f6lfe?<\\/p><p class=\\\"meinz\\\">Dass eine W\\u00fcste auch leben kann, war schon zu Schmidts Zeiten kein Geheimnis. Ganz gleich, ob das Markgraftum Niederlausitz zu B\\u00f6hmen geh\\u00f6rte, zu Habsburg oder zu Sachsen: Die Adligen in L\\u00fcbben oder Lieberose lie\\u00dfen sich von den Herrschern in Prag, Wien und Dresden nicht in die Karten schauen. So kam es, dass die Lausitz nicht nur als abgeh\\u00e4ngte, sondern auch als autonome Region in die Geschichtsb\\u00fccher einging. Eine Region am Weltenrand, an der sich die Zentralgewalten ihre Z\\u00e4hne ausbissen.<\\/p><p class=\\\"meinz\\\">Eine \\u201eAdelsrepublik\\u201c nannte das der Historiker des Klosters Neuzelle, Winfried T\\u00f6pler, in Anlehnung an die polnische Adelsrepublik. Die war zwar unregierbar, aber auch demokratisch. Bis zur \\u00dcbernahme durch Preu\\u00dfen 1815 ging das so. Heute w\\u00fcrde man sagen: Die Lausitz war ein gallisches Dorf, lange bevor Kreuzberg diesen Titel f\\u00fcr sich in Anspruch nehmen durfte.<\\/p><p class=\\\"meinz\\\">Und warm ist es in der W\\u00fcste. Sonst w\\u00fcrden all die Tr\\u00fcffel, Weinreben und Hanfpflanzen auf dem Sandboden nicht gedeihen. Selbst der Pfeffer w\\u00e4chst in der Lausitz. Allerdings erst seit Neuestem, sonst h\\u00e4tte der Pfarrer Schmidt sicher ein weiteres Bonmot parat gehabt. <\\/p><p class=\\\"meinz\\\">Aber warum soll man nicht dorthin gehen, wo der Pfeffer w\\u00e4chst? Wenn im M\\u00e4rz in Berlin gerade mal 12 Grad vorhergesagt sind, k\\u00f6nnen es in der Lausitz gut und gerne 16 Grad sein. Im Sommer freilich ist das nicht immer angenehm. Deshalb f\\u00fcrchten viele, dass sich zur alten W\\u00fcste bald eine neue gesellen k\\u00f6nnte.<\\/p><p class=\\\"meinz\\\">Vielleicht will das Land Brandenburg auch deshalb aus der gr\\u00f6\\u00dften Bergbaulandschaft der Republik die gr\\u00f6\\u00dfte zusammenh\\u00e4ngende Seenlandschaft der Welt machen. Vorausgesetzt, die Spree speist all die neuen Seengespinste. Am Einlassbauwerk zum Cottbuser Ostsee, dem ehemaligen Tagebau Cottbus-Nord, rinnsalt es derzeit nur, obwohl der Sommer 2021 bei Weitem nicht so trocken ist wie seine beiden Vorg\\u00e4nger.<\\/p><p class=\\\"meinz\\\">Der Lausitz kann es egal sein. Wer es mit Wien und Dresden aufnehmen konnte, h\\u00e4lt sich auch Potsdam und Berlin vom Leibe. Zur Not dreht die Lausitz der Hauptstadtregion einfach das Wasser ab. Denn die Spree muss, bevor sie Berlin erreicht, halbwegs unbeschadet durch die ehemaligen Tagebaulandschaften, bevor sie sich im Spreewald zu einem Binnendelta mit unz\\u00e4hligen Flie\\u00dfen auff\\u00e4chert.<\\/p><p class=\\\"meinz\\\">Dort drohen zwar nicht, wie an Ahr oder Oder, <span class=\\\"snippet\\\">Hochwasser<\\/span>. Aber auch das Gegenteil von viel Wasser, n\\u00e4mlich fast keines, ist nicht sch\\u00f6n. Es kann dazu f\\u00fchren, dass das Wasser in der Berliner Spree r\\u00fcckw\\u00e4rts flie\\u00dft. Und auch ein Flussbad funktioniert am Kupfergraben nur, wenn eine Mindestmenge Wasser durchflie\\u00dft. Berlin braucht also die Lausitz, und die Lausitz braucht die Berlinerinnen und Berliner. Als Investoren, als Zuz\\u00fcgler, als Touris. <\\/p><p class=\\\"meinz\\\">Die W\\u00fcste lebt? Oft ist das noch Zukunftsmusik. Aber selbst das ist im Labor Lausitz ein Ereignis. In der Lieberoser Heide soll man dem Werden dessen, was nach der W\\u00fcste kommt, sogar zuschauen k\\u00f6nnen, vorausgesetzt, man bringt ein wenig Zeit mit. Denn die dortige W\\u00fcste, entstanden auf einem der gr\\u00f6\\u00dften Truppen\\u00fcbungspl\\u00e4tze der DDR, soll sich selbst \\u00fcberlassen werden. \\u201eInternationale Naturausstellung\\u201c hei\\u00dft das Projekt, das mit den \\u201eUrw\\u00e4ldern von morgen\\u201c f\\u00fcr sich wirbt.<\\/p><p class=\\\"meinz\\\">Vielleicht werden die Reisenden von morgen aus der Lausitz ganz andere Bilder nach Dresden oder Berlin bringen. Nicht von \\u201eunfruchtbaren Steppen\\u201c wird dann die Rede sein, sondern von einem Labor der Zukunft, in dem die Braunkohle zun\\u00e4chst den Klimawandel befeuerte, dann aber, nach dem Kohleausstieg, nach neuen, nach positiven Energien gesucht wurde.<\\/p><p class=\\\"meinz\\\">Und wenn der Pastor Christian Gottlieb Schmidt demn\\u00e4chst nach Cottbus k\\u00e4me, w\\u00fcrde er vielleicht sogar fantasieren, dass es in der W\\u00fcste eine prosperierende W\\u00fcstenstadt g\\u00e4be, die Lausitzer Ausgabe von Dubai. Nur, dass dort nicht mehr nach fossilen Brennstoffen gesucht wird, sondern nach denen der Zukunft.<\\/p><p class=\\\"Hinweis\\\">Der Strukturwandel  \\nin der Lausitz und Berlin<\\/p><p class=\\\"Hinweis\\\">44\\u201345<\\/p><div class=\\\"Shorty BeitragsArt_taz_print_textbox ArtikelTyp_Shorty_2017 eptShorty\\\"><h2 class=\\\"Titel\\\">Der Fotograf und die Lausitz<\\/h2><p class=\\\"oeinz\\\"><strong>Die Fotos zu unserem Schwerpunkt<\\/strong> stammen vom Fotografen Lorenz Kienzle, Jahrgang 1967, der seit 1992 immer wieder in der Lausitz fotografiert. Kienzle arbeitete nach der Ausbildung in Rom und Berlin f\\u00fcr den Museumsverband Brandenburg und f\\u00fcr den US-amerikanischen Bildhauer Richard Serra, bevor er 2013 die Agentur f\\u00fcr Museumsfotografie kienzle|oberhammer GbR gr\\u00fcndete. Neben Projekten \\u00fcber das Werk der Schriftsteller Theodor Fontane und Alfred D\\u00f6blin, arbeitet Kienzle inzwischen seit 2018 auch kuratorisch mit fotografischen Nachl\\u00e4ssen.<\\/p><p class=\\\"oeinz\\\"><strong>Aktuelle Ausstellung<\\/strong> Bilder von Lorenz Kienzle sind in <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.kulturland-brandenburg.de\\/event\\/brandenburger-industrielandschaften-1992-2021-fotografien-von-lorenz-kienzle-2\\/\')\\\">\\u201eBrandenburger Industrielandschaften 1992\\u20132021\\u201c<\\/a>, einem Ausstellungs- und Buchprojekt im Rahmen des Themenjahres Kulturland Brandenburg 2021 \\u201eZukunft der Vergangenheit \\u2013 Industriekultur in Bewegung\\u201c in der <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.museum-pritzwalk.de\\/\')\\\">Museumsfabrik Pritzwalk<\\/a> bis 2. Januar zu sehen. Zur Ausstellung erscheint im Oktober das Buch <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.verlagberlinbrandenburg.de\\/buecher\\/regionales\\/arbeitswelten-und-lebensraeume.html\')\\\">\\u201eArbeitswelten und Lebensr\\u00e4ume\\u201c<\\/a> im <a href=\\\"javascript:EPUBTAZ.internLink(\'http:\\/\\/www.verlagberlinbrandenburg.de\\/\')\\\">Verlag f\\u00fcr Berlin Brandenburg<\\/a>, dem wir das Foto auf dieser Seite entnommen haben. <i>(taz)<\\/i><\\/p><div class=\\\"clear\\\"> <\\/div><\\/div><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a20\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.8.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a20\\/\\\"><h4 class=\\\"AutorName\\\">Uwe Rada<\\/h4><h5 class=\\\"AutorJob\\\">Redakteur taz.Berlin<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Uwe Rada &lt;rada@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorWebsite\\\" target=\\\"_blank\\\" href=\\\"http:\\/\\/www.uwe-rada.de\\\"><img class=\\\"icon\\\" src=\\\"resources\\/website_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Jahrgang 1963, ist Redakteur f\\u00fcr Stadtentwicklung der taz. Ein weiterer Schwerpunkt ist Osteuropa. Zuletzt erschien bei Siedler sein Buch \\u201eDie Elbe. Europas Geschichte im Fluss\\u201c. Er koordiniert auch das Onlinedossier \\u201eGeschichte im Fluss\\u201c der Bundeszentrale f\\u00fcr politische Bildung. Uwe Rada lebt in Berlin-Pankow. <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-31.017151ecab637f8bedc37bf9fa303c84563e86b7\",\"article\":{\"articleHtml\":{\"name\":\"art03605645.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627663520\",\"sha256\":\"7af5a092de65ada40d4c1210024eb8dd2b7613c7b977c44e87d10d8aef7e6ae9\",\"sSize\":\"11295\"},\"audioFile\":null,\"title\":\"Die W\\u00fcste lebt\",\"teaser\":\"Die Lausitz verk\\u00f6rpert alles, was wir eigentlich nicht m\\u00f6gen. Arbeit von gestern, Kohle, geschundene Landschaft. Und jetzt soll sie noch mit Milliarden gepampert werden? Zeit f\\u00fcr eine Liebeserkl\\u00e4rung\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5787769\",\"pageNames\":[\"s00811236.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097247.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663519\",\"sha256\":\"c2816f9664fb2d44e67f0ccb3602f16e8cea70d91df1edf2a0e592e32a7aa02d\",\"sSize\":\"71076\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097247.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663520\",\"sha256\":\"37a08f058cda1928cf1455a45f29c54cf8df094616cbf789de422a4909cf25b2\",\"sSize\":\"445109\"}],\"authorList\":[{\"name\":\"Uwe Rada\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.8.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993146\",\"sha256\":\"1f5fb9e448dcf23a39adbad84c7c3ba4850cec27aa66916fb24a486e7195ebcd\",\"sSize\":\"74535\"}}]}},{\"sDate\":\"2021-07-31\",\"snippet\":\"... Partei auf ihn h\\u00f6rt. Notgedrungen: <span class=\\\"snippet\\\">Hochwasser<\\/span> und D\\u00fcrresommer schockieren auch CDU...\",\"teaser\":\"Nach der Wahl muss die CDU\\/CSU im Bundestag beim Klimaschutz ernst machen. Mit Andreas Jung hat sie daf\\u00fcr sogar den richtigen Mann. Aber ist er wom\\u00f6glich zu nett f\\u00fcr den Job?\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Ritter ohne R\\u00fcstung<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AWE1_2017 eptGrossWochenend\\\"><h2 class=\\\"Titel\\\">Ritter  \\nohne R\\u00fcstung<\\/h2><h4 class=\\\"Unterzeile\\\">Nach der Wahl muss die CDU\\/CSU im Bundestag beim Klimaschutz ernst machen. Mit Andreas Jung hat sie daf\\u00fcr sogar den richtigen Mann. Aber ist er wom\\u00f6glich  \\nzu nett f\\u00fcr den Job?<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptGrossWochenend\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=952.441 BH=639.921&#10;IB=3228 IH=2152&#10;S=7.1 b=660 h= C=Main&#10;id=1097270 typ=JPEG&#10;\\\" src=\\\"Media.1097270.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097270.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Andreas Jung im Kreise von CDU-Parteimitgliedern im Solarpark Engen,  \\ndirekt an der Autobahn Stuttgart\\u2013 \\nSingen<\\/p><\\/div><div class=\\\"EinzelFoto\\\"><img alt=\\\"BB=243.78 BH=364.772&#10;IB=884 IH=1326&#10;S=1.9 b=660 h= C=Multi&#10;id=1097271 typ=JPEG&#10;\\\" src=\\\"Media.1097271.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097271.norm.jpg\')\\\" class=\\\"MediaImg ImgMulti ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMulti ImgSizeFull\\\">MdB Andreas Jung, oder auch: \\u201eder Andi\\u201c. Er ist ein Mann des Ausgleichs, alle m\\u00f6gen ihn, alle brauchen ihn. Aber ist er auch durchsetzungsf\\u00e4hig?<\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AWE1_2017 eptGrossWochenend\\\"><p class=\\\"Korrespondent\\\">Vom Bodensee <span class=\\\"zAu\\\">Bernhard P\\u00f6tter<\\/span> (Text) und <span class=\\\"zAu\\\">Patrick Pfeiffer<\\/span> (Fotos)<\\/p><p class=\\\"Initial\\\">Die Kulisse wie in einem Heile-Welt-Film: Von der Hochwart, dem h\\u00f6chsten H\\u00fcgel auf der Insel Reichenau, schweift der Blick \\u00fcber Weing\\u00e4rten, Gem\\u00fcsefelder und Streuobstwiesen zum Bodensee. Boote schaukeln auf dem Wasser, die untergehende Sonne taucht die H\\u00fcgel am See in magisches Licht. Andreas Jung, Vizechef der Unionsfraktion im Bundestag, hat zu einer Infoveranstaltung in seinem Wahlkreis eingeladen. Nun steht er im blauen Anzug und legerem wei\\u00dfem Hemd mit offenem Kragen vor 70 CDU-Mitgliedern und durchbricht die Idylle: \\u201eIch bitte Sie, sich f\\u00fcr eine Schweigeminute f\\u00fcr die Opfer der Flutkatastrophe zu erheben.\\u201c<\\/p><p class=\\\"meinz\\\">Als alle wieder auf ihren Plastikst\\u00fchlen sitzen, ergreift neben ihm Deutschlands m\\u00e4chtigster Parlamentarier das Wort. Ralph Brinkhaus, Vorsitzender der CDU\\/CSU-Fraktion, ist extra ans s\\u00fcdliche Ende Deutschlands gekommen, um seinen Konstanzer Parteikollegen zu unterst\\u00fctzen. Brinkhaus spricht \\u00fcber die Pandemie, \\u00fcber Bildung und Bundeswehr. Vor allem aber sagt er: \\u201eEs ist Jungs Verdienst, dass wir klimapolitisch da stehen, wo wir sind. Wir brauchen ihn in Berlin. Und Sie werden noch viel von ihm h\\u00f6ren.\\u201c<\\/p><p class=\\\"meinz\\\">Das ist mehr als das \\u00fcbliche Lob des Chefs f\\u00fcr einen seiner Stellvertreter. Ralph Brinkhaus wei\\u00df, wie wichtig Andreas Jung, 46 Jahre, f\\u00fcr ihn ist. Gerade haben die \\u00dcberschwemmungen die Klimakrise zur\\u00fcck in den Wahlkampf gesp\\u00fclt. Und ohne Jung sieht die CDU beim Klima alt aus. Weil der \\u00d6kodruck auf die Union noch nie so gro\\u00df war, wird der freundliche gro\\u00dfgewachsene Mann mit dem dunklen Scheitel und dem badischen Singsang in der Sprache zu einer zentralen Figur in einer Regierung unter Armin Laschet werden. Doch den Namen des Juristen kennen bisher nur wenige, auch wenn er als Umweltfachmann der CDU und als begabter Netzwerker zum wichtigen Br\\u00fcckenbauer einer schwarz-gr\\u00fcnen Koalition werden k\\u00f6nnte. Der Konstanzer <i>S\\u00fcdkurier<\\/i> schreibt \\u00fcber ihn: \\u201eAndreas Jung ist so gr\\u00fcn, wie Winfried Kretschmann schwarz ist.\\u201c<\\/p><p class=\\\"meinz\\\">Jung selbst sagt auf der Hochwart zur Klimapolitik: \\u201eWir m\\u00fcssen mehr tun und schneller werden.\\u201c Damit wiederholt er die Forderung des CDU-Kanzlerkandidaten Armin Laschet, dass vieles \\u201eentfesselt\\u201c werden m\\u00fcsse: Der Ausbau der erneuerbaren Energien, der Erfindungsreichtum der Ingenieure, das Selbstvertrauen der Menschen, neue Techniken wie der gr\\u00fcne Wasserstoff. Seit 20 Jahren macht Andreas Jung Umweltpolitik in der Union \\u2013 oder versucht es zumindest. Lange wurde er ignoriert und ausgebremst, jetzt k\\u00f6nnte er etwas aufbauen, was es noch nie gab: einen durchsetzungsf\\u00e4higen Umweltfl\\u00fcgel der Konservativen.<\\/p><p class=\\\"meinz\\\">Selbst der franz\\u00f6sische Pr\\u00e4sident Emmanuel Macron hat seine wichtige Rolle erkannt: Am 20. Juli steht Andreas Jung im Innenhof der franz\\u00f6sischen Botschaft in Berlin, wie Bilder auf Twitter zeigen, und bekommt von Botschafterin Anne-Marie Desc\\u00f4tes eine silberne Medaille ans Revers geheftet: den Ritterorden der franz\\u00f6sischen Ehrenlegion. Was f\\u00fcr eine Ehre, zumal f\\u00fcr einen Deutschen. Jung beherrscht die traditionelle Sprache der Diplomatie, weil seine Eltern in seiner badischen Heimatstadt Stockach Franz\\u00f6sisch unterrichteten. Er leitet die deutsch-franz\\u00f6sische Parlamentarische Versammlung, bei der zweimal im Jahr jeweils 50\\u00a0Abgeordnete beider L\\u00e4nder gemeinsam tagen. Neben Umwelt ist Europa zentrales Thema seines politischen Lebens.<\\/p><p class=\\\"meinz\\\">Als ihm auf der Reichenau ein Parteifreund auf die Schulter klopft und zum Orden gratuliert, l\\u00e4chelt Andreas Jung und erz\\u00e4hlt, sein Sohn h\\u00e4tte gehofft, es gebe auch eine Ritterr\\u00fcstung dazu. Aber so ein Metallpanzer w\\u00e4re nicht das Richtige f\\u00fcr den Parlamentarier. Denn Jung macht Politik ohne R\\u00fcstung, seit er 2005 in den Bundestag einzog: zug\\u00e4nglich, ausgleichend, kompetent. Aus ihm ist ein Experte f\\u00fcr komplexe Themen wie den EU-Emissionshandel geworden, der beim Klima eloquent die schwammige CDU-Linie vertritt, die viele Gr\\u00fcne nervt: mehr Investitionen, aber trotzdem die schwarze Null; klimaneutral 2045, aber keine konkreten Zahlen; weder Tempolimit noch Flugverbot. Jung bietet aber beim CO<sub>2<\\/sub>-Preis, dem schnelleren Ausbau der Erneuerbaren oder einer \\u00f6kologischen Steuerreform trotzdem Schnittmengen mit den B\\u00fcndnisgr\\u00fcnen. Und nicht nur mit ihnen. \\u201eDer findet mit Kartoffelbauern genauso einen Draht wie mit Vorstandschefs\\u201c, sagt ein Beobachter aus der Region. \\u201eEr ist vom Typ her jemand, der in den diplomatischen Dienst passen w\\u00fcrde.\\u201c Lob von allen Seiten. Nicht mal anonym redet jemand schlecht \\u00fcber \\u201eden Andi\\u201c. Feinde? Offenbar Fehlanzeige.<\\/p><p class=\\\"meinz\\\">Vielleicht war Jung daf\\u00fcr in der Vergangenheit schlicht nicht wichtig genug. Im Schlachtenget\\u00fcmmel der Energiewende zog Ritter Jung mit seinen wenigen Getreuen immer wieder den K\\u00fcrzeren gegen den Wirtschaftsfl\\u00fcgel der Union: Die Solarindustrie wurde ruiniert, der Windausbau kam nicht vom Fleck, es gab Subventionen f\\u00fcr Kohle und Verbrennungsmotoren. Aber Jung blies nie zur \\u00d6ko-Offensive. 2018 \\u00fcbernahm er den Posten als Finanzexperte der Fraktion und lie\\u00df das Umweltthema damit offiziell hinter sich.<\\/p><p class=\\\"meinz\\\">Aber 2019 wurde er genau daf\\u00fcr wieder rekrutiert: Parteichefin Annegret Kramp-Karrenbauer suchte nach verlorenenWahlen in Bayern, Hessen und Europa und den massenhaften Demonstrationen von Fridays for Future verzweifelt nach einer gr\\u00fcneren Vision f\\u00fcr die Union. Jung schrieb ihr das Konzept. Dann sorgte er ma\\u00dfgeblich daf\\u00fcr, dass im Klimaschutzgesetz ein CO<sub>2<\\/sub>-Preis f\\u00fcr das Tanken und Heizen auftauchte \\u2013 eine Forderung, die in der Union lange als Teufelszeug galt. Und als das Bundesverfassungsgericht im Fr\\u00fchjahr 2021 pl\\u00f6tzlich von der Regierung sch\\u00e4rfere Klimaziele forderte, formulierte wieder Jung zusammen mit seiner CSU-Kollegin Anja Weisgerber die konservative Flucht nach vorn: Klimaneutralit\\u00e4t schon bis 2045, h\\u00f6here Ziele f\\u00fcr Erneuerbare. Armin Laschet verk\\u00fcndete das am 3. Mai im Konrad-Adenauer-Haus. Neben ihm nur der Mann f\\u00fcr die entscheidenden Details: Andreas Jung.<\\/p><p class=\\\"meinz\\\">Seitdem sitzt der Klimaexperte in Talkshows, gibt Interviews und schmiedet hinter den Kulissen B\\u00fcndnisse. Er ist jetzt an einem entscheidenden Punkt seiner Laufbahn. Zum ersten Mal wird sein Herzensthema Umwelt so wichtig, dass selbst die eigene Partei auf ihn h\\u00f6rt. Notgedrungen: <span class=\\\"snippet\\\">Hochwasser<\\/span> und D\\u00fcrresommer schockieren auch CDU-W\\u00e4hlerInnen. Klimaschutzgesetz und Bundesverfassungsgericht zwingen gerade die Law-and-Order-Partei zum Handeln, die EU-Kommission unter der Parteifreundin Ursula von der Leyen legt einen harten \\u201eGreen Deal\\u201c vor, die Wirtschaft dringt auf Klarheit und milliardenschwere Subventionen. Die Union war lange gut darin, neue Klimaziele zu verk\\u00fcnden und sie dann zu vergessen. Jung wei\\u00df: Das funktioniert nicht mehr.<\\/p><p class=\\\"meinz\\\">Inzwischen muss er den Schwung seiner ParteifreundInnen manchmal schon bremsen. Denn bei der CDU\\/CSU trommelt die \\u201eKlimaunion\\u201c mit radikalen \\u00d6koforderungen f\\u00fcr einen gigantischen Ausbau der Erneuerbaren und 100 Prozent \\u00d6koenergie schon 2030. Diese kleine Lobbygruppe von ChristdemokratInnen hat bislang nicht viel Macht, aber ihre Forderungen werden geh\\u00f6rt. Jung teilt nicht jede Position, unterst\\u00fctzt aber die Sto\\u00dfrichtung. \\u201eEs ist doch gut, wenn die Leute, die beim Klimaschutz etwas bewegen wollen, zu uns kommen\\u201c, sagt er. Wie gewohnt diplomatisch.<\\/p><p class=\\\"meinz\\\">Der erfahrene Politiker wei\\u00df genau, warum er lieber vorsichtig ist: Es ist gef\\u00e4hrlich, Dinge zu versprechen, die man nicht halten kann. Denn der Alltag der Energiewende ist z\\u00e4h. Auf seiner Wahlkampftour stoppt er nahe der Autobahn Singen\\u2013Stuttgart bei Engen. Hier wird ein neuer Solarpark direkt neben der Fernstra\\u00dfe eingeweiht. In der Mittagshitze dr\\u00e4ngen sich bei Sprudel und Butterbrezeln ein Dutzend CDU-Gemeindevertreter unter zwei Sonnenschirmen um ihren Abgeordneten aus Berlin, manche stehen eine halbe Stunde in der prallen Sonne, um ihre Geschichten loszuwerden. Jung l\\u00e4sst sich vom t\\u00e4glichen Kampf um Fl\\u00e4chen und Baugenehmigungen in den Gemeinden berichten. Das hier ist eine ganz andere Welt als in den hochfliegenden Visionen der \\u201eKlimaunion\\u201c.<\\/p><p class=\\\"meinz\\\">Jung fragt nach, berichtet aus Berlin und lobt einen Kandidaten, der auf einem Wahlplakat offensiv mit Windanlagen wirbt. Auf der Wiese voller Solarmodule, wo der Verkehr vorbeidonnert, wagt der sonst so loyale Jung sogar eine indirekte Kritik an Parteichef Laschet. Der warnt h\\u00e4ufig, es mit dem Klimaschutz nicht zu \\u00fcbertreiben. Jung dagegen will mehr Optimismus: \\u201eWir m\\u00fcssen die Erneuerbaren mehr als Chance sehen und offensiv f\\u00fcr sie werben.\\u201c<\\/p><p class=\\\"meinz\\\">Was treibt diesen Mann an? Karrierismus wohl kaum. Wer sich als Christdemokrat f\\u00fcr \\u00d6kothemen interessiert, hatte in der Union bisher keine steile Laufbahn vor sich. Die Vordenker f\\u00fcr die \\u201eBewahrung der Sch\\u00f6pfung\\u201c waren Einzelk\\u00e4mpfer und verloren h\\u00e4ufig ihre Schlachten. Herbert Gruhl verlie\\u00df die Partei 1978, Klaus T\\u00f6pfer wurde in Deutschland erst wirklich einflussreich, als er f\\u00fcr die UN in Afrika arbeitete. Der CSU-Abgeordnete Josef G\\u00f6ppel ertrug bis 2017 f\\u00fcnfzehn Jahre lang stoisch seine Rolle als ungeliebtes gr\\u00fcnes Gewissen der Unionsfraktion. Am Ende zog G\\u00f6ppel ein bitteres Fazit: \\u201eIch war eine singul\\u00e4re Stimme und bin daran gescheitert, einen Umweltfl\\u00fcgel in der Union zu gr\\u00fcnden.\\u201c<\\/p><p class=\\\"meinz\\\">Jung w\\u00e4hlte einen anderen Weg: Kompromiss statt Konfrontation, Biob\\u00fcrgerlichkeit statt \\u00d6korevolution. Der Katholik zitiert dazu immer wieder Papst Franziskus. Umwelt und Gerechtigkeit seien ihm wichtig, seit er als Sch\\u00fcler in der \\u201eAG M\\u00fcll\\u201c mitmachte. Ist er damit nicht in der falschen Partei? Jung sch\\u00fcttelt energisch den Kopf. Er k\\u00e4mpft schon immer daf\\u00fcr, dass auch Umweltsch\\u00fctzerInnen die Union w\\u00e4hlen k\\u00f6nnen. In seiner liberalen Heimat S\\u00fcdbaden sei Umwelt eben auch immer ein konservatives Thema gewesen. \\u201eAls in Wyhl bei Freiburg in den siebziger Jahren ein Atomkraftwerk gebaut werden sollte, gab es auch in der Union einen Aufstand\\u201c, sagt Jung. Am Bodensee seien die Menschen sensibel f\\u00fcr \\u00d6kofragen, weil sie mit dem See leben. Die Landwirte h\\u00e4tten fr\\u00fch gemerkt, dass sich Naturereignisse wie Hagel und Hitze h\\u00e4ufen. Die Region hat sich gegen Fracking gewehrt, 20 Kilometer hinter der Grenze plant die Schweiz ein atomares Endlager. Die Themen liegen vor der Haust\\u00fcr.<\\/p><img alt=\\\"BB=243.78 BH=364.772&#10;IB=884 IH=1326&#10;S=1.9 b=660 h= C=Multi&#10;id=1097271 typ=JPEG&#10;\\\" src=\\\"Media.1097271.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1097271.norm.jpg\')\\\" class=\\\"MediaImg ImgMulti ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMulti ImgSizeFull\\\">MdB Andreas Jung, oder auch: \\u201eder Andi\\u201c. Er ist ein Mann des Ausgleichs, alle m\\u00f6gen ihn, alle brauchen ihn. Aber ist er auch durchsetzungsf\\u00e4hig?<\\/p><p class=\\\"meinz\\\">Andreas Jung erz\\u00e4hlt freim\\u00fctig, wenn man ihn im wei\\u00dfen E-Mercedes durch seinen Wahlkreis begleitet. Am selben Tag verk\\u00fcndet der Daimler-Chef, ab 2030 nur noch E-Mobile zu bauen. F\\u00fcr Jung sind das gute Nachrichten: Denn damit l\\u00e4sst Daimler die fossilen Energien hinter sich, ganz ohne ein direktes staatliches Verbot. Umwelt, so Jungs Botschaft, ist nichts, was nur die Gr\\u00fcnen k\\u00f6nnen.<\\/p><p class=\\\"meinz\\\">So sieht das auch ein Dutzend Herren, die auf dem Fuchshof bei Konstanz auf \\u201eden Andi\\u201c warten. Darunter Obstbauern, der CDU-Umweltsprecher aus dem Landtag, Experten vom Naturschutzbund Nabu und BUND, ein Beh\\u00f6rdenvertreter. Der Raum ist dekoriert mit historischen F\\u00e4ssern, Wagenr\\u00e4dern und Obstpressen, dennoch geht es hier um die Zukunft. Ein neues Gesetz verbietet Pestizide in Naturschutzgebieten, aber dort haben manche Anwesende ihre B\\u00e4ume stehen, nun f\\u00fcrchten sie das Aus. Die Natursch\\u00fctzer wollen den Giftcocktail verringern und trotzdem irgendwie die Produktion retten. Jung hat bei dem Thema schon fr\\u00fcher vermittelt, jetzt sitzt er mit hochgekrempelten \\u00c4rmeln am Tisch, fragt nach, will Details wissen, bindet den Kollegen aus dem Landtag ein, lotet Kompromisse aus. \\u201eWir brauchen gemeinsam eine partnerschaftliche Perspektive\\u201c, sagt er.<\\/p><p class=\\\"meinz\\\">Das ist Jungs Vorstellung von Politik: alle Beteiligten an einen Tisch holen, informell Einigungen vorbereiten. Auf dem Fuchshof gibt es heute kein Ergebnis. Aber Arbeitsauftr\\u00e4ge und Jungs Versprechen, dranzubleiben.<\\/p><p class=\\\"meinz\\\">Jungs Begeisterung f\\u00fcr den Kompromiss ist sein gro\\u00dfes Talent. Vielleicht ist sie aber auch seine gro\\u00dfe Schw\\u00e4che. Man habe ihn f\\u00fcr seine Themen in der Partei \\u201enie wirklich k\\u00e4mpfen sehen\\u201c, sagen Beobachter. Gelegenheiten dazu gab es genug. In den letzten Jahren war die Unionsfraktion das schwarze Loch der Energiewende: Hier wurde blockiert und getrickst, die Bremserfraktion schickte einen Staatssekret\\u00e4r und eine Abteilungsleiterin als Aufpasser ins Wirtschaftsministerium. Kanzleramt und Ministerium standen oft hilflos vor diesem Machtzentrum, in dem \\u201e70 Prozent der zust\\u00e4ndigen Abgeordneten die ganze Richtung der Energiewende nicht passt\\u201c, wie ein hochrangiger Unionspolitiker sagte.<\\/p><p class=\\\"meinz\\\">Jung war dabei, konnte das aber nicht verhindern: Es war die bislang gr\\u00f6\\u00dfte Niederlage in seiner Bilderbuchkarriere. Josef G\\u00f6ppel sieht in Jung einen \\u201enetten Kollegen, aber keinen K\\u00e4mpfer\\u201c. Man m\\u00fcsse \\u201ein einer Fraktion von 300\\u00a0Leuten auch mal couragiert aufstehen\\u201c, um die Stimmung zu wenden. \\u201eDas habe ich bei ihm nie erlebt, obwohl er f\\u00fcr Klimapolitik zust\\u00e4ndig war.\\u201c Und J\\u00fcrgen Resch von der Deutschen Umwelthilfe sagt: \\u201eWir sind zunehmend entt\\u00e4uscht, wie wenig bei Jungs Politik f\\u00fcr die Umwelt rausgekommen ist.\\u201c Obwohl er \\u201emenschlich gro\\u00dfe Sympathie\\u201c f\\u00fcr Jung empfinde. Der m\\u00fcsse aber \\u201eseinen Stil \\u00e4ndern, moderieren reicht nicht mehr\\u201c.<\\/p><p class=\\\"meinz\\\">Andreas Jung kennt diese Vorw\\u00fcrfe. \\u201eAber verwechseln Sie nicht Verbindlichkeit mit fehlender H\\u00e4rte in der Sache\\u201c, sagt er dazu. Jahrelang, so erz\\u00e4hlt er, habe er in der Fraktion dagegen gehalten, als der Wirtschaftsfl\\u00fcgel noch gegen Umweltstandards und Erneuerbare vom Leder zog. Er habe klimapolitisch Kante gezeigt und neu im Bundestag mit anderen Unionsumweltpolitikern f\\u00fcr einen \\u201eT\\u00f6pfer-Kurs\\u201c geworben. Sp\\u00e4ter rief er den \\u201eKlimakreis\\u201c der Fraktion ins Leben. \\u201eWenn wegen anderer Krisen der Klimaschutz nicht mit dem n\\u00f6tigen Nachdruck weiterverfolgt wurde, war das ein Fehler\\u201c, meint er r\\u00fcckblickend. Aber man habe gelernt: \\u201eBei Corona wurde dann in Konjunktur und Zukunft investiert \\u2013 etwa in die Wasserstoffstrategie.\\u201c<\\/p><p class=\\\"meinz\\\">Mit \\u00f6ffentlicher Kritik an den eigenen Leuten h\\u00e4lt er sich extrem zur\\u00fcck. Lieber sucht er nach Alliierten au\\u00dferhalb der Fraktion, so hat er beim Aufbau des neuen wirtschaftsnahen Thinktanks Epico KlimaInnovation mitgeholfen. Sein informeller \\u201eKlimakreis\\u201c in der Fraktion, den 2018 die CSU-Klimasprecherin Anja Weisgerber von ihm erbte, sei von wenigen Aufrechten auf mehrere Dutzend gewachsen, sagt Weisgerber. \\u201eWir stimmen uns ab, wir h\\u00f6ren Experten an, aber wir wirken vor allem nach innen in die Fraktion\\u201c, sagt sie. \\u201eWir sind schlie\\u00dflich eine Volkspartei, die alle Seiten mitnehmen muss. Wir arbeiten im Maschinenraum des Parlaments.\\u201c<\\/p><p class=\\\"meinz\\\">Da prallen zwei Konzepte aufeinander: Aufstand der Weltretter gegen die Mechaniker der Macht. Man k\\u00f6nnte sagen: J\\u00fcrgen Resch und Josef G\\u00f6ppel w\\u00fcnschen sich, dass Ritter Jung ab und zu einen Drachen t\\u00f6tet. Der aber will den Drachen lieber z\\u00e4hmen, um auf ihm zu reiten.<\\/p><p class=\\\"meinz\\\">Die Lust am Ausgleich liegt in Jungs Heimat. Man suche hier keinen Streit, sagen die Leute. Die Schweiz mit ihrer Konsensdemokratie ist gleich nebenan. Und Jungs Heimat, die Gem\\u00fcseinsel Reichenau, erscheint wie eine Insel der Seligen. Ritter Jung wohnt hier zwischen Treibh\\u00e4usern und Weltkulturerbe, auf seiner Insel stehen Kl\\u00f6ster, die 1.300 Jahre alt sind, und Dorflinden, die 700 Jahre auf dem Buckel haben. Hinrichtungen gab es nur auf dem Festland.<\\/p><p class=\\\"meinz\\\">Jung ist durchaus machtbewusst. Er hat im Fr\\u00fchjahr die gr\\u00fcn-schwarze Koalitionsvereinbarung in Baden-W\\u00fcrttemberg f\\u00fchrend mitverhandelt. Er ist Chef der CDU-Landesgruppe Baden-W\\u00fcrttemberg im Bundestag, der zweitst\\u00e4rksten Truppe von Abgeordneten. Er ist einer der \\u201eTeppichh\\u00e4ndler\\u201c, die in der Fraktion Posten und Aufgaben verteilen. Und nach der Wahl wird Jung so m\\u00e4chtig sein wie nie zuvor. Folgerichtig w\\u00e4re der Job als Vizechef der Fraktion, statt f\\u00fcr Finanzen zust\\u00e4ndig f\\u00fcr Umwelt, oder ein Ministeramt. Kann Jung ein treuer Knappe von Laschet werden \\u2013 und trotzdem eine eigene Truppe von Regenbogenkriegern anf\\u00fchren? Immerhin sind zwei Wortf\\u00fchrer der Bremserfraktion verschwunden: CSU-Mann Georg N\\u00fcsslein stolperte \\u00fcber Maskendeals, CDU-Wirtschaftssprecher Joachim Pfeiffer gab lieber sein Mandat auf, als die Gesch\\u00e4ftspartner seiner Nebenjobs offenzulegen.<\\/p><p class=\\\"meinz\\\">Jeder anst\\u00e4ndige Ritter hat einen Hofnarren. Andreas Jung hat gleich zwanzig. Am Ende einer langen Wahlkampfwoche erscheint er noch beim Stockacher Narrengericht. Seit 1351 klagen die Narren in Jungs Heimatstadt Pers\\u00f6nlichkeiten wegen diverser n\\u00e4rrischer Vorw\\u00fcrfe an, der Delinquent darf sich dann m\\u00f6glichst geistreich verteidigen und wird dazu verurteilt, teuren Wein zu stiften. Dieses Jahr hat es Cem \\u00d6zdemir von den Gr\\u00fcnen erwischt. Also sitzt \\u00d6zdemir in Anzug und Sneakers im Garten des Stockacher Restaurants Zum Goldenen Ochsen, umringt von M\\u00e4nnern in schwarzen Jacketts mit bunten Narrenkappen auf dem Kopf.<\\/p><p class=\\\"meinz\\\">Es ist nicht ganz einfach, mit \\u00d6zdemir am Rand des lustigen Treffens ein paar ernsthafte Worte zu wechseln. Dann sagt er aber doch, dass er Jung f\\u00fcr sein \\u201egro\\u00dfes Engagement, Sachkenntnis und das Herzblut, mit dem er dabei ist\\u201c, sch\\u00e4tzt. Aber \\u00d6zdemir sieht noch mehr in ihm: \\u201eWir brauchen Jungs Einfluss bei der Union. Denn gegen die Union wird es keinen Klimaschutz geben, wenn ich an ihre Ministerpr\\u00e4sidenten in den L\\u00e4ndern denke.\\u201c<\\/p><p class=\\\"meinz\\\">Aber Jung brauchte auch schon fr\\u00fcher die Gr\\u00fcnen f\\u00fcr mehr Klimaschutz, sagt \\u00d6zdemir: \\u201eOhne uns w\\u00e4re der CO<sub>2<\\/sub>-Preis, den er verhandelt hat, bei den hom\\u00f6opathischen 10 Euro geblieben, den haben wir unter schwersten Bedingungen im Vermittlungsausschuss auf 25 Euro gebracht.\\u201c Und Laschet, der mit Klimaschutz wenig am Hut habe, schiele eh auf die FDP. \\u201eJung muss f\\u00fcr mehr Klimaschutz in der Union jetzt in der Fraktion die Machtfrage stellen, gegen die alten Seilschaften, die in den letzten Jahren Klimaschutz sabotiert haben.\\u201c<\\/p><p class=\\\"meinz\\\">Der Termin ist zu Ende. F\\u00fcr Andreas Jung geht es endlich nach Hause zur Familie und ins Wochenende. Auf der Fahrt durch Stockach weist er aus dem Fenster: Da liegt der Fu\\u00dfballplatz des VfR Stockach, wo er in der Jugend als Torwart gespielt hat. Die Vereinsfarben: Schwarz-Gr\\u00fcn.<\\/p><p class=\\\"Hinweis\\\"><strong>Bernhard P\\u00f6tter,<\\/strong> 55, ist taz-Redakteur f\\u00fcr Klima und Umwelt. Er ist gespannt darauf, wie umweltvertr\\u00e4glich die CDU\\/CSU werden kann.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a131\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.63.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a131\\/\\\"><h4 class=\\\"AutorName\\\">Bernhard P\\u00f6tter<\\/h4><h5 class=\\\"AutorJob\\\">Redakteur f\\u00fcr Wirtschaft und Umwelt<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Bernhard P\\u00f6tter &lt;poetter@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Jahrgang 1965. Seine Schwerpunkte sind die Themen Klima, Energie und Umweltpolitik. Wenn die Zeit es erlaubt, besch\\u00e4ftigt er sich noch mit Kirche, Kindern und Konsum. F\\u00fcr die taz arbeitet er seit 1993, zwischendurch und frei u.a. auch f\\u00fcr DIE ZEIT, WOZ, GEO, New Scientist. Autor einiger B\\u00fccher, Zum Beispiel \\u201eTatort Klimawandel\\u201c (oekom Verlag) und \\u201eStromwende\\u201c(Westend-Verlag, mit Peter Unfried und Hannes Koch). <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-31.017151ecab637f8bedc37bf9fa303c84563e86b7\",\"article\":{\"articleHtml\":{\"name\":\"art03605673.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627663409\",\"sha256\":\"ee2e488ee30357bcdd0e4a57fc5872b1ff2a34229ac5c37b004e7b8f06152eee\",\"sSize\":\"23615\"},\"audioFile\":null,\"title\":\"Ritter ohne R\\u00fcstung\",\"teaser\":\"Nach der Wahl muss die CDU\\/CSU im Bundestag beim Klimaschutz ernst machen. Mit Andreas Jung hat sie daf\\u00fcr sogar den richtigen Mann. Aber ist er wom\\u00f6glich zu nett f\\u00fcr den Job?\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5788912\",\"pageNames\":[\"s00811243.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097270.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663380\",\"sha256\":\"03ef0e879aa5050132a9678a6834f367e45d5f5116c1691d369dd2b2067ede11\",\"sSize\":\"59836\"},{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097271.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663408\",\"sha256\":\"d0bd3b572798176ac356af51e234e931dc8abde01e9a34f47ca5b36a05dc0d51\",\"sSize\":\"49192\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097270.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663381\",\"sha256\":\"bdb32d3041505a54b4e7283c2984f81feb22735d118b60f6a3539c5f6b024ce1\",\"sSize\":\"472086\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1097271.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1627663409\",\"sha256\":\"de5338ae5701839d29634de64bb8a8e2f659260aaeb6c6695e5a5fcee577da99\",\"sSize\":\"76201\"}],\"authorList\":[{\"name\":\"Bernhard P\\u00f6tter\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.63.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993242\",\"sha256\":\"6f4832f9ab762691daea86d8793e572736527724980b940695a68330d562da26\",\"sSize\":\"88130\"}}]}},{\"sDate\":\"2021-07-27\",\"snippet\":\"....\\u201c Kinobetreiber, deren Filmtheater von den <span class=\\\"snippet\\\">Hochwassern<\\/span> im Westen Deutschlands besch\\u00e4digt wurden...\",\"teaser\":\"Die Kunststiftung NRW, die seit \\u2026\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>unterm strich<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_ARubrik4_2017 eptZwischentitelGross\\\"><h4 class=\\\"Dach\\\">unterm strich<\\/h4><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_ARubrik4_2017 eptZwischentitelGross BeitragsArt_taz_print_west ArtikelTyp_ARubrik4_2017 eptZwischentitelGross\\\"><p class=\\\"oeinz\\\"><strong>Die Kunststiftung NRW,<\\/strong> die seit 1989 Kunst und Kultur in NRW f\\u00f6rdert, hat einen neuen Pr\\u00e4sidenten: Thomas Sternberg, auch Pr\\u00e4sident des Zentralkomitees der deutschen Katholiken. Die Kulturministerin Isabel Pfeiffer-Poensgen begr\\u00fc\\u00dft seine Ernennung: \\u201eEr wird seinen breiten Erfahrungshorizont im Bereich der Kulturpolitik sowie seine tiefe Kenntnis der nordrhein-westf\\u00e4lischen Kunst- und Kulturszene als Pr\\u00e4sident einbringen und die Kunststiftung in eine erfolgreiche Zukunft f\\u00fchren.\\u201c<\\/p><p class=\\\"oeinz\\\">Kinobetreiber, deren <strong>Filmtheater von den <span class=\\\"snippet\\\">Hochwassern<\\/span><\\/strong> im Westen Deutschlands besch\\u00e4digt wurden, sollen <strong>finanzielle Unterst\\u00fctzung<\\/strong> bekommen. Die Filmf\\u00f6rderungsanstalt (FFA) will 350.000 Euro Soforthilfe zur Verf\\u00fcgung stellen, teilte sie am Montag mit. Vergleichbare Programme gab es nach den Hochwasserkatastrophen 2002 und 2013. Gezahlt wird, um Geb\\u00e4ude und Technik wiederherzustellen. Die Zusch\\u00fcsse betragen bis zu zehn Prozent der Kosten, maximal 7.000 Euro pro Leinwand.<\\/p><p class=\\\"oeinz\\\"><strong>Der preisgekr\\u00f6nte US-Komiker Jackie Mason<\\/strong> ist tot, erfolgreich am Broadway und in TV-Shows. Bekannt war der aufbrausende Mason f\\u00fcr seinen scharfz\\u00fcngigen Humor und bissige Beobachtungen \\u00fcber die Gesellschaft und die Zumutungen des Daseins. Oft ging es bei seinen Stand-up-Comedy-Auftritten um j\\u00fcdische Identit\\u00e4t \\u2013 Mason war auch Rabbiner \\u2013, M\\u00e4nner und Frauen oder eigene Unzul\\u00e4nglichkeiten. Er starb im Alter von 93 Jahren in einer Klinik in Manhattan.<\\/p><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-27.555ee58674c7460be40bdee758f47067d917d223\",\"article\":{\"articleHtml\":{\"name\":\"art03604506.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627318990\",\"sha256\":\"a44631ace7ba61487793d4b6e58921be0e525a117cdccb32c74146fd7deffadb\",\"sSize\":\"3654\"},\"audioFile\":null,\"title\":\"unterm strich\",\"teaser\":\"\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5785795\",\"pageNames\":[\"s00810932.pdf\"],\"imageList\":null,\"authorList\":null}},{\"sDate\":\"2021-07-30\",\"snippet\":\"... Eifel speisen, haben w\\u00e4hrend des <span class=\\\"snippet\\\">Hochwassers<\\/span> Heiz\\u00f6ltanks sowie Tankstellen unter Wasser...\",\"teaser\":\"In den Orten, in denen Praxen zerst\\u00f6rt wurden, fehlten auch Medikamente\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>\\u00c4rzte warnen vor Seuchengefahr in Flutgebieten<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A4_2017 eptPolitik\\\"><h2 class=\\\"Titel\\\">\\u00c4rzte warnen vor Seuchengefahr  \\nin Flutgebieten<\\/h2><h4 class=\\\"Unterzeile\\\">In den Orten, in denen Praxen zerst\\u00f6rt wurden, fehlten auch Medikamente<\\/h4><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A4_2017 eptPolitik\\\"><p class=\\\"oeinz\\\">Die deutschen Amts\\u00e4rzte beklagen eine mangelhafte medizinische Grundversorgung in den Hochwasserregionen. Die Gesundheit der Bev\\u00f6lkerung in den Katastrophengebieten sei \\u201emassiv bedroht, weil die Infrastruktur nicht funktioniert\\u201c, sagte die Vorsitzende des Bundesverbands der \\u00c4rztinnen und \\u00c4rzte des \\u00f6ffentlichen Gesundheitsdienstes, Ute Teichert, den Zeitungen der Funke Mediengruppe.<\\/p><p class=\\\"meinz\\\">Die Situation sei \\u201enach wie vor erschreckend\\u201c, erkl\\u00e4rte Teichert. In den Orten, in denen Krankenh\\u00e4user und Arztpraxen von den Wassermassen zerst\\u00f6rt wurden, m\\u00fcssten viele Menschen ohne dringend ben\\u00f6tigte Medikamente auskommen. Zum Teil herrsche Seuchengefahr.<\\/p><p class=\\\"meinz\\\">Eine zentrale Aufgabe sei es jetzt, mobile Arzteinheiten zu organisieren und in die Ortschaften zu bringen, forderte die Medizinerin, die bis 2012 das Gesundheitsamt im flutbetroffenen Landkreis Ahrweiler leitete. Zwar gebe es etwa in Ahrweiler ein provisorisches Zelt f\\u00fcr die medizinische Versorgung, Menschen aus anderen betroffenen D\\u00f6rfern gelangten aber nicht dorthin. \\u201eDaher gibt es jetzt ehrenamtliche Initiativen von \\u00c4rzten und Pflegekr\\u00e4ften, die mit Rucks\\u00e4cken von Haus zu Haus gehen\\u201c, sagte Teichert.<\\/p><p class=\\\"meinz\\\">Als Konsequenz aus der Flutkatastrophe forderte die Verbandschefin Verbesserungen im Krisenmanagement. \\u201eWir m\\u00fcssen uns f\\u00fcr die Zukunft besser \\u00fcberlegen, wie die Gesundheitsversorgung der Menschen nach Katastrophen aufrechtzuerhalten ist\\u201c, sagte Teichert. Derzeit spiele die Gesundheitsversorgung beim Katastrophenschutz eher eine untergeordnete Rolle, kritisierte sie.<\\/p><p class=\\\"meinz\\\">Derweil l\\u00e4sst der Wasserverband Eifel-Rur (WVER) das Wasser von zwei Talsperren in der Eifel auf m\\u00f6gliche R\\u00fcckst\\u00e4nde pr\\u00fcfen. Die Proben aus der Rurtal- und der Urfttalsperre w\\u00fcrden auf Spuren von Benzin, Heiz\\u00f6l und Metallen untersucht, sagte der Sprecher des Verbands, Marcus Seiler. Ergebnisse l\\u00e4gen noch nicht vor. Im Einzugsbereich der Fl\\u00fcsse, die sowohl die Urfttalsperre als auch die Rurtalsperre in der Eifel speisen, haben w\\u00e4hrend des <span class=\\\"snippet\\\">Hochwassers<\\/span> Heiz\\u00f6ltanks sowie Tankstellen unter Wasser gestanden.<\\/p><p class=\\\"meinz\\\">Die Rurtalsperre ist der F\\u00fcllmenge nach die zweitgr\\u00f6\\u00dfte Talsperre in Deutschland mit 202,6 Millionen Kubikmetern Fassungsverm\\u00f6gen. Sie liegt westlich von Euskirchen. Ein Bereich des Talsperrenverbunds ist Teil der Trinkwasserversorgung f\\u00fcr 600.000 Menschen in Aachen und den umliegenden St\\u00e4dten. Der Sprecher sagte, eine Folge der \\u00dcberschwemmung sei Treibgut auf der Wasseroberfl\\u00e4che. <i>(epd, dpa)<\\/i><\\/p><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-30.b061da1dfd13fcad5f8b4bbf8e0ef747c4e5a7b5\",\"article\":{\"articleHtml\":{\"name\":\"art03605328.html\",\"storageType\":\"issue\",\"sMoTime\":\"1627578774\",\"sha256\":\"0fec98582752b41b93f75bd7aac699385c3b5e770aba1a46e30bd6bc1b5c3afb\",\"sSize\":\"4758\"},\"audioFile\":null,\"title\":\"\\u00c4rzte warnen vor Seuchengefahr in Flutgebieten\",\"teaser\":\"In den Orten, in denen Praxen zerst\\u00f6rt wurden, fehlten auch Medikamente\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5790371\",\"pageNames\":[\"s00811139.pdf\"],\"imageList\":null,\"authorList\":null}},{\"sDate\":\"2021-07-20\",\"snippet\":\"... des Dauerregens sind unter anderem <span class=\\\"snippet\\\">Hochwasser<\\/span> an B\\u00e4chen und kleineren Fl\\u00fcssen... zu sp\\u00e4t vor dem verheerenden <span class=\\\"snippet\\\">Hochwasser<\\/span> gewarnt. \\u201eWir sind extrem zufrieden... \\u2013 pr\\u00e4ventiv und im Ernstfall. Beim <span class=\\\"snippet\\\">Hochwasser<\\/span> der vergangenen Woche hat das...\",\"teaser\":\"Die Unwetter der vergangenen Woche kamen mit Ansage. Warnungen erreichten die Bev\\u00f6lkerung aber nur teilweise. Wo liegt der Fehler jetzt?\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title><span class=\\\"snippet\\\">Hochwasser<\\/span>: Warnung nicht geh\\u00f6rt<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><h2 class=\\\"Titel\\\"><span class=\\\"snippet\\\">Hochwasser<\\/span>: Warnung nicht geh\\u00f6rt<\\/h2><h4 class=\\\"Unterzeile\\\">Die Unwetter der vergangenen Woche kamen mit Ansage. Warnungen erreichten die Bev\\u00f6lkerung aber nur teilweise. Wo liegt der Fehler jetzt?<\\/h4><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Tobias Schulze<\\/span>  \\nund <span class=\\\"zAu\\\">Jost Maurin<\\/span><\\/p><p class=\\\"oeinz\\\">Vor einem Jahr haben die Beh\\u00f6rden gewarnt. Im Juni 2020 hat das Bundesamt f\\u00fcr Bev\\u00f6lkerungsschutz und Katastrophenhilfe eine 400-seitige Brosch\\u00fcre herausgegeben. Der Titel: \\u201eDie untersch\\u00e4tzten Risiken \\u201aStarkregen\\u2018 und \\u201aSturzfluten\\u2018\\u201c. Die beiden Ph\\u00e4nomene seien \\u201etrotz ihres immensen Gefahrenpotenzials keine Themen, die in der \\u00d6ffentlichkeit weit verbreitet sind\\u201c. Zentral, so eine der Hauptaussagen, sei daher die Kommunikation mit der Bev\\u00f6lkerung \\u2013 pr\\u00e4ventiv und im Ernstfall.<\\/p><p class=\\\"meinz\\\">Beim <span class=\\\"snippet\\\">Hochwasser<\\/span> der vergangenen Woche hat das offenbar nicht funktioniert. Seit dem Wochenende dreht sich die Debatte darum, ob die Beh\\u00f6rden ausreichend gewarnt haben. Mit angesto\\u00dfen hat die Diskussion die britische Hydrologin Hannah Cloke, die das europ\\u00e4ische Fr\\u00fchwarnsystem Efas mitentwickelt hat. Schon Tage im Voraus habe man die deutschen Beh\\u00f6rden vor \\u00dcberschwemmungen gewarnt. \\u201eDer Fakt, dass Menschen nicht evakuiert wurden oder keine Warnungen erhalten haben, l\\u00e4sst darauf schlie\\u00dfen, das etwas schiefgegangen ist\\u201c, sagte sie am Sonntag der <i>Times.<\\/i><\\/p><p class=\\\"meinz\\\">Der Deutsche Wetterdienst (DWD) und das Bundesamt f\\u00fcr Bev\\u00f6lkerungsschutz haben am Montag Vorw\\u00fcrfe zur\\u00fcckgewiesen, sie h\\u00e4tten zu sp\\u00e4t vor dem verheerenden <span class=\\\"snippet\\\">Hochwasser<\\/span> gewarnt. \\u201eWir sind extrem zufrieden mit unserem fr\\u00fchzeitigen Warnmanagement\\u201c, sagte DWD-Sprecher Uwe Kirsche der taz. Bereits am Vormittag des vergangenen Montags (12.\\u00a0Juli) gab der DWD eine \\u201eVorabinformation Unwetter vor heftigem\\/ergiebigem Regen\\u201c heraus f\\u00fcr die Zeit von Dienstag- bis Donnerstagmorgen. \\u201eBis Donnerstagfr\\u00fch k\\u00f6nnen aufsummiert \\u00f6rtlich begrenzt Regenmengen von bis zu 200 Liter pro Quadratmeter auftreten\\u201c, lautete ein Satz in dieser Mitteilung, der sich sp\\u00e4ter auch bewahrheitete.<\\/p><p class=\\\"meinz\\\">Gegen 18 Uhr am Montag folgte eine offizielle Unwetterwarnung f\\u00fcr ein Gebiet zwischen Dortmund und Saarbr\\u00fccken, in dem auch die von der Katastrophe besonders betroffenen Landkreise Ahrweiler und Rhein-Erft liegen. \\u201eInfolge des Dauerregens sind unter anderem <span class=\\\"snippet\\\">Hochwasser<\\/span> an B\\u00e4chen und kleineren Fl\\u00fcssen sowie \\u00dcberflutungen von Stra\\u00dfen m\\u00f6glich\\u201c, schrieben die Meteorologen. Und: \\u201eEs k\\u00f6nnen zum Beispiel Erdrutsche auftreten.\\u201c Mit dem Unwetter sei zwischen Dienstag- und Donnerstagmorgen zu rechnen \\u2013 was sich bewahrheitete.<\\/p><p class=\\\"meinz\\\">Am Dienstagmorgen warnte der DWD sogar vor einem \\u201eextremen Unwetter\\u201c und grenzte das betroffene Gebiet enger ein. \\u201eDie entsprechenden konkreten einzelnen Warnungen werden dann jeweils auf Landkreis- und\\/oder Gemeindeebene an die zahlreichen DWD-Partner im Katastrophenschutz (THW, Feuerwehr, Polizeien, Einsatzst\\u00e4be) verteilt\\u201c, erkl\\u00e4rte Sprecher Kirsche. Auch die betroffenen Landkreise und Kommunen seien informiert worden.<\\/p><p class=\\\"meinz\\\">Der Pr\\u00e4sident des Bundesamtes f\\u00fcr Bev\\u00f6lkerungsschutz und Katastrophenhilfe, Armin Schuster, wies ebenfalls Kritik zur\\u00fcck, es gebe kein ausreichendes Warnsystem. \\u201eDie Warninfrastruktur ist nicht unser Problem gewesen, sondern wie sensibel reagieren Beh\\u00f6rden und Bev\\u00f6lkerung auf diese Warnungen\\u201c, sagte Schuster am Montag im Deutschlandfunk und verwies auf 150 Warnmeldungen, die zwischen Mittwoch und Samstag abgesetzt worden seien. Derzeit werde f\\u00fcr 90 Millionen Euro ein Sirenenkataster aufgebaut, um zu sehen, wo in Deutschland noch Sirenen vorhanden seien. Man habe sich in den vergangenen Jahren vor allem auf den Ausbau digitaler Warnsysteme konzentriert, was aber nicht ausreiche, wenn Strom und Mobilfunk ausfielen.<\\/p><p class=\\\"meinz\\\">Das nordrhein-westf\\u00e4lische Innenministerium teilte laut <i>Bild<\\/i>-Zeitung mit, alle amtlichen Warnungen seien der Leitstelle der Kreise und der kreisfreien St\\u00e4dte zugestellt worden. Die konkreten Vorbereitungen, die diese auf Grundlage der amtlichen Wetterwarnungen dann treffe, l\\u00e4gen aber in deren eigenem Ermessen. Da ein solches Ereignis abzusehen gewesen sei, sei am Dienstag eine \\u201eLandeslage\\u201c eingerichtet worden, um fr\\u00fchzeitig zu erkennen, ob in einem Kreis oder einer kreisfreien Stadt \\u00fcber\\u00f6rtliche Hilfe ben\\u00f6tigt werde.<\\/p><p class=\\\"meinz\\\">Weder der Kreis Ahrweiler noch der Rhein-Erft-Kreis reagierten bis Redaktionsschluss auf Bitten der taz um Stellungnahme. Beide Kreisverwaltungen sind wegen der Folgen der Katastrophe derzeit stark belastet.<\\/p><p class=\\\"meinz\\\">Als Folge des Unwetters k\\u00f6nnte nun eine ohnehin geplante Reform des Katastrophenschutzes beschleunigt werden. Beim Bundesamt f\\u00fcr Bev\\u00f6lkerungsschutz und Katastrophenhilfe soll ein \\u201eKompetenzzentrum\\u201c entstehen, in dem Vertreter verschiedener Ebenen und Organisationen in st\\u00e4ndigem Austausch stehen. Kompetenzen sollen dabei nicht von L\\u00e4ndern und Kommunen auf den Bund \\u00fcbertragen werden, die Kommunikation soll sich aber verbessern. Vorbild ist das Gemeinsame Terrorismusabwehrzentrum der Sicherheitsbeh\\u00f6rden von Bund und L\\u00e4ndern. <i>(mit rtr)<\\/i><\\/p><p class=\\\"oeinz\\\"><span class=\\\"Seitennummer\\\"><a href=\\\"javascript:EPUBTAZ.internLink(\'art03602897.html\')\\\">nahaufnahme 4\\u20135<\\/a><\\/span><\\/p><p class=\\\"oeinz\\\"><span class=\\\"Seitennummer\\\"><a href=\\\"javascript:EPUBTAZ.internLink(\'art03602908.html\')\\\">wirtschaft + umwelt 8<\\/a><\\/span><\\/p><p class=\\\"oeinz\\\"><span class=\\\"Seitennummer\\\"><a href=\\\"javascript:EPUBTAZ.internLink(\'art03602903.html\')\\\">meinung + diskussion 12<\\/a><\\/span><\\/p><p class=\\\"oeinz\\\"><span class=\\\"Seitennummer\\\"><a href=\\\"javascript:EPUBTAZ.internLink(\'art03602895.html\')\\\">tazzwei 14<\\/a><\\/span><\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a2557\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.153.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a2557\\/\\\"><h4 class=\\\"AutorName\\\">Tobias Schulze<\\/h4><h5 class=\\\"AutorJob\\\">Ressortleiter Inland<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Tobias Schulze &lt;tobias.schulze@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorTwitter\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/twitter.com\\/tschlze\\\"><img class=\\\"icon\\\" src=\\\"resources\\/microblog_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Geboren 1988, arbeitet seit 2013 f\\u00fcr die taz. Leitet das Ressort Inland und schreibt unter anderem \\u00fcber deutsche Au\\u00dfen- und Verteidigungspolitik. Hat davor Politikwissenschaft studiert und an der Deutschen Journalistenschule gelernt.  <\\/div><\\/div><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a136\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.35.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a136\\/\\\"><h4 class=\\\"AutorName\\\">Jost Maurin<\\/h4><h5 class=\\\"AutorJob\\\">Redakteur f\\u00fcr Wirtschaft und Umwelt<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorTwitter\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/twitter.com\\/JostMaurinTAZ\\\"><img class=\\\"icon\\\" src=\\\"resources\\/microblog_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorFacebook\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/facebook.com\\/jost.maurintaz\\\"><img class=\\\"icon\\\" src=\\\"resources\\/facebook_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Jahrgang 1974. Er schreibt vor allem zu Ern\\u00e4hrungsfragen \\u2013 etwa \\u00fcber Agrarpolitik, Gentechnik, Pestizide, Verbraucherschutz und die Lebensmittelindustrie. 2018, 2017 und 2014 Journalistenpreis \\u201eGr\\u00fcne Reportage\\u201c. 2015 \\u201eBester Zweiter\\u201c beim Deutschen Journalistenpreis. 2013 nominiert f\\u00fcr den \\u201eLangen Atem\\u201c. Bevor er zur taz kam, war er Redakteur bei der Nachrichtenagentur Reuters und Volont\\u00e4r bei der S\\u00fcddeutschen Zeitung.  <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-20.d9d5aa81d69014e334b23224374ee3b5d893327b\",\"article\":{\"articleHtml\":{\"name\":\"art03602920.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626715067\",\"sha256\":\"2c099845b695a46c3edb3ce2c3155efb8250014edbf37340df4f36e105c773c7\",\"sSize\":\"10110\"},\"audioFile\":null,\"title\":\"Hochwasser: Warnung nicht geh\\u00f6rt\",\"teaser\":\"Die Unwetter der vergangenen Woche kamen mit Ansage. Warnungen erreichten die Bev\\u00f6lkerung aber nur teilweise. Wo liegt der Fehler jetzt?\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5787468\",\"pageNames\":[\"s00810443.pdf\"],\"imageList\":null,\"authorList\":[{\"name\":\"Tobias Schulze\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.153.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993514\",\"sha256\":\"4126b3a53aca8619f638bb205589650fe2d5b52df34c8d8675300bd33646e30a\",\"sSize\":\"75048\"}},{\"name\":\"Jost Maurin\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.35.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993379\",\"sha256\":\"6614523c4e4938a9231769c4354f8b39e846240bcc2712f56aa564288d5cbf00\",\"sSize\":\"53141\"}}]}},{\"sDate\":\"2021-07-17\",\"snippet\":\"... Umweltsch\\u00fctzer fordern Konsequenzen aus dem <span class=\\\"snippet\\\">Hochwasser<\\/span>: Mehr Klimaschutz, die Bewohner mancher..., sie h\\u00e4tten den Schutz vor <span class=\\\"snippet\\\">Hochwasser<\\/span> vernachl\\u00e4ssigt. Alle verwiesen auf Projekte...\",\"teaser\":\"Wissenschaftler und Umweltsch\\u00fctzer fordern Konsequenzen aus dem <span class=\\\"snippet\\\">Hochwasser<\\/span>: Mehr Klimaschutz, die Bewohner mancher besonders gef\\u00e4hrdeter Orte in T\\u00e4lern sollen umsiedeln, Flussauen wiederhergestellt werden\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Wie sich Deutschland sch\\u00fctzen kann<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AL0_2017 eptNorm\\\"><h2 class=\\\"Titel\\\">Wie sich Deutschland sch\\u00fctzen kann<\\/h2><h4 class=\\\"Unterzeile\\\">Wissenschaftler und Umweltsch\\u00fctzer fordern Konsequenzen aus dem <span class=\\\"snippet\\\">Hochwasser<\\/span>: Mehr Klimaschutz, die Bewohner mancher besonders gef\\u00e4hrdeter Orte in T\\u00e4lern sollen umsiedeln, Flussauen wiederhergestellt werden<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptNorm\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=824.489 BH=457.512&#10;IB=2738 IH=1825&#10;S=6.1 b=660 h= C=Main&#10;id=1094596 typ=JPEG&#10;\\\" src=\\\"Media.1094596.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1094596.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Der Planet schl\\u00e4gt zur\\u00fcck: Flutsch\\u00e4den in Kreuzberg in Rheinland-Pfalz <span class=\\\"zAu\\\">Foto: Bj\\u00f6rn Kietzmann<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AL0_2017 eptNorm\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Jost Maurin<\\/span><\\/p><p class=\\\"Initial\\\">Das\\u201c, sagt Karsten Smid, Klimaexperte der Umweltorganisation Greenpeace, \\u201eist der Fukushima-Moment der fossilen Energien.\\u201c Er glaubt, oder besser: hofft, dass die durch Starkregen verursachten \\u00dcberschwemmungen mit mehr als 100 Toten zu einem schnellen und konsequenten Ausstieg aus Kohle, \\u00d6l und Gas f\\u00fchren \\u2013 \\u00e4hnlich wie die Reaktorkatastrophe in Japan 2011, die zum Verzicht auf Atomkraft f\\u00fchrte.<\\/p><p class=\\\"meinz\\\">Denn dass der Klimawandel das Wetter st\\u00e4rker ausschlagen l\\u00e4sst, ist in der Wissenschaft weitgehend Konsens. \\u201eSchon vor \\u00fcber 30 Jahren haben Klimamodelle vorhergesagt, dass Extremniederschl\\u00e4ge h\\u00e4ufiger werden, w\\u00e4hrend Tage mit schwachem Regen seltener werden\\u201c, sagt Professor Stefan Rahmstorf vom Potsdam-Institut f\\u00fcr Klimafolgenforschung. \\u201eDas ist eine Folge der Physik: Pro Grad Erw\\u00e4rmung kann die Luft 7 Prozent mehr Wasserdampf aufnehmen und dann auch abregnen.\\u201c Umweltsch\\u00fctzer kritisieren, dass die bisher weltweit vereinbarten Reduktionen des Treibhausgas-Aussto\\u00dfes nicht reichten, um die Erderhitzung auf 1,5 Grad zu begrenzen. Ab dieser Marke sind die Auswirkungen des Klimawandels deutlich gr\\u00f6\\u00dfer.<\\/p><p class=\\\"meinz\\\">Auch Deutschland hat sich Kritikern zufolge bisher zu wenig an die Erderhitzung angepasst. Die konventionellen Ans\\u00e4tze zum Schutz vor extremen Niederschl\\u00e4gen reichten nicht aus, warnt etwa Boris Lehmann, Professor f\\u00fcr Wasserbau und Hydraulik an der Technischen Universit\\u00e4t Darmstadt. \\u201eDas zeigen uns die aktuellen schlimmen Folgen solcher Ereignisse.\\u201c<\\/p><p class=\\\"meinz\\\">Besonders dringend muss etwas in T\\u00e4lern geschehen, die sehr stark von \\u00dcberschwemmungen gef\\u00e4hrdet sind. \\u201eWir m\\u00fcssen in bestimmten St\\u00e4dten in Tallagen Umsiedlungen vornehmen. In der Klimakrise, in der wir stecken, sind manche H\\u00e4user und Ortschaften nicht mehr haltbar\\u201c, warnt Greenpeace-Aktivist Smid. \\u201eWir m\\u00fcssen sie f\\u00fcr unbewohnbar erkl\\u00e4ren.\\u201c<\\/p><p class=\\\"meinz\\\">\\u201eD\\u00f6rfer, St\\u00e4dte und Landschaften sollten wie Schw\\u00e4mme konzipiert werden\\u201c, r\\u00e4t Christian Kuhlicke, Professor am Helmholtz-Zentrum f\\u00fcr Umweltforschung in Leipzig. \\u201eJeder Kubikmeter Wasser, der nicht \\u00fcber die Kanalisation in Fl\\u00fcsse eingeleitet wird, tr\\u00e4gt zur Abflachung von Hochwasserwellen bei.\\u201c Begr\\u00fcnte D\\u00e4cher zum Beispiel k\\u00f6nnten dazu beitragen, aber auch mehr offene, also nicht etwa durch Asphalt versiegelte Fl\\u00e4chen. Au\\u00dferdem mehr B\\u00e4ume, mehr Auen oder mehr Kuhlen in Parks, die Wasser speichern k\\u00f6nnen. M\\u00f6glich seien auch unterirdische Speicherhallen.<\\/p><p class=\\\"meinz\\\">R\\u00fcckstauklappen, druckdichte Fenster und T\\u00fcren sowie Balken vor den Eing\\u00e4ngen k\\u00f6nnten verhindern, dass das Wasser in gef\\u00e4hrdete Geb\\u00e4ude eindringt. \\u201eDie Konzepte liegen vor und der Nutzen solcher Ma\\u00dfnahmen ist belegt. Die Umsetzung allerdings gestaltet sich bisher z\\u00e4h\\u201c, kritisiert Kuhlicke. \\u201eWeder wird gen\\u00fcgend informiert und aufgekl\\u00e4rt, noch wird Geb\\u00e4udeschutz belohnt.\\u201c Er empfiehlt \\u201eein gro\\u00df angelegtes Klimaanpassungsprogramm f\\u00fcr Geb\\u00e4ude\\u201c und klarere Gesetze f\\u00fcr den Hochwasserschutz in St\\u00e4dten.<\\/p><p class=\\\"meinz\\\">Der Bund f\\u00fcr Umwelt und Naturschutz Deutschland (BUND) fordert, dass Auen nicht mehr als \\u00c4cker genutzt werden, damit sich der Wasserr\\u00fcckhalt der B\\u00f6den verbessert. \\u201eAu\\u00dferdem m\\u00fcssen Feuchtgebiete und Moore fl\\u00e4chendeckend erhalten und wiederhergestellt werden.\\u201c So viele Fl\\u00fcsse wie m\\u00f6glich sollen \\u201eentgradigt\\u201c werden, damit gro\\u00dfe Wassermassen in die Auen ausweichen k\\u00f6nnen. Der BUND verlangte eine Milliarde f\\u00fcr mehr Naturschutz, welche die Bundesregierung und die in erster Linie zust\\u00e4ndigen L\\u00e4nderregierungen f\\u00fcr Renaturierungsma\\u00dfnahmen nutzen m\\u00fcssten.<\\/p><p class=\\\"meinz\\\">Die Umweltministerien der besonders von den aktuellen \\u00dcberschwemmungen betroffenen L\\u00e4nder Nordrhein-Westfalen und Rheinland-Pfalz wiesen aber Vorw\\u00fcrfe zur\\u00fcck, sie h\\u00e4tten den Schutz vor <span class=\\\"snippet\\\">Hochwasser<\\/span> vernachl\\u00e4ssigt. Alle verwiesen auf Projekte, die sie schon haben, zum Beispiel zur Erstellung von \\u00f6rtlichen Schutzkonzepten oder zur Renaturierung von Fl\\u00fcssen. Fest steht aber auch, dass sie die Katastrophe dieser Woche nicht verhindern konnten. Bundesumweltministerin Svenja Schulze (SPD) forderte, die Anpassung an den Klimawandel \\u00e4hnlich wie den K\\u00fcstenschutz gesetzlich als Gemeinschaftsaufgabe von Bund und L\\u00e4ndern zu verankern.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a136\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.35.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a136\\/\\\"><h4 class=\\\"AutorName\\\">Jost Maurin<\\/h4><h5 class=\\\"AutorJob\\\">Redakteur f\\u00fcr Wirtschaft und Umwelt<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorTwitter\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/twitter.com\\/JostMaurinTAZ\\\"><img class=\\\"icon\\\" src=\\\"resources\\/microblog_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorFacebook\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/facebook.com\\/jost.maurintaz\\\"><img class=\\\"icon\\\" src=\\\"resources\\/facebook_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Jahrgang 1974. Er schreibt vor allem zu Ern\\u00e4hrungsfragen \\u2013 etwa \\u00fcber Agrarpolitik, Gentechnik, Pestizide, Verbraucherschutz und die Lebensmittelindustrie. 2018, 2017 und 2014 Journalistenpreis \\u201eGr\\u00fcne Reportage\\u201c. 2015 \\u201eBester Zweiter\\u201c beim Deutschen Journalistenpreis. 2013 nominiert f\\u00fcr den \\u201eLangen Atem\\u201c. Bevor er zur taz kam, war er Redakteur bei der Nachrichtenagentur Reuters und Volont\\u00e4r bei der S\\u00fcddeutschen Zeitung.  <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-17.598d674addd99d686a8adbc4c5b59200cb339dcb\",\"article\":{\"articleHtml\":{\"name\":\"art03602359.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626456718\",\"sha256\":\"505068db4a024dcffd88c21174c329eb5a83009cefc60c9cd212ba1017b4dfd0\",\"sSize\":\"8449\"},\"audioFile\":null,\"title\":\"Wie sich Deutschland sch\\u00fctzen kann\",\"teaser\":\"Wissenschaftler und Umweltsch\\u00fctzer fordern Konsequenzen aus dem Hochwasser: Mehr Klimaschutz, die Bewohner mancher besonders gef\\u00e4hrdeter Orte in T\\u00e4lern sollen umsiedeln, Flussauen wiederhergestellt werden\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5782611\",\"pageNames\":[\"s00810213.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094596.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626456717\",\"sha256\":\"6742884667ccac8501b2c1011d3c46e291fa5c4f0124e0ecc3d9c831896cfb5e\",\"sSize\":\"63229\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094596.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626456718\",\"sha256\":\"998f30cb49b8f26146cacf51d4a9d1e99905df33b7878e868d7a9cd603e03ad7\",\"sSize\":\"578231\"}],\"authorList\":[{\"name\":\"Jost Maurin\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.35.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993379\",\"sha256\":\"6614523c4e4938a9231769c4354f8b39e846240bcc2712f56aa564288d5cbf00\",\"sSize\":\"53141\"}}]}},{\"sDate\":\"2021-07-22\",\"snippet\":\"... Helfer brauner sind als das <span class=\\\"snippet\\\">Hochwasser<\\/span> Anpacken und machen, so sieht...\",\"teaser\":\"Andreas Speit Der rechte Rand\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Wo manche Helfer brauner sind als das <span class=\\\"snippet\\\">Hochwasser<\\/span><\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_hamburg ArtikelTyp_AKolumne1_2017 eptKolumne\\\"><h4 class=\\\"Dach\\\"><span class=\\\"zAu\\\">Andreas Speit<\\/span> Der rechte Rand<\\/h4><h2 class=\\\"Titel\\\">Wo manche Helfer  \\nbrauner sind  \\nals das <span class=\\\"snippet\\\">Hochwasser<\\/span><\\/h2><\\/div><div id=\\\"fotos\\\" class=\\\"eptKolumne\\\"><div class=\\\"EinzelFoto\\\"><img alt=\\\"BB=56.693 BH=73.701&#10;IB=378 IH=491&#10;S=0.5 b=330 h= C=Main&#10;id=1095503 typ=TIFF&#10;\\\" src=\\\"Media.1095503.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095503.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeHalf\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeHalf\\\" id=\\\"MediaButze BuMain\\\"><span class=\\\"zAu\\\">Jungsfoto: dpa<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_hamburg ArtikelTyp_AKolumne1_2017 eptKolumne\\\"><p class=\\\"Initial\\\">Anpacken und machen, so sieht es auf den ersten Blick aus: Bei den Hilfsaktionen zur Hochwasserkatastrophe im Westen und S\\u00fcdwesten der Republik reihen sich selbsternannte Helfende aus dem rechten Spektrum ein. Aus der Querdenken-Szene und rechtsextremen Kreisen sind verschiedene Hilfsaktionen gestartet worden. Mit dabei: die Fanbase der Hooligan-Band Kategorie C. In den Sozialen Medien rief der S\\u00e4nger Hannes Ostendorf aus Bremen zur Unterst\\u00fctzung der Betroffen auf. \\u201eWir sagen auch noch mal Danke!\\u201c, schrieb er in einem Telegram-Kanal. Denn sie h\\u00e4tten bereits 800 Hilfspakete zusammen bekommen. Vor Ort w\\u00e4ren ihnen die Sach- und Lebensmittelspenden \\u201eaus der Hand gerissen\\u201c worden, berichtet er und betont, dass jetzt Benzin und Diesel besonders gebraucht w\\u00fcrden.<\\/p><p class=\\\"meinz\\\">Tats\\u00e4chlich finden sich im Netz Aufnahmen von Ostendorf beim Bepacken eines Kastenwagens: Kisten und S\\u00e4cke mit Lebensmitteln und Kleidung. Mehrere M\\u00e4nner packen mit an. Einen langen Transporter f\\u00fcr Tierfutter soll er ebenso organisiert haben. In der Gemeinde Horhausen im Westerwald will er bereits Spenden bei Mitstreitern abgeliefert haben. Ein Video im Telegram-Kanal von Stefan Raven zeigt \\u201eNico\\u201c, der beim Fahren eines Transporters den \\u201eKameraden\\u201c f\\u00fcr die Spenden dankt, und auch gleich bittet, jetzt nicht mehr weiter zu senden: Sie k\\u00f6nnten die Verteilung nicht mehr organisieren. Auch Deutsches Rotes Kreuz und Bundeswehr k\\u00f6nnten gerade keine Spenden mehr aufnehmen, wei\\u00df er, denn sie st\\u00fcnden im Kontakt.<\\/p><p class=\\\"meinz\\\">Die Hooligan-Band hat zudem eine Versteigerungsaktion beworben. 100 Live-Alben w\\u00fcrde sie f\\u00fcr die 100 meist bietenden Angebote abgeben. Aus der rechtsextremen Musik-Szene wird den Betroffenen der Flut auch weitere Unterst\\u00fctzung versprochen, die sicher hilfreich w\\u00e4ren, wenn all den Ank\\u00fcndigungen denn auch tats\\u00e4chlich Taten folgen sollten.<\\/p><img alt=\\\"BB=56.693 BH=73.701&#10;IB=378 IH=491&#10;S=0.5 b=330 h= C=Main&#10;id=1095503 typ=TIFF&#10;\\\" src=\\\"Media.1095503.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095503.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeHalf\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeHalf\\\" id=\\\"MediaButze BuMain\\\"><span class=\\\"zAu\\\">Jungsfoto: dpa<\\/span><\\/p><p class=\\\"meinz\\\">An Versprechen mangelt es jedenfalls nicht, auch wenn die meisten gleich auch ein bisschen mitverdienen wollen: Das rechte Rapper-Projekt \\u201eRapbellions\\u201c, bei dem auch Xavier Naidoo mitwirkt, sammelt Geldspenden ab 50 Euro aufw\\u00e4rts f\\u00fcr Merchandise-Hoodies. Das Chemnitzer Label PC Records bietet an, Spenden im Laden anzunehmen und zu vermitteln. Solche Sachspenden will auch das neue Rechtsrock-Magazin \\u201eRock Hate\\u201c sammeln. Die Band \\u201eGassenraudi\\u201c k\\u00fcndigte an, exklusive Sampler-CDs in Holzboxen zu versteigern, von deren Einnahmen die H\\u00e4lfte an Flutopfer gehen w\\u00fcrden. Das \\u201eidentit\\u00e4re\\u201c Rap-Projekt \\u201eNeuer Deutscher Standard\\u201c (NDS) k\\u00fcndigte an, zwanzig Prozent der Einnahmen eines kommenden Tontr\\u00e4gers an Betroffene weiterzuleiten, berichtet der \\u201eBlick nach rechts\\u201c.<\\/p><p class=\\\"meinz\\\">Die rechten Aktionen haben in Politik und Medien bereits erste Kritik erfahren, auch Warnungen wegen propagandistischer Ausnutzung der Katastrope wurden laut. Raven wettert zur\\u00fcck, zum Beispiel gegen den Nordrhein-Westf\\u00e4lischen Innenminister Herbert Reul (CDU). Der solle nicht \\u00fcber die Gesinnung der Helfer hetzen, Ostendorf wende schlie\\u00dflich \\u201eZeit und eigenes Verm\\u00f6gen auf, um den Leuten zu helfen\\u201c, schreibt er. Auf seiner Webseite prangt gleich oben das Logo von Kategorie C. Seit Jahren nutzen Rechte Katastrophen, um sich als \\u201eK\\u00fcmmerer\\u201c zu inszenieren, zur Gewinnung von Akzeptanz.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a226\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.60.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a226\\/\\\"><h4 class=\\\"AutorName\\\">Andreas Speit<\\/h4><h5 class=\\\"AutorJob\\\">Autor<\\/h5><\\/a><\\/div><\\/div><div class=\\\"AutorDescr\\\"><p class=\\\"meinz\\\"> \\narbeitet als freier Journalist und Autor \\u00fcber die rechte Szene nicht nur in Norddeutschland.<\\/p><\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-22.87300c91db9ed025eae5dfe46b1e3d37700f99d9\",\"article\":{\"articleHtml\":{\"name\":\"art03603453.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626885466\",\"sha256\":\"bd0c7357d80cda66e25c241542d3b8e9f61694541e383b5aa531f4bfbc5103ad\",\"sSize\":\"6823\"},\"audioFile\":null,\"title\":\"Wo manche Helfer brauner sind als das Hochwasser\",\"teaser\":\"Andreas Speit Der rechte Rand\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5781917\",\"pageNames\":[\"s00810568.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095503.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626885466\",\"sha256\":\"6f255bfff8b7ffb70cc3a6e3210c5bafac72160ba556b09abb1309e6f1362b30\",\"sSize\":\"12086\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095503.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626885466\",\"sha256\":\"12680cda651aaa7be0c4619a04f545e21d08adb855e59da559163d11326a0df9\",\"sSize\":\"15307\"}],\"authorList\":[{\"name\":\"Andreas Speit\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.60.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993484\",\"sha256\":\"d2a68667b0bbec17220154fbffc6f427bed7ef4124884599ce3c488403b8c0d4\",\"sSize\":\"58024\"}}]}},{\"sDate\":\"2021-07-19\",\"snippet\":\"... sie keine Warnung vor dem <span class=\\\"snippet\\\">Hochwasser<\\/span> erhalten h\\u00e4tten. Einzig der Wasserbauingenieur... des Krisenstabs mit zahlreichen Ahr-<span class=\\\"snippet\\\">Hochwassern<\\/span> gibt es bei dem gegenw\\u00e4rtigen... tagelangem Dauerregen kam das verheerende <span class=\\\"snippet\\\">Hochwasser<\\/span>, das im engen Ahrtal zu...\",\"teaser\":\"Im romantischen Ahrtal ist nichts mehr wie zuvor. Die H\\u00e4lfte der Geb\\u00e4ude in der Kreisstadt Bad Neuenahr-Ahrweiler sind schwer besch\\u00e4digt. Ein Besuch im Ortsteil Heimersheim\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Mitten in der Nacht kam das Wasser<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><div id=\\\"Zusammenfassung\\\"><div class=\\\"Zusammenfassung\\\"><span class=\\\"Zusammenfassung\\\"><span class=\\\"snippet\\\">hochwasser<\\/span><\\/span><\\/div><\\/div><h2 class=\\\"Titel\\\">Mitten in der Nacht  \\nkam das Wasser<\\/h2><h4 class=\\\"Unterzeile\\\">Im romantischen Ahrtal ist nichts mehr wie zuvor. Die H\\u00e4lfte der Geb\\u00e4ude in der Kreisstadt Bad Neuenahr-Ahrweiler sind schwer besch\\u00e4digt. Ein Besuch im Ortsteil Heimersheim<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptPolitik\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=600.945 BH=420&#10;IB=2306 IH=1537&#10;S=4.5 b=660 h= C=Main&#10;id=1094957 typ=JPEG&#10;\\\" src=\\\"Media.1094957.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1094957.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Aufr\\u00e4umarbeiten in  \\nBad Neuenahr. Mindestens 110\\u00a0Menschen sind im Landkreis Ahrweiler gestorben <span class=\\\"zAu\\\">Foto:  \\nThomas Lohnes\\/getty<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Aus Heimersheim  \\n<span class=\\\"zAu\\\">Annette Hauschild<\\/span><\\/p><p class=\\\"Initial\\\">Schlamm und Staub liegen \\u00fcberall \\u2013 auf Stra\\u00dfen, H\\u00e4usern, G\\u00e4rten, Menschen. Es riecht nach ausgelaufenem Heiz\\u00f6l. Nach tagelangem Dauerregen kam das verheerende <span class=\\\"snippet\\\">Hochwasser<\\/span>, das im engen Ahrtal zu gro\\u00dfen Verw\\u00fcstungen gef\\u00fchrt hat. Das rheinland-pf\\u00e4lzische Heimersheim liegt am Unterlauf der Ahr, kurz vor der M\\u00fcndung in den Rhein.<\\/p><p class=\\\"meinz\\\">1,80 Meter stand das Wasser in dem 3.000-Einwohner-Ortsteil von Bad Neuenahr-Ahrweiler hoch. Die meisten H\\u00e4user sind Einfamilienh\\u00e4user, wei\\u00df get\\u00fcncht, und wo das Wasser stand, ist die Farbe hellbraun. Am Oberlauf der Ahr, wo das Tal enger ist, waren es 5\\u00a0Meter. Aber auch in Heimersheim sind die Sch\\u00e4den immens. Stra\\u00dfen wurden untersp\\u00fclt, die Uferbefestigung wurde weggerissen, die Feuerwehrstation \\u00fcberflutet.<\\/p><p class=\\\"meinz\\\">Auf einer Kreuzung regelt eine junge Polizistin den Verkehr mit Handzeichen. Am Stra\\u00dfenrand liegen durchn\\u00e4sste und mit z\\u00e4hem Schlamm \\u00fcberzogene Haufen, so weit das Auge reicht: Wohnungseinrichtungen, M\\u00f6bel, pers\\u00f6nliche Unterlagen, Fotos und Schulb\\u00fccher. Viele Einwohner sind noch dabei, die Reste ihres Hab und Guts auf den B\\u00fcrgersteig und in die Vorg\\u00e4rten zu hieven und immer wieder Schlamm aus ihren H\\u00e4usern zu schippen. Auch hier sind zwei Menschen ertrunken, eine Frau mit ihrer Tochter, die im Keller noch schnell etwas retten wollten. Das Wasser dr\\u00fcckte gegen die Kellert\\u00fcr, sodass sie diese nicht mehr \\u00f6ffnen konnten.<\\/p><p class=\\\"meinz\\\">Die Menschen sind emsig besch\\u00e4ftigt, alle von oben bis unten voller Schlamm. Freunde, Familienangeh\\u00f6rige, aber auch v\\u00f6llig Fremde haben sich spontan Urlaub genommen, um mit anzupacken. Schlafstellen werden angeboten, und, was den Anwohnern besonders wichtig ist, die M\\u00f6glichkeit, zur Toilette zu gehen und sich nach etlichen Tagen ohne flie\\u00dfendes Wasser zu duschen. Strom gibt es mittlerweile wieder.<\\/p><p class=\\\"meinz\\\">Die private Nachbarschaftshilfe funktioniert: Arbeitgeber stellen ihre Leute frei, damit sie mit anpacken k\\u00f6nnen, Firmen spenden Lebensmittel und Trinkwasser. Ein Lkw mit einer Ladung Dixi-Klos rauscht vorbei, eine Leihgabe eines Bauunternehmers. Bauern schleppen mit ihren Traktoren schwere Ger\\u00e4tschaften und schaffen M\\u00fcll weg. Iranische Fl\\u00fcchtlinge verk\\u00f6stigen ihre Nachbarn mit Riesenmengen Pilaw. Die Feuerwehr vom Frankfurter Flughafen hat 100 Feldbetten an ihre Kollegen in Heimersheim geschickt. Die Stimmung ist ruhig und freundlich.<\\/p><p class=\\\"meinz\\\">Trotzdem sind viele Anwohner w\\u00fctend auf die Politik. Eine Frau um die 40 macht mit ihren T\\u00f6chtern eine Pause von der Plackerei, sitzt auf einem M\\u00e4uerchen, das von ihrem Vorgarten stehengeblieben ist, und raucht eine Zigarette. Man sieht ihr die M\\u00fcdigkeit an. Seit zehn Stunden ist die Familie heute an der Arbeit und es geht noch weiter. Sorgenvoll blickt sie auf die Tr\\u00fcmmer ihrer Existenz. \\u201eKlimakrise\\u201c, meint sie einsilbig, \\u201eeindeutig\\u201c. <\\/p><p class=\\\"meinz\\\">Dann rafft sie sich auf und stapft in ihren Gummistiefeln durch den Schlamm zur\\u00fcck ins Haus, weiter schaufeln. Ihre Nachbarn sehen auch in der Fl\\u00e4chenversiegelung einen Grund f\\u00fcr die Katastrophe. \\u201eEs wird ja alles zugebaut\\u201c, sagt einer.<\\/p><p class=\\\"meinz\\\">Familie Schwarz, Vater und Sohn, erz\\u00e4hlen, was geschehen ist: \\u201eEs hatte tagelang in Str\\u00f6men geregnet. Als der Regen aufh\\u00f6rte, dachten wir, jetzt sei alles vor\\u00fcber und waren beruhigt\\u201c, sagt der junge Mann. \\u201eWir h\\u00f6rten nur so ein komisches Ger\\u00e4usch. Jetzt ist uns klar, dass das vom Fluss kam.\\u201c Um halb zw\\u00f6lf Uhr nachts habe das Wasser schon bis zur Wade hoch auf der Stra\\u00dfe gestanden. \\u201eEs ist erst in den Keller geflossen, dann sehr schnell bis zum ersten Stock gestiegen.\\u201c<\\/p><p class=\\\"meinz\\\">Wenigstens stehen die H\\u00e4user in Heimersheim noch. Am Ober- und Mittellauf der Ahr wurde das Dorf Schuld weitgehend zerst\\u00f6rt. In weiteren Ortschaften sind Geb\\u00e4ude weggerissen worden oder einsturzgef\\u00e4hrdet. Insgesamt 4.000 Menschen wurden evakuiert, 3.000 sind \\u2013 Stand Sonntagmittag \\u2013 nicht erreichbar. In Altenahr stand das Wasser bis zu f\\u00fcnf Metern hoch, die Menschen kletterten zum Teil auf die D\\u00e4cher.<\\/p><p class=\\\"meinz\\\">Hunderte Lkw mit Hilfsg\\u00fctern kommen laut Kreisverwaltung Bad Neuenahr-Ahrweiler, zu der auch Heimersheim geh\\u00f6rt, jeden Tag an. Im Ortsteil Bad Neuenahr selbst sind nach ersten groben Sch\\u00e4tzungen die H\\u00e4lfte aller Geb\\u00e4ude schwer besch\\u00e4digt. Die Bundeswehr hat in Tanklastern Trinkwasser ins Notgebiet geschickt. Alle Nachbarst\\u00e4dte und -gemeinden haben Unterk\\u00fcnfte f\\u00fcr Evakuierte bereitgestellt. Viele Privatleute bieten Schlafpl\\u00e4tze an.<\\/p><p class=\\\"meinz\\\">W\\u00e4hrend am Unterlauf der Ahr aufger\\u00e4umt wird, sucht man am Mittel- und Oberlauf mit Fu\\u00dftrupps und Drohnen weiter nach Vermissten. Immer wieder werden dabei auch Tote gefunden. Allein im Landkreis Ahrweiler in Rheinland-Pfalz haben mindestens 110\\u00a0Menschen ihr Leben verloren. Bundeswehr und THW r\\u00e4umen mit schweren R\\u00e4umger\\u00e4ten die Zufahrtsstra\\u00dfen frei. Einige Orte sind nur per Hubschrauber oder zu Fu\\u00df erreichbar. Strom, Wasser und Mobilfunk gibt es dort nicht mehr.<\\/p><p class=\\\"meinz\\\">Am Sonntag verschaffte sich Bundeskanzlerin Angela Merkel (CDU) begleitet von der rheinland-pf\\u00e4lzischen Ministerpr\\u00e4sidentin Malu Dreyer (SPD) einen Eindruck von der Situation im Krisengebiet. <\\/p><p class=\\\"meinz\\\">F\\u00fcr kommenden Dienstag wird die rheinland-pf\\u00e4lzische gr\\u00fcne Umweltministerin Anne Spiegel im Krisengebiet erwartet. Spiegel, so erkl\\u00e4rte eine Bad Neuenahrerin, wolle erst sp\\u00e4ter kommen, \\u201eum den Hilfskr\\u00e4ften nicht im Weg zu stehen\\u201c. Im Mai diesen Jahres erinnerte der Kreis Ahrweiler noch an das schwere <span class=\\\"snippet\\\">Hochwasser<\\/span> des Jahres 2016, das bisher als \\u201eJahrhunderthochwasser\\u201c galt.<\\/p><p class=\\\"meinz\\\">Trotz der Erfahrungen des Krisenstabs mit zahlreichen Ahr-<span class=\\\"snippet\\\">Hochwassern<\\/span> gibt es bei dem gegenw\\u00e4rtigen Einsatz Probleme in der Kommunikation mit den B\\u00fcrgern. Heimersheimer beklagen, dass sie keine Warnung vor dem <span class=\\\"snippet\\\">Hochwasser<\\/span> erhalten h\\u00e4tten. <\\/p><p class=\\\"meinz\\\">Einzig der Wasserbauingenieur Matthias Bertram berichtete der taz am Telefon, dass er regelm\\u00e4\\u00dfig auf den Pegelstand gesehen habe. \\u201eDer Pegelstand in Altenahr kommt eine Dreiviertelstunde sp\\u00e4ter in Bad Neuenahr an\\u201c, erkl\\u00e4rte der Fachmann. Bis halb zehn Uhr abends habe seine Familie am Mittwoch Sands\\u00e4cke gef\\u00fcllt, um die Hauseing\\u00e4nge abzudichten. Um halb zw\\u00f6lf habe das Wasser schon auf der Stra\\u00dfe gestanden.<\\/p><p class=\\\"oeinz\\\"><span class=\\\"Seitennummer\\\">meinung + diskussion 12<\\/span><\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a6635\\/\\\"\\/><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a6635\\/\\\"><h4 class=\\\"AutorName\\\">Annette Hauschild<\\/h4><\\/a><\\/div><\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-19.bf90f4dc871113515d240519d33a0ee36f04fd76\",\"article\":{\"articleHtml\":{\"name\":\"art03602719.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626625247\",\"sha256\":\"260ac9f6b2d8abc1066f613d7ea23d2483e11eac9e5d43511a9cc1fedf032bae\",\"sSize\":\"9784\"},\"audioFile\":null,\"title\":\"Mitten in der Nacht kam das Wasser\",\"teaser\":\"Im romantischen Ahrtal ist nichts mehr wie zuvor. Die H\\u00e4lfte der Geb\\u00e4ude in der Kreisstadt Bad Neuenahr-Ahrweiler sind schwer besch\\u00e4digt. Ein Besuch im Ortsteil Heimersheim\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5781570\",\"pageNames\":[\"s00810387.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094957.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626625245\",\"sha256\":\"9ef9dc9e6fd9c9f2ed4cab384606ee492c9c934a652d11edcf57c665a331ddd0\",\"sSize\":\"60613\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094957.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626625247\",\"sha256\":\"c502cd0d86c5d62f67441992774120d533affdce7f424e3db2630cce87b5610a\",\"sSize\":\"399238\"}],\"authorList\":null}},{\"sDate\":\"2021-07-23\",\"snippet\":\"... dieselben Spr\\u00fcche, die nach jedem <span class=\\\"snippet\\\">Hochwasser<\\/span> und nach jeder Katastrophe kommen... leben, noch nie einen Tropfen <span class=\\\"snippet\\\">Hochwasser<\\/span> im Keller. Wir haben zuerst... h\\u00e4tte allenfalls f\\u00fcr ein leichtes <span class=\\\"snippet\\\">Hochwasser<\\/span> gereicht. Es war die Flutwelle...\",\"teaser\":\"Seit 30 Jahren versucht der Biowinzer Christoph B\\u00e4cker aus dem Ahrtal, mit der Natur zu arbeiten. Nun hat sie sein Weingut zerst\\u00f6rt. Wie es weitergeht, wei\\u00df er noch nicht\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>\\u201eIch kann der Natur nicht b\\u00f6se sein\\u201c<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><div id=\\\"Zusammenfassung\\\"><div class=\\\"Zusammenfassung\\\"><span class=\\\"Zusammenfassung\\\">extremwetter<\\/span><\\/div><\\/div><h2 class=\\\"Titel\\\">\\u201eIch kann der Natur  \\nnicht b\\u00f6se sein\\u201c<\\/h2><h4 class=\\\"Unterzeile\\\">Seit 30 Jahren versucht der Biowinzer Christoph B\\u00e4cker aus dem Ahrtal, mit der Natur  \\nzu arbeiten. Nun hat sie sein Weingut zerst\\u00f6rt. Wie es weitergeht, wei\\u00df er noch nicht<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptPolitik\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=464.882 BH=303.034&#10;IB=1614 IH=1075&#10;S=3.5 b=660 h= C=Main&#10;id=1095629 typ=JPEG&#10;\\\" src=\\\"Media.1095629.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095629.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Marienthal im Ahrtal nach der Flut, nur wenige Kilometer von Bad Neuenahr-Ahrweiler entfernt   <span class=\\\"zAu\\\">Foto: dpa<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_A2_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Interview <span class=\\\"zAu\\\">Manfred Kriener<\\/span><\\/p><p class=\\\"oeinz\\\"><strong>taz: Herr B\\u00e4cker, Sie sind Biowinzer in Ahrweiler, mitten im Katastrophengebiet. Wann haben Sie gemerkt, dass Unheil droht?<\\/strong><\\/p><p class=\\\"oeinz\\\"><strong>Christoph B\\u00e4cker:<\\/strong> Das hat gedauert. Bei uns hat es n\\u00e4mlich gar nicht so f\\u00fcrchterlich geregnet. Die Regenmenge h\\u00e4tte allenfalls f\\u00fcr ein leichtes <span class=\\\"snippet\\\">Hochwasser<\\/span> gereicht. Es war die Flutwelle, die von der oberen Ahr kam, die Gefahr war schwer einzusch\\u00e4tzen. Ich habe im Fernsehen die Aussagen des Wetterdienstes verfolgt, aber diese Dramatik war nicht vorherzusehen. Richtig ernst wurde es erst am Abend, kurz vor dem Dunkelwerden. Der Pegel stieg rasend schnell, man konnte zugucken, wie ganze Uferbereiche im Wasser verschwanden.<\\/p><p class=\\\"meinz\\\"><strong>Sind Sie in Panik geraten?<\\/strong><\\/p><p class=\\\"meinz\\\">Nein. Wir hatten in unserem Betrieb, obwohl wir im Risikogebiet leben, noch nie einen Tropfen <span class=\\\"snippet\\\">Hochwasser<\\/span> im Keller. Wir haben zuerst unsere Fahrzeuge in Sicherheit gebracht. Als das Wasser weiter stieg und \\u00fcber die Stra\\u00dfe in unser Haus floss, haben wir die wichtigsten Unterlagen zusammengepackt: Impfausweis, wichtige Dokumente, Unterlagen f\\u00fcr die Buchf\\u00fchrung. Das haben wir in die obere Etage gerettet.<\\/p><p class=\\\"meinz\\\"><strong>Viele Weing\\u00fcter sind zerst\\u00f6rt. Was ist von Ihrem \\u00fcbrig?<\\/strong><\\/p><p class=\\\"meinz\\\">Nicht viel. Die abgef\\u00fcllten Flaschen sind okay, die m\\u00fcssen wir nur neu etikettieren. Aber vor allem die Maschinen sind hin\\u00fcber, der Gabelstapler, die Kelter, die Traubenm\\u00fchle, die Pumpen, aber auch die Barriquef\\u00e4sser, die gro\\u00dfen Holzf\\u00e4sser. Und wir haben kein Gas, kein Wasser, kein Strom, kein Internet. Nat\\u00fcrlich machen mir auch die Weinberge gro\\u00dfe Sorgen, die ich nicht vor den jetzt um sich greifenden Pilzkrankheiten sch\\u00fctzen kann.<\\/p><p class=\\\"meinz\\\"><strong>Wegen der Feuchtigkeit im Ahrtal breitet sich der falsche Mehltau aus. Jetzt werden aus Hubschraubern Fungizide gespr\\u00fcht, um den Jahrgang 2021 zu retten\\u202f\\u2026<\\/strong><\\/p><p class=\\\"meinz\\\">\\u2026\\u202fmeine eigenen Biofl\\u00e4chen werden wohl auch bespr\\u00fcht, dagegen kann ich mich nicht wehren. Es ist vermutlich die einzige M\\u00f6glichkeit, damit wir im Herbst \\u00fcberhaupt etwas ernten k\\u00f6nnen. Am Montag kommen Kollegen von der Mosel, die mir hoffentlich helfen, meine Weinberge \\u00f6kologisch korrekt \\u00fcbers Jahr zu bringen.<\\/p><p class=\\\"meinz\\\"><strong>Es gibt vielleicht keinen richtigen Biowein, aber wenigstens eine Ernte?<\\/strong><\\/p><p class=\\\"meinz\\\">Ich hoffe auf eine Sondergenehmigung f\\u00fcr meine Bioweine. Diese Katastrophe ist nun wirklich ein H\\u00e4rtefall, den ich nicht verschuldet habe.<\\/p><p class=\\\"meinz\\\"><strong>K\\u00f6nnen Sie den 2021er Jahrgang bei befreundeten Winzern ausbauen?<\\/strong><\\/p><p class=\\\"meinz\\\">Das wird schwierig. Da m\\u00fcsste ich die Trauben an die Mosel fahren. Die meisten Weing\\u00fcter im Ahrtal liegen direkt am Fluss, nur ein einziges auf dem Berg. Die hat es voll erwischt. Wir brauchen jetzt Ersatzmaschinen und Lagertanks, dann m\\u00fcssen wir hoffen, dass es \\u00fcberhaupt noch etwas zu ernten gibt.<\\/p><p class=\\\"meinz\\\"><strong>Sie sind seit 1990 Biowinzer, sie waren der Erste an der Ahr, Sie haben sich mit der Natur verb\\u00fcndet. Jetzt hat die Natur ihre Existenz attackiert. War alles Engagement vergeblich?<\\/strong><\\/p><p class=\\\"meinz\\\">Ich kann der Natur nicht b\\u00f6se sein. Die Natur ist genau so, wie die Menschheit sie zugerichtet hat. Wir m\\u00fcssen jetzt die \\u00c4rmel hochkrempeln und weitermachen, auch wenn es schwerf\\u00e4llt. Ich habe famili\\u00e4re Unterst\\u00fctzung, bin einigerma\\u00dfen abgesichert, selbst wenn dies der Todessto\\u00df f\\u00fcr meinen Betrieb war.<\\/p><p class=\\\"meinz\\\"><strong>Kann Ihr Weingut ohne Abrissbirne wieder instandgesetzt werden?<\\/strong><\\/p><p class=\\\"meinz\\\">Ich hoffe, es geht ohne Abrissbirne, aber ich wei\\u00df nicht, ob der Betrieb am Leben bleibt. Werden sich die Weinberge erholen? Gibt es eine Ernte? Wann kann ich wieder Wein verkaufen? Wann kommen wieder Besucher ins Ahrtal? Es wird viele Monate dauern, bis die Sch\\u00e4den halbwegs beseitigt sind. Diese Zeit zu \\u00fcberbr\\u00fccken, das kann ich mir gegenw\\u00e4rtig nicht vorstellen.<\\/p><p class=\\\"meinz\\\"><strong>Wie sieht es aktuell mit den Grundbed\\u00fcrfnissen aus: essen, trinken, schlafen, duschen, Toilette benutzen?<\\/strong><\\/p><p class=\\\"meinz\\\">Wir sind ganz in der N\\u00e4he in einer Schule untergekommen. Sp\\u00e4ter k\\u00f6nnen wir vielleicht bei Freunden unterschl\\u00fcpfen. Unser eigenes Haus ist hoffentlich nicht unbewohnbar, da m\\u00fcssen jetzt die Statiker ran. Zum Gl\\u00fcck liegen K\\u00fcche und Schlafzimmer im oberen Geschoss und sind unversehrt. Aber wenn ich in den Weinkeller gehe, dann kommen mir die Tr\\u00e4nen.<\\/p><p class=\\\"meinz\\\"><strong>Wie geht es Ihnen, wenn Sie Herrn Laschet zuh\\u00f6ren, der hier durch den Schlamm marschiert und sich als Klimasch\\u00fctzer inszeniert?<\\/strong><\\/p><p class=\\\"meinz\\\">Tut mir leid, aber das ist einfach l\\u00e4cherlich. Das sind dieselben Spr\\u00fcche, die nach jedem <span class=\\\"snippet\\\">Hochwasser<\\/span> und nach jeder Katastrophe kommen. Die Lehren, die wir jetzt ziehen sollten \\u2013 da passiert nichts. Der Kohleausstieg wird irgendwann kommen, viel zu sp\\u00e4t. Das macht einen nur noch w\\u00fctend.<\\/p><p class=\\\"meinz\\\"><strong>Die Solidarit\\u00e4t ist gewaltig, auch aus dem Ausland kommen viele Hilfsangebote. Halten die Winzer in der Krise zusammen?<\\/strong><\\/p><p class=\\\"meinz\\\">Ich glaube schon, dass diese Katastrophe die Winzer zusammenschwei\\u00dft. Meine Sorge ist allerdings, dass die Solidarit\\u00e4tswelle schnell wieder verebbt. Wir brauchen Hilfe \\u00fcber einen langen Zeitraum.<\\/p><p class=\\\"meinz\\\"><strong>Was k\\u00f6nnen unsere Leser:innen den betroffenen Weing\\u00fctern Gutes tun?<\\/strong><\\/p><p class=\\\"meinz\\\">Bioweine von der Ahr trinken! Das w\\u00e4re die gr\\u00f6\\u00dfte Unterst\\u00fctzung. Das ist noch befriedigender als finanzielle Hilfe.<\\/p><div class=\\\"Shorty BeitragsArt_taz_print_biokasten ArtikelTyp_Bio_2017 eptShorty\\\"><img alt=\\\"BB=56.693 BH=73.701&#10;IB=315 IH=410&#10;S=0.5 b= h=410 C=Bio&#10;id=1095630 typ=JPEG&#10;\\\" src=\\\"Media.1095630.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095630.norm.jpg\')\\\" class=\\\"MediaImg ImgBio ImgSize192\\\"\\/><p class=\\\"MediaButze BuBio ImgSize192\\\"><span class=\\\"zAu\\\">Foto: MWVLW RP<\\/span><\\/p><div class=\\\"BioText\\\"><p class=\\\"ShortyTitel\\\">Christoph B\\u00e4cker<\\/p><p class=\\\"meinz\\\"> Jahrgang 1961, f\\u00fchrt  \\nein kleines Familien-Weingut in Ahrweiler. www.weingutbaecker.de<\\/p><\\/div><div class=\\\"clear\\\"> <\\/div><\\/div><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a3726\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.256.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a3726\\/\\\"><h4 class=\\\"AutorName\\\">Manfred Kriener<\\/h4><\\/a><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Manfred Kriener, Jahrgang 1953, ist Umweltjournalist und Autor in Berlin. Themenschwerpunkte: Klima, Umwelt, Landwirtschaft sowie Essen &amp; Trinken. Kriener war elf Jahre lang taz-\\u00d6kologieredakteur, danach Gr\\u00fcndungschefredakteur des Slow-Food-Magazins und des Umweltmagazins zeozwei.. Zuletzt erschienen: \\u201eLeckerland ist abgebrannt \\u2013 Ern\\u00e4hrungsl\\u00fcgen und der rasante Wandel der Esskultur\\u201c. Das Buch schaffte es in die Spiegel-Bestsellerliste und wurde von Umweltministerin Svenja Schulze in der taz vorgestellt. Kriener arbeitet im Journalistenb\\u00fcro www.textetage.com in Kreuzberg.\\n\\n <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-23.cfddd69cfbd368b85c477798630ffbb457abf6d8\",\"article\":{\"articleHtml\":{\"name\":\"art03603651.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626975498\",\"sha256\":\"5cdaff6ef8b85ae20bc97c86b7de11644ef674dbc001238cfa149405237f81e0\",\"sSize\":\"10253\"},\"audioFile\":null,\"title\":\"\\u201eIch kann der Natur nicht b\\u00f6se sein\\u201c\",\"teaser\":\"Seit 30 Jahren versucht der Biowinzer Christoph B\\u00e4cker aus dem Ahrtal, mit der Natur zu arbeiten. Nun hat sie sein Weingut zerst\\u00f6rt. Wie es weitergeht, wei\\u00df er noch nicht\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5785154\",\"pageNames\":[\"s00810618.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095629.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626975497\",\"sha256\":\"f7b6eca2f6e67c49ac962235c0a31beb8493a73e69094356c79360f8695e204e\",\"sSize\":\"70976\"},{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095630.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626975498\",\"sha256\":\"3414934380064eeb7669e342c2717b13865068722062809c811e5e45345064b2\",\"sSize\":\"10320\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095629.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626975498\",\"sha256\":\"13749f84c87e7460dc562fcaf42e14754ce0eac8b237552a977ffffc8c0c910f\",\"sSize\":\"498447\"}],\"authorList\":[{\"name\":\"Manfred Kriener\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.256.png\",\"storageType\":\"global\",\"sMoTime\":\"1614364224\",\"sha256\":\"72da02c7c4bb356d801c210dd80abbda91a14fe440b15fb972b7c5e2e14fcaf3\",\"sSize\":\"68475\"}}]}},{\"sDate\":\"2021-07-23\",\"snippet\":\"... erfolgreichen Amtszeit zeigt gerade die <span class=\\\"snippet\\\">Hochwasser<\\/span>-Katastrophe wieder einmal, wie machtlos... ist der Nato-Gipfel. Beim <span class=\\\"snippet\\\">Hochwasser<\\/span> in Rheinland-Pfalz und Nordrhein... sich die sch\\u00e4umende Wupper bei <span class=\\\"snippet\\\">Hochwasser<\\/span>. Der Fluss rauscht so laut...\",\"teaser\":\"Svenja Schulze ist die erfolgreichste Umweltministerin, die Deutschland je hatte. Dennoch geht es der Umwelt schlecht. Die j\\u00fcngsten \\u00dcberschwemmungen zeigen, wie wenig ihr Amt ausrichten kann\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Die Macht der Machtlosen<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AMonoA_2017 eptGrossWerktags\\\"><h2 class=\\\"Titel\\\">Die Macht der Machtlosen<\\/h2><h4 class=\\\"Unterzeile\\\">Svenja Schulze ist die erfolgreichste Umweltministerin, die Deutschland je hatte. Dennoch geht es der Umwelt schlecht. Die j\\u00fcngsten \\u00dcberschwemmungen zeigen,  \\nwie wenig ihr Amt ausrichten kann<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptGrossWerktags\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=649.134 BH=884.767&#10;IB=2209 IH=3313&#10;S=4.9 b=660 h= C=Main&#10;id=1095631 typ=JPEG&#10;\\\" src=\\\"Media.1095631.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095631.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Viel erreicht, aber wenig bekannt: Umweltministerin Svenja Schulze zu Besuch im Thyssenkrupp-Werk am 28. Juni 2021   <span class=\\\"zAu\\\">Foto: Marcel Kusch\\/dpa<\\/span><\\/p><\\/div><div class=\\\"EinzelFoto\\\"><img alt=\\\"BB=515.906 BH=350.551&#10;IB=1787 IH=1191&#10;S=3.9 b=660 h= C=Multi&#10;id=1095632 typ=JPEG&#10;\\\" src=\\\"Media.1095632.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095632.norm.jpg\')\\\" class=\\\"MediaImg ImgMulti ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMulti ImgSizeFull\\\">Solingen, 16. Juli 2021: Schulze im Gespr\\u00e4ch mit Helfern des Technischen Hilfswerks. Sie verschafft sich einen \\u00dcberblick \\u00fcber die Verw\\u00fcstungen, die das <span class=\\\"snippet\\\">Hochwasser<\\/span> der Wupper auf einem Campingplatz angerichtet hat. <span class=\\\"zAu\\\">Foto: Roberto Pfeil\\/dpa<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AMonoA_2017 eptGrossWerktags\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Bernhard P\\u00f6tter<\\/span><\\/p><p class=\\\"Initial\\\">Am 16. Juli steht Svenja Schulze in roter Regenjacke auf einer Br\\u00fccke in Solingen. Vor sich die Mikrofone der Kameras, hinter sich die sch\\u00e4umende Wupper bei <span class=\\\"snippet\\\">Hochwasser<\\/span>. Der Fluss rauscht so laut, dass man die Bundesumweltministerin nur schwer versteht: \\u201eDas ist eine nationale Trag\\u00f6die, die wir national beantworten m\\u00fcssen\\u201c, sagt sie, sichtlich ersch\\u00fcttert. Jetzt brauche es Geld und Zusammenarbeit f\\u00fcr den Wiederaufbau und mehr Anstrengungen f\\u00fcr bessere Anpassung und Klimaschutz. \\u201eWer es immer noch nicht geglaubt hat, jetzt kann man es noch mal sehr deutlich sehen\\u201c, sagt Schulze: \\u201eWir werden mehr tun m\\u00fcssen.\\u201c<\\/p><p class=\\\"meinz\\\">Schulzes Themen haben gerade Oberwasser: Klimawandel, Anpassung, \\u00d6ko-Energien, Stadtumbau, Fluss- und Wassermanagement. Fast genau einen Monat vorher sah das anders aus.<\\/p><p class=\\\"meinz\\\">Mitte Juni sitzt Schulze in Berlin vor fast leeren B\\u00e4nken der Bundespressekonferenz. Sie pr\\u00e4sentiert die \\u201eKlimawirkungs- und Risikoanalyse\\u201c des Bundes: Deutschland hat sich seit 1881 im Schnitt um 1,6 Grad erw\\u00e4rmt, es drohen Hitze, D\\u00fcrre, Starkregen und Wassermangel, die Risiken nehmen zu. Was tun? Mehr Raum f\\u00fcr die Fl\\u00fcsse, mehr Gr\\u00fcn in den St\\u00e4dten, mehr Klimaschutz. Schulze sagt: \\u201eEs muss schnell gehen, denn viele Ma\\u00dfnahmen brauchen Zeit, bis sie wirken.\\u201c In der \\u201eTagesschau\\u201c finden sich dazu nach zehn Minuten Sendung drei S\\u00e4tze. Gro\\u00dfes Thema ist der Nato-Gipfel.<\\/p><p class=\\\"meinz\\\">Beim <span class=\\\"snippet\\\">Hochwasser<\\/span> in Rheinland-Pfalz und Nordrhein-Westfalen tut Schulze, was sie kann: reden, erinnern, mahnen. Aber entschieden wird anderswo: Katastrophenschutz ist L\\u00e4ndersache, die Bauplanung liegt bei den Kommunen, Fl\\u00fcsse ausbaggern lassen die Verkehrspolitiker. Zum Ende einer eigentlich erfolgreichen Amtszeit zeigt gerade die <span class=\\\"snippet\\\">Hochwasser<\\/span>-Katastrophe wieder einmal, wie machtlos eine Umweltministerin ist.<\\/p><p class=\\\"meinz\\\">Zwei Wochen vor der Flut steht die Ministerin bei Dietmar Veith auf der Wiese. \\u201eTu Gutes, aber sorg auch f\\u00fcr eine schwarze Null\\u201c, sagt der Biobauer mit den grauen Haaren, in Fleecejacke und Wanderhose, auf Gut Hasselholz bei Aachen. Er zeigt \\u00fcber seine sattgr\\u00fcne Wiese, wo kniehoch die Gr\\u00e4ser und Kr\\u00e4uter stehen und die Schwalben im Tiefflug kurven. Er erkl\\u00e4rt, wie Pflanzenarten sich vom Acker machen, aber wiederkommen, wenn man \\u201eder Natur auf die Spr\\u00fcnge hilft\\u201c, und zeigt auf ein Geh\\u00f6lz, wo wieder Haselm\\u00e4use eingezogen sind. Er wei\\u00df auch nicht, wie die da hin gekommen sind. \\u201eMan muss mit der Natur Geduld haben\\u201c, sagt der Landwirt. \\u201eAber noch mehr Geduld braucht man mit den Landwirtschaftskammern.\\u201c<\\/p><p class=\\\"meinz\\\">Svenja Schulze lacht immer noch unter ihrer roten FFP2-Maske, als sie nach dem Termin wieder in ihren Reisebus einsteigt. Geduld mit der Natur, aber vor allem mit den z\\u00e4hen Widerst\\u00e4nden in Politik und B\\u00fcrokratie, das hat ihr gefallen. Biobauer Veith und Ministerin Schulze haben noch etwas gemeinsam: Veith \\u00fcbergibt am n\\u00e4chsten Tag den Hof an seinen Sohn. Und Svenja Schulze wird wohl im Herbst nicht mehr Ministerin sein. Bisher geben das jedenfalls die Umfragen f\\u00fcr ihre SPD nicht her. Seit 1988 ist sie in der Partei.<\\/p><p class=\\\"meinz\\\">Deshalb ist diese Sommerreise an zwei hei\\u00dfen Junitagen auch so etwas wie eine Abschiedstournee f\\u00fcr die 52-J\\u00e4hrige, die in M\\u00fcnster ihren Wahlkreis hat und f\\u00fcr die SPD bei den Bundestagswahlen antritt. Schulzes Amtszeit als Ministerin f\\u00fcr Umwelt, Naturschutz und nukleare Sicherheit war k\\u00fcrzer als sonst, weil die Koalition so sp\\u00e4t stand, daf\\u00fcr aber turbulent. Schulze wollte eigentlich einfach den Koalitionsvertrag abarbeiten. Aber dann kamen D\\u00fcrresommer, Insektensterben, \\u201eFridays for Future\\u201c und ein Donnerschlag vom Bundesverfassungsgericht. Pl\\u00f6tzlich erschien m\\u00f6glich, was vorher undenkbar war.<\\/p><p class=\\\"meinz\\\">Die dreieinhalb Jahre Schulze zeigen, welche Fortschritte m\\u00f6glich sind, wenn Themen wichtig werden, der Druck von au\\u00dfen hoch bleibt und die Verantwortlichen diese Chancen ergreifen. Sie zeigen aber auch die Grenzen des Umweltministeriums, wie es bis jetzt definiert wurde. Und sie lassen hoffen, dass Schulzes Amtszeit daran ein bisschen was ge\\u00e4ndert hat. Die Ministerin will sich selbst keine Schulnote geben, ist aber \\u201esehr zufrieden mit den Ergebnissen\\u201c. Selbst Kritiker gestehen ihr zu: Schulze ist vermutlich die erfolgreichste von bislang neun BundesumweltministerInnen.<\\/p><p class=\\\"meinz\\\">Dabei musste sie gleich am Anfang ihrer Amtszeit  eingestehen, dass Deutschland sein Klimaziel f\\u00fcr 2020 (minus 40 Prozent weniger Emissionen gegen\\u00fcber 1990) nicht schaffen werde. Aufgrund von Corona, neuen Ma\\u00dfnahmen und mithilfe von viel Geld wurden dann doch minus 42 Prozent erreicht. Schulze, studierte Politikwissenschaftlerin und Gemanistin, bekam einen Crash-Kurs in Umweltpolitik, lernte schnell und leistete sich bei Detailfragen trotzdem hier und da ein paar Stolpereien. Ihr Vorteil: Sie wird oft untersch\\u00e4tzt. Und diese Mischung aus Gl\\u00fcck und Geschick blieb ihr treu.<\\/p><p class=\\\"meinz\\\">Zum Beispiel bei den Insekten. Auf ihrer ersten Sommerreise 2018 besuchte sie den Entomologischen Verein Krefeld, der einen erschreckenden R\\u00fcckgang bei Insekten nachgewiesen hatte. Schulze staunte \\u00fcber die Tausenden von archivierten K\\u00e4fern, Schmetterlingen, Motten, Wanzen und Larven in den dunklen Holzregalen der ForscherInnen.<\\/p><p class=\\\"meinz\\\">Noch mehr staunten alle, welche Wucht das Thema pl\\u00f6tzlich entfaltete: In Bayern zwangen 1,7 Millionen Wahlberechtigte mit einem Volksbegehren zur Artenvielfalt die CSU zum Kurswechsel. Schulzes Umweltministerium erkannte seine Chance: Aus einem bel\\u00e4chelten Thema f\\u00fcr Spinnen und Spinner wurde eine Kampagne, die im Sommer 2021 mit einem Paket zum Insektenschutz endete: mehr Geld, mehr Schutzgebiete, ab 2024 Verbot des umk\\u00e4mpften Pestizids Glyphosat.<\\/p><p class=\\\"meinz\\\">\\u201eDamals hat es angefangen, dass Artenvielfalt zum breiten Thema wurde\\u201c, sagt Schulze auf dem Hof von Bauer Veith. \\u201eInzwischen haben wir alle begriffen: Artenschutz f\\u00e4ngt bei uns vor der Haust\\u00fcr an.\\u201c F\\u00fcr die geb\\u00fcrtige Neusserin ist der Besuch auf dem Hof ein Wahlkampf-Wohlf\\u00fchltermin in ihrem Bundesland, wo sie viele Jahre auch Wissenschaftsministerin war. Damit die mitreisenden JournalistInnen auch wirklich mitbekommen, wie erfolgreich die Umweltministerin war, hat ihr Stab eine umfassende Bilanz verteilt: 24 eng bedruckte Seiten mit den Erfolgen der Svenja Schulze. Klimaschutzgesetz, Kohleausstieg, Wasserstoff-Strategie, \\u00d6ko-Fokus f\\u00fcr Coronahilfen, CO<sub>2<\\/sub>-Preis, Finanzspritzen f\\u00fcr Unternehmen und E-Autos, Verbot von Plastikt\\u00fcten. Es h\\u00f6rt gar nicht auf.<\\/p><p class=\\\"meinz\\\">Ist Schulze, die bestgelaunte Bundesumweltministerin aller Zeiten, auch die beste? Olaf Bandt lacht, wenn man ihm am Telefon diese Frage stellt. F\\u00fcr den Chef des einflussreichen Umweltverbands BUND erreicht die Umweltministerin immer zu wenig. Dann sagt er: \\u201eEs stimmt, Schulzes Amtszeit hat sicher die gr\\u00f6\\u00dften Fortschritte f\\u00fcr die Umweltpolitik in Deutschland gebracht. Und sie hatte dabei immer die Finger im Spiel.\\u201c<\\/p><p class=\\\"meinz\\\">Bandt erinnert aber auch daran, was alles noch fehlt: \\u201eDie deutschen und EU-Ziele zur Artenvielfalt verfehlen wir.\\u201c Der Kohleausstieg m\\u00fcsse schneller vonstatten gehen, die erneuerbaren Energien m\\u00fcssten viel schneller ausgebaut werden, im Verkehr d\\u00fcrfe die Regierung nicht weiter die Probleme aussitzen. Er prognostiziert, dass in Zukunft auf deutsche Gerichte mehr Klagen zukommen. \\u201eUnsere Anw\\u00e4lte sind da \\u00fcberall dran.\\u201c<\\/p><img alt=\\\"BB=515.906 BH=350.551&#10;IB=1787 IH=1191&#10;S=3.9 b=660 h= C=Multi&#10;id=1095632 typ=JPEG&#10;\\\" src=\\\"Media.1095632.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095632.norm.jpg\')\\\" class=\\\"MediaImg ImgMulti ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMulti ImgSizeFull\\\">Solingen, 16. Juli 2021: Schulze im Gespr\\u00e4ch mit Helfern des Technischen Hilfswerks. Sie verschafft sich einen \\u00dcberblick \\u00fcber die Verw\\u00fcstungen, die das <span class=\\\"snippet\\\">Hochwasser<\\/span> der Wupper auf einem Campingplatz angerichtet hat. <span class=\\\"zAu\\\">Foto: Roberto Pfeil\\/dpa<\\/span><\\/p><p class=\\\"meinz\\\">Tats\\u00e4chlich verfehlt Deutschland weiterhin viele seiner \\u00d6ko-Ziele bei Wasser- und Luftqualit\\u00e4t, beim Artenschutz und Fl\\u00e4chenfra\\u00df. Wie kann das sein: Die Umweltministerin ist erfolgreich, aber der Umwelt geht es weiter schlecht? Die Antwort: Viele der wichtigen Themen werden nicht in Schulzes Ressort entschieden.<\\/p><p class=\\\"meinz\\\">Und so ist ihre Abschiedstour auch kein Triumphzug. Gleich zu Beginn steht Schulze mit dem Vorstandschef von Thyssenkrupp Stahl in Duisburg auf einer gr\\u00fcnen Wiese. Hinter ihnen zwei gigantische Hoch\\u00f6fen, die leise schnaufen. Die Stahlwerker, verantwortlich f\\u00fcr zwei Prozent aller deutschen Klimagase, wollen klimaneutral auf \\u00d6ko-Wasserstoff umr\\u00fcsten. Als Schulze ihr Amt antrat, war davon noch nicht gro\\u00df die Rede. Heute will die Industrie sich wandeln, braucht daf\\u00fcr aber Milliardenhilfen. Und, ach ja, 3.000 neue Windr\\u00e4der f\\u00fcr die Herstellung ihres gr\\u00fcnen Wasserstoffs werden auch ben\\u00f6tigt. Schulze \\u00fcbergibt feierlich zwei Papierb\\u00f6gen mit dem Bundesadler: Darauf steht, dass das Unternehmen jetzt mal einen ordentlichen Antrag auf Staatshilfen stellen kann. Der Stahlchef schenkt der Ministerin ein etwa zehn Zentimeter kurzes St\\u00fcck \\u201enachhaltigen\\u201c Stahl.<\\/p><p class=\\\"meinz\\\">Eher symbolisch ist hierbei die Rolle des Umweltministeriums: Ob es Milliarden f\\u00fcr den gr\\u00fcnen Stahl gibt, entscheiden Wirtschafts-, Finanzminister und die EU-Kommission. Den dringend n\\u00f6tigen Ausbau der Erneuerbaren blockieren Wirtschaftsministerium und der Koalitionspartner CDU\\/CSU. Das Geld f\\u00fcr den schnellsten und sparsamsten europ\\u00e4ischen Gro\\u00dfrechner JUWELS am Forschungszentrum J\\u00fclich, der n\\u00e4chsten Station der Reise, kommt von den Forschungsministerien in Berlin und D\\u00fcsseldorf. \\u00dcber das Schicksal der Insekten von Bauer Veith f\\u00e4llen die Landwirtschaftsministerin und die EU das finale Urteil. Ob sich die Elektrobusse in K\\u00f6ln rentieren, die Schulze am n\\u00e4chsten Tag besucht, bestimmt der Stau in der Stadt und damit die Verkehrsplanung in NRW.<\\/p><p class=\\\"meinz\\\">An diesem Morgen besucht die Ministerin das Marie-Juchacz-Zentrum, ein Altenwohnheim der Arbeiterwohlfahrt im Norden von K\\u00f6ln. \\u201ePepper\\u201c, ein niedlicher kleiner Pflegeroboter mit Kulleraugen, begr\\u00fc\\u00dft sie; im Innenhof des Heimes werden Reden gehalten, die BewohnerInnen rufen \\u201eLauter!\\u201c von den Balkonen. Schulzes Ministerium hilft mit 12.000 Euro bei der Anschaffung von E-Autos f\\u00fcr die \\u201eSpringerdienste\\u201c der Pflegenden.<\\/p><p class=\\\"meinz\\\">Die Pflege soll klimaneutral werden \\u2013 das hei\\u00dft: H\\u00e4user d\\u00e4mmen, regionales Fleisch in der Kantine anbieten, die Pendlerwege des Personals optimieren. Der Verwalter verweist auf die Klimaanlagen, um Medikamente zu k\\u00fchlen. Hitzewellen sind bedrohlich f\\u00fcr alte Menschen, insgesamt bedrohlicher als \\u00dcberflutungen, aber sie sind unsichtbar. F\\u00fcr die volle Klimatisierung aller 82 Wohnungen mit 350 Menschen reicht das Geld nicht. Und es k\\u00e4me, wenn, dann auch nicht aus dem Umweltministerium.<\\/p><p class=\\\"meinz\\\">Das ist das Elend mit Schulzes Job: f\\u00fcr alles verantwortlich, f\\u00fcr wenig zust\\u00e4ndig, kaum bekannt. Schulze sagt dennoch, die Arbeit mache ihr Spa\\u00df. Sie habe \\u201ekein Gef\\u00fchl der Ohnmacht\\u201c. Der niedliche \\u201ePepper\\u201c sagt zur Ministerin mit seiner lustigen Computerstimme: \\u201eIch begr\\u00fc\\u00dfe dich. Ich erkenne dich nicht.\\u201c Eine junge Pflegerin, sichtbar nerv\\u00f6s, nennt die Ministerin in ihrer Rede mehrfach \\u201eFrau Schulz\\u201c. Auf der Liste der bekannten PolitikerInnen taucht Schulze nicht auf. Ihre Vorg\\u00e4ngerin, Pateifreundin Barbara Hendricks, sagte mal trotzig: \\u201eDann werde ich eben bekannt als die unbekannte Ministerin.\\u201c In den Wahlumfragen bleibt ihre SPD weit entfernt von der Macht.<\\/p><p class=\\\"meinz\\\">\\u201eDa ist noch nichts entschieden\\u201c, macht sich Schulze Mut, wenn man sie nach den tr\\u00fcben Aussichten fragt. Und tapfer sagt sie: \\u201eIch bin nicht frustriert, f\\u00fcr mich ist wichtiger, was ich erreicht habe.\\u201c Dazu z\\u00e4hlt sie nicht nur die Erfolge im Amt, sondern auch in der Partei. \\u201eIn der SPD haben wir jetzt ein modernes Angebot mit Klimaschutz und sozialer Absicherung, das ist ein Riesenschritt nach vorn.\\u201c<\\/p><p class=\\\"meinz\\\">Schulzes Amtszeit war auch erfolgreich darin, die SPD gr\\u00fcner zu machen, obwohl sie diese Formulierung gar nicht gern h\\u00f6rt. Bei jeder Gelegenheit betonte sie, \\u00d6kologie sei schon immer ein rotes Thema gewesen. Tats\\u00e4chlich bremste die Bergbau-Gewerkschaft IGBCE die SPD-Genossen oft bei der Energiewende aus, nicht umsonst nannten die Gr\\u00fcnen die Sozis gern \\u201eAutofahrerpartei\\u201c. Noch 2018 warnte die damalige Parteichefin Andrea Nahles vor einer \\u201eBlutgr\\u00e4tsche gegen die Braunkohle\\u201c. Und als Schulze Ende 2018 einen CO<sub>2<\\/sub>-Preis f\\u00fcr Heizen und Tanken ins Spiel brachte, lie\\u00df sie ihr SPD-Finanzminister Olaf Scholz im Regen stehen. Schulze selbst pl\\u00e4dierte beim Kohleausstieg immer f\\u00fcr einen \\u201egerechten \\u00dcbergang\\u201c mit sozialer Abfederung.<\\/p><p class=\\\"meinz\\\">Nun ist das Kohlethema abger\\u00e4umt, die Regionen und Gewerkschaften sind mit 40 Milliarden Steuergeld ruhig gestellt. Und f\\u00fcr Olaf Scholz ist Klimaschutz pl\\u00f6tzlich ein Herzensthema und eine Zukunftschance. Was ist passiert? Scholz habe irgendwann gemerkt, dass Deutschland teure Zahlungen in der EU leisten m\\u00fcsse, wenn das Klimaziel gerissen werde, sagen die einen. \\u201eDer Olaf\\u201c habe das Thema jetzt durchdrungen und erkenne seine Bedeutung, hei\\u00dft es aus Schulzes Umfeld. Immerhin verhinderte die SPD 2019 gegen die CDU\\/CSU, dass es bei den Coronahilfen eine Abwrackpr\\u00e4mie f\\u00fcr Verbrennungsmotoren gibt.<\\/p><p class=\\\"meinz\\\">Dann trotzte die SPD der Union das Klimaschutzgesetz ab, weil 2019 mit Hitzesommern und lauten \\u201eFridays for Future\\u201c-Demonstrationen das Thema pl\\u00f6tzlich akut wurde. Es knallte in der Koalition, aber die kleine, blonde und oft untersch\\u00e4tzte Schulze erreichte, was sie wollte. Das Gesetz wurde ihr Meisterst\\u00fcck, denn es ver\\u00e4ndert die Machtbalance in der Regierung. Jetzt ist Klimaneutralit\\u00e4t per Gesetz das Ziel. Es schreibt pro Jahr f\\u00fcr Industrie, Verkehr, Landwirtschaft oder Bauen eine Obergrenze bei CO<sub>2<\\/sub>-Emissionen fest. Damit ist nun jedes Ressort gesetzlich zu konkreten CO<sub>2<\\/sub>-Einsparungen verpflichtet, diese werden nicht mehr ans Umweltministerium delegiert. Hinzu kommt ein CO<sub>2<\\/sub>-Preis f\\u00fcr Heizen und Tanken, wie ihn Schulze 2018 erfolglos forderte.<\\/p><p class=\\\"meinz\\\">In diesen Monaten zahlte sich aus, dass das Umweltministerium seit Jahren auf diesen Punkt hingearbeitet hatte. Daten, Gesetze, Gutachten lagen bereit oder wurden schnell vorgelegt. Schulze hatte den beamteten Staatssekret\\u00e4r Jochen Flasbarth von ihrer Vorg\\u00e4ngerin \\u00fcbernommen, der das Haus im Griff hatte und hinter den Kulissen die Entscheidungen vorantrieb. Und als im April 2021 das Bundesverfassungsgericht das Klimaschutzgesetz in der urspr\\u00fcnglichen Fassung als verfassungswidrig einstufte, schlug die gro\\u00dfe Stunde des Ministeriums. In wenigen Tagen legten die BeamtInnen einen Gesetzentwurf vor, der die deutschen Klima-Ambitionen noch einmal drastisch versch\\u00e4rfte: klimaneutral bereits im Jahr 2045, h\\u00f6here Ausbauziele f\\u00fcr die Erneuerbaren schon 2030, klare Reduktionsziele f\\u00fcr jedes Jahr bis 2045. Schulze hinterl\\u00e4sst ein riesiges Erbe.<\\/p><p class=\\\"meinz\\\">Die Ministerin sitzt nun in ihrem Tourbus, Oberdeck vorn an der Panoramascheibe, freier Blick in die Rheinebene. Am Horizont gr\\u00fc\\u00dft der K\\u00f6lner Dom, direkt neben der Autobahn schicken die riesigen K\\u00fchlt\\u00fcrme des Braunkohlekraftwerks Weisweiler wei\\u00dfe Wolken in die Luft. Ein Ort mit Fernblick, gut f\\u00fcr R\\u00fcckblick und Ausblick. Wie sieht sie die Zukunft ihres Hauses? Schulze sieht es als Querschnittsressort, das \\u201enicht mehr immer bitte sagen muss, sondern \\u00fcberall entscheidend mitredet\\u201c. Umweltpolitik habe sich \\u201evom Rand in die Mitte bewegt\\u201c. Schluss mit der Machtlosigkeit der Umwelt in der Regierung.<\\/p><p class=\\\"meinz\\\">Ein paar Kilometer weiter unter dem unschuldigen blauwei\\u00dfen Sommerhimmel liegen der Braunkohle-Tagebau Garzweiler und der lange umk\\u00e4mpfte Hambacher Forst. Das hoch umstrittene Thema ist heute erledigt. Umfragen zeigen eine stabile Mehrheit f\\u00fcr Klimaschutz und Energiewende. K\\u00fcrzlich schlug ein repr\\u00e4sentativer \\u201eB\\u00fcrgerrat\\u201c vor, den Klimawandel mit Tempolimit, mehr Erneuerbaren Energien und weniger Fleischkonsum zu bek\\u00e4mpfen. Schulzes Thesen sind jetzt Mainstream. Schulze und ihre Partei sind es nicht mehr.<\\/p><p class=\\\"meinz\\\">Die Entscheidung der \\u201eKohlekommission\\u201c zum sp\\u00e4ten Ausstieg aus der Kohle 2038 haben viele kritisiert. Svenja Schulze steht dazu: \\u201eKlimaschutz funktioniert nur, wenn wir die Menschen mitnehmen.\\u201c Ob die Menschen sie und die SPD bei der Wahl mitnehmen, ist eine ganz andere Frage.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a131\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.63.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a131\\/\\\"><h4 class=\\\"AutorName\\\">Bernhard P\\u00f6tter<\\/h4><h5 class=\\\"AutorJob\\\">Redakteur f\\u00fcr Wirtschaft und Umwelt<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Bernhard P\\u00f6tter &lt;poetter@taz.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Jahrgang 1965. Seine Schwerpunkte sind die Themen Klima, Energie und Umweltpolitik. Wenn die Zeit es erlaubt, besch\\u00e4ftigt er sich noch mit Kirche, Kindern und Konsum. F\\u00fcr die taz arbeitet er seit 1993, zwischendurch und frei u.a. auch f\\u00fcr DIE ZEIT, WOZ, GEO, New Scientist. Autor einiger B\\u00fccher, Zum Beispiel \\u201eTatort Klimawandel\\u201c (oekom Verlag) und \\u201eStromwende\\u201c(Westend-Verlag, mit Peter Unfried und Hannes Koch). <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-23.cfddd69cfbd368b85c477798630ffbb457abf6d8\",\"article\":{\"articleHtml\":{\"name\":\"art03603654.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626975492\",\"sha256\":\"e1e95a3da22820e9c4c33505c5c4ff647529b9f23702d941a4cf2e26fb198dd7\",\"sSize\":\"21393\"},\"audioFile\":null,\"title\":\"Die Macht der Machtlosen\",\"teaser\":\"Svenja Schulze ist die erfolgreichste Umweltministerin, die Deutschland je hatte. Dennoch geht es der Umwelt schlecht. Die j\\u00fcngsten \\u00dcberschwemmungen zeigen, wie wenig ihr Amt ausrichten kann\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5785119\",\"pageNames\":[\"s00810619.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095631.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626975488\",\"sha256\":\"afeedbd2c6b5f519167be24bd551b1fa3ce93f12392da97c4e919075173425d4\",\"sSize\":\"57092\"},{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095632.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626975491\",\"sha256\":\"69a79fc0beea1ded6fbccdb1a00dd7a8025083943441e35359ce058f9c644ad4\",\"sSize\":\"60177\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095631.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626975490\",\"sha256\":\"3213d04eed44b1054ece28da1f5ad4a2ee09dfba1f4b9679af22ee2bf0881d69\",\"sSize\":\"324017\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095632.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626975492\",\"sha256\":\"9e40d2590ae68105915312964bb05576df1b93a1836d9e3fb601dde3c87f469e\",\"sSize\":\"443837\"}],\"authorList\":[{\"name\":\"Bernhard P\\u00f6tter\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.63.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993242\",\"sha256\":\"6f4832f9ab762691daea86d8793e572736527724980b940695a68330d562da26\",\"sSize\":\"88130\"}}]}},{\"sDate\":\"2021-07-20\",\"snippet\":\"... mit intensiveren Niederschl\\u00e4gen und steigendem <span class=\\\"snippet\\\">Hochwasser<\\/span> fertig werden zu m\\u00fcssen. Dabei... Risikobewertung des Senats anders als <span class=\\\"snippet\\\">Hochwasser<\\/span> ein generelles Risiko dar. Sie...\",\"teaser\":\"In Hamburg droht die Hochwassergefahr vor allem von der Elbe, doch auch auf Binnenhochwasser bereitet sich die Stadt vor. Die Risikoparameter drohen rasch zu veralten\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Bevor das Wasser kommt<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_hamburg ArtikelTyp_A2_2017 eptPolitik\\\"><h2 class=\\\"Titel\\\">Bevor das Wasser kommt<\\/h2><h4 class=\\\"Unterzeile\\\">In Hamburg droht die Hochwassergefahr vor allem von der Elbe, doch auch auf Binnenhochwasser bereitet sich die Stadt vor. Die Risikoparameter drohen rasch zu veralten<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptPolitik\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=532.913 BH=318.979&#10;IB=1816 IH=1193&#10;S=4 b=660 h= C=Main&#10;id=1095154 typ=JPEG&#10;\\\" src=\\\"Media.1095154.norm.jpg\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1095154.norm.jpg\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Hamburg baut vor: Hochwasserschutzwand  <span class=\\\"zAu\\\">Foto: Daniel Reinhardt\\/dpa<\\/span><\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_hamburg ArtikelTyp_A2_2017 eptPolitik\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Gernot Kn\\u00f6dler<\\/span><\\/p><p class=\\\"oeinz\\\">Wolkenbr\\u00fcche und Dauerregen \\u2013 Hamburg stellt sich darauf ein, in Zukunft mit intensiveren Niederschl\\u00e4gen und steigendem <span class=\\\"snippet\\\">Hochwasser<\\/span> fertig werden zu m\\u00fcssen. Dabei haben die Beh\\u00f6rden und Institutionen mit ganz unterschiedlichen Baustellen zu tun \\u2013 und daf\\u00fcr zum Teil \\u00fcberraschende L\\u00f6sungen gefunden.<\\/p><p class=\\\"meinz\\\">Die gr\\u00f6\\u00dfte Bedrohung, der die Stadt ausgesetzt ist, sind nicht Sturzfluten, wie sie im Westen Deutschlands halbe D\\u00f6rfer weggerissen haben, sondern Sturmfluten, bei denen der Wind und die See das Wasser in die Elbe dr\\u00fccken. Der Senat hat die Deiche so weit erh\\u00f6ht, dass sie eine Sturmflut, die Stand heute nur alle 200 Jahre vorkommen d\\u00fcrfte, abfangen k\\u00f6nnen. Steigt das Wasser jedoch h\\u00f6her als 7,62 Meter am Pegel St. Pauli, s\\u00e4uft halb S\\u00fcderelbe ab \\u2013 vom Alten Land \\u00fcber Wilhelmsburg bis einschlie\\u00dflich der Vier- und Marschlande.<\\/p><p class=\\\"meinz\\\">Eine weitere Bedrohung geht von Binnenhochw\\u00e4ssern aus, die die vielen Fl\\u00fcsse und Fl\\u00fcsschen im Stadtgebiet anschwellen lassen k\\u00f6nnen. Hierf\\u00fcr hat der Senat auf eine Vorgabe der EU hin <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.taz.de\\/!5258469\\/\')\\\">\\u00dcberschwemmungsgebiete <\\/a>ausgewiesen \\u2013 Areale, die statistisch alle 100 Jahre einmal \\u00fcberflutet werden. Um Sch\\u00e4den zu vermeiden und den Abfluss nicht zu behindern, darf hier <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.taz.de\\/!5023729\\/\')\\\">nicht neu gebaut werden.<\\/a> Zudem darf hier nichts gelagert oder produziert werden, was das Wasser verunreinigen k\\u00f6nnte.<\\/p><p class=\\\"Zwischentitel\\\">\\u00dcberlaufende Siele<\\/p><p class=\\\"oeinz\\\">Starker anhaltender Regen l\\u00e4sst aber nicht nur Fl\\u00fcsse \\u00fcber die Ufer treten, sondern in erster Linie die Siele \\u2013 also Abwasserleitungen. \\u201eStarkregenereignisse\\u201c stellen laut der <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.hamburg.de\\/hochwasserrisikomanagementplan\\/\')\\\">Risikobewertung des Senats<\\/a> anders als <span class=\\\"snippet\\\">Hochwasser<\\/span> ein generelles Risiko dar. Sie k\\u00f6nnten \\u201egrunds\\u00e4tzlich \\u00fcberall auftreten, die Wahrscheinlichkeit des Eintretens f\\u00fcr einen spezifischen Ort kann jedoch nicht hinreichend statistisch abgesichert angegeben werden\\u201c.<\\/p><p class=\\\"meinz\\\">Um damit klar zu kommen, hat der st\\u00e4dtische Ver- und Entsorger Hamburg Wasser (HW) zusammen mit Wissenschaftlern einen <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/sri.hamburgwasser.de\\/\')\\\">Starkregenindex<\\/a> entwickelt. Er zeigt in einem 500-Meter-Raster in Echtzeit an, wie viel Niederschlag an einem Tag gefallen ist. Dabei ergibt ein Blick in die archivierten Daten, dass schon das regionale Wetter stark ausdifferenziert ist.<\\/p><p class=\\\"meinz\\\">Der Index ist in zw\\u00f6lf Stufen eingeteilt, die wiederum bestimmten Eintrittswahrscheinlichkeiten entsprechen. Stufe eins und zwei kommen rechnerisch mindestens alle f\\u00fcnf Jahre vor. Auf sie ist die Kanalisation ausgelegt. Ole Braukmann, Sprecher von <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.hamburgwasser.de\')\\\">Hamburg Wasser<\\/a>, dr\\u00fcckt es anders aus: \\u201eWenn eine bestimmte Heftigkeit \\u00fcberschritten wird, sind wir aus der Haftung entlassen.\\u201c Grundst\\u00fcckseigent\\u00fcmer k\\u00f6nnen auf einer <a href=\\\"javascript:EPUBTAZ.externLink(\'https:\\/\\/www.hamburg.de\\/pressearchiv-fhh\\/15140414\\/2021-06-03-bukea-starkregenkarte\\/\')\\\">Starkregengefahrenkarte <\\/a>nachsehen, ob sie handeln m\\u00fcssen.<\\/p><p class=\\\"meinz\\\">Weil der Klimawandel starke Regeng\\u00fcsse wahrscheinlicher werden lasse, m\\u00fcsse dieser Ma\\u00dfstab nach oben angepasst werden, sagt Wolfram Hammer vom Umweltverband BUND. Die EU sieht das genauso, weshalb die Umweltbeh\\u00f6rde das Risiko als Kombination von Eintrittswahrscheinlichkeit und m\\u00f6glichem Schaden alle sechs Jahre neu bewertet. Die Daten des Starkregenindex zeigen, dass die Ereignisse der Stufen 0,5 und eins in den Jahren 2000 bis 2010 leicht zugenommen haben. Die Daten f\\u00fcr das vergangene Jahrzehnt werden gerade ausgewertet.<\\/p><p class=\\\"meinz\\\">Zum Klimawandel komme als versch\\u00e4rfender Faktor das Wachstum der Stadt mit der damit einher gehenden Bodenversiegelung, sagt HW-Sprecher Braukmann. Um die Siele weniger zu belasten, wird deshalb versucht, das Wasser vorher abzuleiten.<\\/p><p class=\\\"meinz\\\">Im Heinz-Klink-Stadion in Billstedt hat HW zusammen mit verschiedenen Beh\\u00f6rden die Sanierung des Stadions genutzt, um Speicherk\\u00e4sten unter die Tartanbahn zu setzen, die \\u00fcbersch\\u00fcssiges Wasser auffangen und versickern lassen. <\\/p><p class=\\\"meinz\\\">Und im Bauprojekt Jenfelder Au wird das Regenwasser nicht in die Siele geleitet, sondern in Gr\\u00e4ben und in einen zentralen See. \\u201eWir haben ja nicht nur die Starkregenereignisse, sondern auch Trockenphasen\\u201c, sagt Braukmann. <\\/p><p class=\\\"meinz\\\">Die Projekte geh\\u00f6ren zu einem bis 2030 laufenden Programm des Senats, das volllaufende Keller und Garagen verhindern soll.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a199\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.3.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a199\\/\\\"><h4 class=\\\"AutorName\\\">Gernot Kn\\u00f6dler<\\/h4><h5 class=\\\"AutorJob\\\">Hamburg-Redakteur<\\/h5><\\/a><\\/div><\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-20.d9d5aa81d69014e334b23224374ee3b5d893327b\",\"article\":{\"articleHtml\":{\"name\":\"art03602951.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626715124\",\"sha256\":\"58ce5f4a221573bcc498d75888b5be5d2ab34a5120d4110a57bf7da694c09e3b\",\"sSize\":\"7945\"},\"audioFile\":null,\"title\":\"Bevor das Wasser kommt\",\"teaser\":\"In Hamburg droht die Hochwassergefahr vor allem von der Elbe, doch auch auf Binnenhochwasser bereitet sich die Stadt vor. Die Risikoparameter drohen rasch zu veralten\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5781677\",\"pageNames\":[\"s00810450.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095154.norm.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626715123\",\"sha256\":\"120bda708bc07198366c9b07bad7e1992d3582cfa9d93177c21b26838f9fc564\",\"sSize\":\"18478\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1095154.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626715124\",\"sha256\":\"ada84b9ba93b2efab57072776e1dab5491c63a9f76b4fa18949eb0787519a7fb\",\"sSize\":\"66114\"}],\"authorList\":[{\"name\":\"Gernot Kn\\u00f6dler\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.3.png\",\"storageType\":\"global\",\"sMoTime\":\"1564993707\",\"sha256\":\"c2c3201353dd8efb792155f01990b534413376314877f1437b7873b4900eafe0\",\"sSize\":\"62218\"}}]}},{\"sDate\":\"2021-07-17\",\"snippet\":\"... Niederlande und Belgiens steigt das <span class=\\\"snippet\\\">Hochwasser<\\/span>. \\u201eWir haben noch nicht den...\",\"teaser\":\"Die Unwetterkatastrophe sorgt in NRW und Rheinland-Pfalz weiter f\\u00fcr Verw\\u00fcstungen. Inzwischen gibt es mehr als 100 Tote. Jede Stunde k\\u00e4men neue Hiobsbotschaften, sagt die rheinland-pf\\u00e4lzische Ministerpr\\u00e4sidentin Malu Dreyer\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>Keine Entspannung in Sicht<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AL1_2017 eptNorm\\\"><h2 class=\\\"Titel\\\">Keine Entspannung in Sicht<\\/h2><h4 class=\\\"Unterzeile\\\">Die Unwetterkatastrophe sorgt in NRW und Rheinland-Pfalz weiter f\\u00fcr Verw\\u00fcstungen. Inzwischen gibt es mehr als 100 Tote. Jede Stunde k\\u00e4men neue Hiobsbotschaften, sagt die rheinland-pf\\u00e4lzische Ministerpr\\u00e4sidentin Malu Dreyer<\\/h4><\\/div><div id=\\\"fotos\\\" class=\\\"eptNorm\\\"><div id=\\\"foto\\\"><img alt=\\\"BB=737.508 BH=470.711&#10;IB=2738 IH=1876&#10;S=5.5 b=660 h= C=Main&#10;id=1094597 typ=JPEG&#10;\\\" src=\\\"Media.1094597.norm.png\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1094597.norm.png\')\\\" class=\\\"MediaImg ImgMain ImgSizeFull\\\"\\/><p class=\\\"MediaButze BuMain ImgSizeFull\\\" id=\\\"MediaButze BuMain\\\">Das <span class=\\\"snippet\\\">Hochwasser<\\/span> hat Erftstadt-Blessem mit voller Wucht getroffen <span class=\\\"zAu\\\">Foto: Rhein-Erft-Kreis\\/dpa<\\/span><\\/p><\\/div><div class=\\\"EinzelFoto\\\"><img alt=\\\"BB=124.724 BH=198.425&#10;IB=500 IH=792&#10;S=1 b=330 h= C=Multi&#10;id=1094598 typ=PDF&#10;\\\" src=\\\"Media.1094598.norm.png\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1094598.norm.png\')\\\" class=\\\"MediaImg ImgMulti ImgSizeHalf\\\"\\/><p class=\\\"MediaButze BuMulti ImgSizeHalf\\\">Karte zeigt St\\u00e4dte und Gemeinden, in denen der Katastrophenfall ausgerufen wurde<\\/p><\\/div><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_AL1_2017 eptNorm\\\"><p class=\\\"Korrespondent\\\">Von <span class=\\\"zAu\\\">Pascal Beucker<\\/span><\\/p><p class=\\\"Initial\\\">Erdrutsche von ungeheurem Ausma\\u00df, ein riesiger Krater, weggesp\\u00fclte Wohnh\\u00e4user: Die Bilder aus Erftstadt zeugen von schier unfassbaren Verw\\u00fcstungen. Die Flut kam im Stadtteil Blessem schnell und unaufhaltsam. Auch Teile der historischen Burg st\\u00fcrzten ein. \\u201eWegen der Dynamik\\u201c sei die Lage in Erftstadt zurzeit \\u201eganz besonders kritisch\\u201c, sagte der nordrhein-westf\\u00e4lische Innenminister Herbert Reul am Freitagmittag in D\\u00fcsseldorf. \\u201eWir gehen von mehreren Toten aus, wissen es aber nicht.\\u201c<\\/p><p class=\\\"meinz\\\">Die Unwetterkatastrophe hat NRW und Rheinland-Pfalz weiter fest im Griff. Wegen Starkregens sind Fl\\u00fcsse massiv \\u00fcber die Ufer gestiegen. In beiden Bundesl\\u00e4ndern gab es zu Redaktionsschluss mehr als 100 Tote. Auch in Teilen Baden-W\\u00fcrttembergs, der Niederlande und Belgiens steigt das <span class=\\\"snippet\\\">Hochwasser<\\/span>.<\\/p><p class=\\\"meinz\\\">\\u201eWir haben noch nicht den Stand, dass wir sagen k\\u00f6nnen, die Lage entspannt sich\\u201c, sagte die rheinland-pf\\u00e4lzische Ministerpr\\u00e4sidentin Malu Dreyer am Freitag in Mainz. \\u201eDas Leid nimmt so dramatisch zu, weil wir jede Stunde neue Hiobsbotschaften bekommen.\\u201c Noch immer seien nicht alle betroffenen Ortschaften erreichbar. \\u201eDie Sch\\u00e4den sind so dramatisch, dass wir noch lange mit dem Wiederaufbau befasst sein werden\\u201c, sagte sie. \\u201eMan kann nur traurig und entsetzt \\u00fcber alles sein.\\u201c<\\/p><p class=\\\"meinz\\\">Dreyer best\\u00e4tigte, dass bei dem Unwetter in der Nacht zum Donnerstag zw\\u00f6lf Bewohner:innen einer Wohneinrichtung f\\u00fcr Menschen mit geistiger Behinderung in Sinzig ums Leben gekommen sind. \\u201eDas ist ganz, ganz schrecklich, wenn man nur eine Sekunde lang daran denkt, dass in einem Wohnheim so viele Menschen umgekommen sind\\u201c, sagte sie.<\\/p><img alt=\\\"BB=124.724 BH=198.425&#10;IB=500 IH=792&#10;S=1 b=330 h= C=Multi&#10;id=1094598 typ=PDF&#10;\\\" src=\\\"Media.1094598.norm.png\\\" onclick=\\\"javascript:EPUBTAZ.openImage(\'Media.1094598.norm.png\')\\\" class=\\\"MediaImg ImgMulti ImgSizeHalf\\\"\\/><p class=\\\"MediaButze BuMulti ImgSizeHalf\\\">Karte zeigt St\\u00e4dte und Gemeinden, in denen der Katastrophenfall ausgerufen wurde<\\/p><p class=\\\"meinz\\\">Die Einrichtung der Lebenshilfe hat 36 Wohnpl\\u00e4tze und befindet sich in einem tiefer gelegenen Viertel Sinzigs, einer kleinen Stadt, die an der M\\u00fcndung der Ahr in den Rhein liegt. Nach Angaben des Gesch\\u00e4ftsf\\u00fchrers der Lebenshilfe Rheinland-Pfalz, Matthias Mandos, waren die Fluten schneller gekommen, als die Menschen h\\u00e4tten in Sicherheit gebracht werden k\\u00f6nnen. \\u201eDas Wasser drang innerhalb einer Minute bis an die Decke des Erdgeschosses\\u201c, sagte Mandos.<\\/p><p class=\\\"meinz\\\">Nach Angaben des Mainzer Sozialministeriums sind von der Unwetterkatastrophe in Rheinland-Pfalz mehr als ein Dutzend station\\u00e4re Einrichtungen der Pflege und Eingliederungshilfe sowie betreute Wohnangebote betroffen. Mehrere Einrichtungen seien von den Wassermassen zerst\\u00f6rt oder unbewohnbar gemacht worden und h\\u00e4tten evakuiert werden m\\u00fcssen.<\\/p><p class=\\\"meinz\\\">Kurz nach Dreyer trat der nordrhein-westf\\u00e4lische Ministerpr\\u00e4sident Armin Laschet in D\\u00fcsseldorf vor die Presse. \\u201eUnser Land erlebt eine Flutkatastrophe von historischem Ausma\\u00df\\u201c, sagte er. Insgesamt sind in NRW 25 St\\u00e4dte und Landkreise von den \\u00dcberschwemmungen betroffen \\u2013 weite Teile des Rheinlands, Teile des Ruhrgebiets und kleinere Teile Westfalens. Wie in Rheinland-Pfalz mussten mehrere Ortschaften per Hubschrauber evakuiert werden. \\u201eWir werden H\\u00e4user reparieren, Br\\u00fccken wieder aufbauen und Verkehrswege wieder instandsetzen, aber die Menschenleben, die in den Fluten verloren gegangen sind, sind unersetzbar\\u201c, sagte Laschet.<\\/p><p class=\\\"meinz\\\">Die Wassermassen haben auch die Verkehrsverbindungen erheblich getroffen. So mussten Streckenabschnitte mehrerer Autobahnen gesperrt werden. Stark beeintr\\u00e4chtigt ist weiterhin der Bahnverkehr. So waren am Freitag die Strecke von K\\u00f6ln \\u00fcber Wuppertal und Hagen nach Dortmund als auch von K\\u00f6ln \\u00fcber Bonn nach Koblenz unbefahrbar. Die Zugverbindung von K\\u00f6ln nach Br\\u00fcssel ist ebenso unterbrochen. \\u201eAufgrund der Vielzahl an St\\u00f6rungen bitten wir Sie, den Bereich NRW weitr\\u00e4umig zu umfahren\\u201c, teilte die Deutsche Bahn mit.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a54\\/\\\"><img class=\\\"AutorImg\\\" src=\\\"global\\/AutorImg.122.png\\\"\\/><\\/a><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a54\\/\\\"><h4 class=\\\"AutorName\\\">Pascal Beucker<\\/h4><h5 class=\\\"AutorJob\\\">Inlandsredakteur<\\/h5><\\/a><ul class=\\\"AutorKontakt\\\"><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorMail\\\" href=\\\"mailto:Pascal Beucker &lt;pascal@beucker.de&gt;\\\"><img class=\\\"icon\\\" src=\\\"resources\\/email_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorTwitter\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/twitter.com\\/PascalBeucker\\\"><img class=\\\"icon\\\" src=\\\"resources\\/microblog_64x64.png\\\"\\/><\\/a><\\/li><li class=\\\"AutorKontakt\\\"><a class=\\\"AutorWebsite\\\" target=\\\"_blank\\\" href=\\\"http:\\/\\/www.beucker.de\\\"><img class=\\\"icon\\\" src=\\\"resources\\/website_64x64.png\\\"\\/><\\/a><\\/li><\\/ul><\\/div><\\/div><div class=\\\"AutorDescr\\\">Jahrgang 1966. Arbeitet seit 2014 als Redakteur im Inlandsressort der taz. Zuvor f\\u00fcnfzehn Jahre taz-Korrespondent in Nordrhein-Westfalen. Mehrere Buchver\\u00f6ffentlichungen (u.a. \\u201eEndstation R\\u00fccktritt!? Warum deutsche Politiker einpacken\\u201c, Bouvier Verlag, 2011). Seit 2018 Mitglied im Vorstand der taz-Genossenschaft.<\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-17.598d674addd99d686a8adbc4c5b59200cb339dcb\",\"article\":{\"articleHtml\":{\"name\":\"art03602361.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626456716\",\"sha256\":\"67484de71485ee51749f1f62b72ed0655e2a70874acb9796805d38de67eae7da\",\"sSize\":\"8653\"},\"audioFile\":null,\"title\":\"Keine Entspannung in Sicht\",\"teaser\":\"Die Unwetterkatastrophe sorgt in NRW und Rheinland-Pfalz weiter f\\u00fcr Verw\\u00fcstungen. Inzwischen gibt es mehr als 100 Tote. Jede Stunde k\\u00e4men neue Hiobsbotschaften, sagt die rheinland-pf\\u00e4lzische Ministerpr\\u00e4sidentin Malu Dreyer\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5782610\",\"pageNames\":[\"s00810214.pdf\"],\"imageList\":[{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094598.norm.png\",\"storageType\":\"issue\",\"sMoTime\":\"1626456713\",\"sha256\":\"ef16e0ed31189995715bb6dfcb56908d3462e72db3688ece3bbc435f7e6aa51f\",\"sSize\":\"111871\"},{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094597.norm.png\",\"storageType\":\"issue\",\"sMoTime\":\"1626456715\",\"sha256\":\"01385e4b2aad7d32939b4b38b983d9d600ac3f79c5bf8dee39e2597ac28ae7ae\",\"sSize\":\"660028\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094598.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626456714\",\"sha256\":\"4f4d7e3e30fd0b8185831ba55de625b154d43118410981a4e75ae8a068c6ca90\",\"sSize\":\"39514\"},{\"resolution\":\"high\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"Media.1094597.high.jpg\",\"storageType\":\"issue\",\"sMoTime\":\"1626456716\",\"sha256\":\"f58f557dd5e1476e7fa4d50a39f9ac24725464c4ec07824d40ad66f8776bb5d6\",\"sSize\":\"488435\"}],\"authorList\":[{\"name\":\"Pascal Beucker\",\"image\":{\"resolution\":\"normal\",\"type\":\"picture\",\"alpha\":1,\"oSharable\":false,\"name\":\"AutorImg.122.png\",\"storageType\":\"global\",\"sMoTime\":\"1579454168\",\"sha256\":\"78cc7a9d2ecef42abe39a48382b747122af40591a9a04672ae86d48180ffb1f5\",\"sSize\":\"75182\"}}]}},{\"sDate\":\"2021-07-23\",\"snippet\":\"... einer der St\\u00e4dte, die das <span class=\\\"snippet\\\">Hochwasser<\\/span> schwer getroffen hat. \",\"teaser\":\"die drei fragezeichen\",\"articleHtml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\"?>\\n<!DOCTYPE html>\\n<html xmlns=\\\"http:\\/\\/www.w3.org\\/1999\\/xhtml\\\" xml:lang=\\\"de\\\" lang=\\\"de\\\">\\n<head>\\n<meta name=\\\"generator\\\" content=\\\"taz E-Book Generator Version 3.000\\\"\\/>\\n<meta http-equiv=\\\"Content-Type\\\" content=\\\"text\\/html; charset=utf-8\\\"\\/>\\n<meta http-equiv=\\\"cache-control\\\" content=\\\"no-cache\\\"\\/>\\n<meta http-equiv=\\\"expires\\\" content=\\\"0\\\"\\/>\\n<meta http-equiv=\\\"pragma\\\" content=\\\"no-cache\\\"\\/>\\n<title>\\u201eMan sollte Kinder nicht zu Verwandten wegschicken\\u201c<\\/title>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/base2017.css\\\"> <\\/link>\\n<meta name=\\\"viewport\\\" content=\\\"width=device-width, user-scalable=no, initial-scale=1.0\\\"\\/>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/platform.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/jquery-3.min.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/tazApi.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/setupApp.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApi.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/scroll.css\\\"> <\\/link>\\n  <link rel=\\\"stylesheet\\\" type=\\\"text\\/css\\\" href=\\\"resources\\/tazApiArticle.css\\\"> <\\/link>\\n  <script src=\\\"resources\\/eventLib.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/scrollPager.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n  <script src=\\\"resources\\/pageSetup.js\\\" type=\\\"text\\/javascript\\\" charset=\\\"utf-8\\\" language=\\\"javascript\\\"> <\\/script>\\n<\\/head>\\n<body>\\n<div id=\\\"content\\\"><div id=\\\"titel\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_Detail_2017 eptNorm\\\"><h4 class=\\\"Dach\\\">die drei fragezeichen<\\/h4><h2 class=\\\"Titel\\\">\\u201eMan sollte Kinder nicht zu Verwandten wegschicken\\u201c<\\/h2><\\/div><div id=\\\"brot\\\" class=\\\"titel BeitragsArt_taz_print_west ArtikelTyp_Detail_2017 eptNorm\\\"><p class=\\\"Hinweis\\\">Nach der Flut haben viele mit dem Verlust von Angeh\\u00f6rigen und von ihrem Zuhause zu k\\u00e4mpfen. Psychotherapeut Dittkrist erkl\\u00e4rt, wie man mit Kindern in dieser Situation umgeht<\\/p><p class=\\\"oeinz\\\"><strong>taz: Herr Dittkrist, bei dem Unwetter im Westen Deutschlands wurden St\\u00e4dte zerst\\u00f6rt, mindestens 170 Menschen starben, viele verloren ihr Zuhause, darunter auch Kinder. Was brauchen sie nach so einem traumatischen Ereignis?<\\/strong><\\/p><p class=\\\"meinz\\\"><strong>Henning Dittkrist:<\\/strong> Das, was sie eigentlich immer brauchen, nur eben noch mehr: ihre Familie. Eltern sollten so viel Zeit wie m\\u00f6glich mit ihren Kindern verbringen und ihnen Zuneigung schenken. Ebenfalls wichtig ist, Kindern zu verstehen zu geben, dass ihre Gef\\u00fchle berechtigt sind und es in Ordnung ist zu weinen. Eltern sollten ihre Kinder dar\\u00fcber hinaus von Medienberichten fernhalten, da Bilder und Videos retraumatisierend wirken k\\u00f6nnen. Allem voran brauchen Kinder jetzt die Gewissheit, dass sie in Sicherheit sind.<\\/p><p class=\\\"meinz\\\"><strong>Sollte ich also vermeiden, vor meinem Kind Schw\\u00e4che zu zeigen?<\\/strong><\\/p><p class=\\\"meinz\\\">Nein. Es ist sehr wichtig, authentisch und ehrlich zu sein. Kinder merken sofort, wenn die Eltern ihnen etwas vorspielen oder verheimlichen. Auf keinen Fall sollte man seine Kinder eine Zeit lang zu Verwandten schicken, um das Chaos zu Hause alleine zu beseitigen. Die Eltern sind in so einer Krisensituation das Allerwichtigste. Am meisten hilft Kindern, wenn sie bei der Bew\\u00e4ltigung der Probleme miteinbezogen werden, sie also bei Aufr\\u00e4umarbeiten dabei sein und zuh\\u00f6ren d\\u00fcrfen, wenn ihre Eltern mit anderen Erwachsenen \\u00fcber die Flut sprechen. Nur so k\\u00f6nnen sie die Lebensver\\u00e4nderung verarbeiten.<\\/p><p class=\\\"meinz\\\"><strong>Und was, wenn mein Kind gar nicht \\u00fcber das Geschehene sprechen will?<\\/strong><\\/p><p class=\\\"meinz\\\">Kindern f\\u00e4llt es oft schwer, ihre Gef\\u00fchle mit Worten auszudr\\u00fccken. H\\u00e4ufig \\u00e4u\\u00dfern sich ihre Emotionen stattdessen in ihrem Verhalten, etwa beim Spielen oder Malen. Eltern sollten ihre Kinder genau beobachten und sich fragen: Wie hat sich mein Kind vor der Katastrophe verhalten, und wie verh\\u00e4lt es sich jetzt? Es gibt Kinder, die werden nach so einem Ereignis total aggressiv, andere werden ruhig und zur\\u00fcckhaltend. Wieder andere lachen weniger, k\\u00f6nnen sich schlechter konzentrieren oder haben starke Stimmungsschwankungen. <\\/p><p class=\\\"meinz\\\">Nach traumatischen Ereignissen ist es erst mal normal, wenn Kinder zerstreut sind, Albtr\\u00e4ume oder Gef\\u00fchlsausbr\\u00fcche haben, schlie\\u00dflich muss das Gehirn das Geschehene irgendwie verarbeiten. Verhalten sich Kinder aber \\u00fcber Monate hinweg anders, sollten Eltern eine*n Kinder- und Psychotherapeut*in aufsuchen. Denn dann besteht Verdacht auf Traumatisierung.<\\/p><p class=\\\"meinz\\\">       <i>Interview: Rieke Wiemann<\\/i><\\/p><p class=\\\"oeinz\\\">Henning Dittkrist  \\n48, arbeitet als Kinder-  \\nund Jugendpsychotherapeut in Leverkusen, also in einer  \\nder St\\u00e4dte, die das <span class=\\\"snippet\\\">Hochwasser<\\/span> schwer getroffen hat.<\\/p><div id=\\\"Info\\\"><div class=\\\"Autoren\\\"><div class=\\\"Autor\\\"><div class=\\\"AutorProfil\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a64808\\/\\\"\\/><div class=\\\"AutorKontakt\\\"><a class=\\\"AutorA\\\" target=\\\"_blank\\\" href=\\\"https:\\/\\/www.taz.de\\/Autor\\/!a64808\\/\\\"><h4 class=\\\"AutorName\\\">Rieke Wiemann<\\/h4><h5 class=\\\"AutorJob\\\">Volont\\u00e4rin<\\/h5><\\/a><\\/div><\\/div><div class=\\\"AutorDescr\\\"> Jahrgang 1994, ist Volont\\u00e4rin bei der taz. Studiert nebenbei den Master of Science Journalismus an der Uni Leipzig <\\/div><\\/div><\\/div><\\/div><\\/div><div class=\\\"lastElement\\\"> <\\/div><\\/div><\\/body>\\n<\\/html>\\n\",\"baseUrl\":\"https:\\/\\/dl.taz.de\\/data\\/tApp\\/taz\\/content\\/2021\\/2021-07-23.cfddd69cfbd368b85c477798630ffbb457abf6d8\",\"article\":{\"articleHtml\":{\"name\":\"art03603656.html\",\"storageType\":\"issue\",\"sMoTime\":\"1626975506\",\"sha256\":\"5001e142a8bea257b79b475d7f4f47ddaab17940ff155e4d4994c3671e09b385\",\"sSize\":\"5649\"},\"audioFile\":null,\"title\":\"\\u201eMan sollte Kinder nicht zu Verwandten wegschicken\\u201c\",\"teaser\":\"die drei fragezeichen\",\"onlineLink\":\"https:\\/\\/www.taz.de\\/!5785113\",\"pageNames\":[\"s00810620.pdf\"],\"imageList\":null,\"authorList\":null}}]}}}"
    
    func decodeme<T>(data: Data, type: T.Type) -> Result<T,Error> where T: Decodable  {
          var result: Result<T,Error>
          let dec = JSONDecoder()
          /**
           (lldb) po String(decoding: data, as: UTF8.self)
           "{\"data\":{\"search\":{\"total\":805,\"text\":\"Hochwasser\"}}}"
        
           */
          do {
            let dict = try dec.decode([String:T].self, from: data)
            result = .success(dict["data"]!)
          }
          catch {
            print(error)
            result = .failure(Log.fatal("JSON decoding error"))
//            result = .failure(GqlFeeder().fatal("JSON decoding error"))
          }
          return result
        }
        
        if let d = result.data(using: .utf8) {
          let res = decodeme(data:d, type: GqlSearchResponseWrapper.self)
          
          switch res {
            case .success(let frq):
//              print("frq: \(frq)")
              return frq.search
            case .failure(let err):
              print("err: \(err)")
          }
          print("Done")
        } else {
          print("Fail")
        }

      return nil
  }
}
